css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
#appmenu-toolbar-button {
  margin: 3px !important;
  border:1px solid rgba(0,0,0,0.8) !important;
  margin-top: 0px !important;
  -moz-appearance: none !important;
  background-color: #db6f19 !important;
  box-shadow: inset rgba(255,255,255,0.6) 0px 0px 2px,
              inset rgba(255,255,255,0.1) 0px 12px 0px,
              inset rgba(255,160,48,0.8) 0px 12px 10px,
              inset rgba(0,0,0,0.2) 0px 20px 6px 8px;
  -moz-transition-property: background-color, box-shadow;
  -moz-transition-duration: 0.5s;
  border-radius: 3px;
}
#appmenu-toolbar-button:hover {
  background-color: #eb7f29 !important;
  box-shadow: inset rgba(255,255,255,0.6) 0px 0px 2px,
              inset rgba(255,255,255,0.1) 0px 12px 0px,
              inset rgba(255,160,48,0.8) 0px 12px 10px,
              inset rgba(0,0,0,0.4) 0px 9px 6px 8px;
}
#appmenu-toolbar-button[open] {
  box-shadow: inset rgba(0,0,0,0.6) 0px 0px 2px,
              inset rgba(255,255,255,0.1) 0px 12px 0px,
              inset rgba(255,160,48,0.8) 0px 12px 10px,
              inset rgba(0,0,0,0.2) 0px 20px 6px 8px;
  border-bottom-left-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
  border-bottom: none !important;
}
#main-window[privatebrowsingmode] #appmenu-toolbar-button {
  background-color: #731AAB !important;
  background-image: url("data:image/png;base64,"), -moz-linear-gradient(-90deg, rgba(162,52,200,0.6), rgba(112,16,160, 0)) !important;
  background-repeat: no-repeat;
  background-position: 6px 0px, left top;
  box-shadow: inset rgba(255,255,255,0.6) 0px 0px 2px,
              inset rgba(255,255,255,0.1) 0px 12px 0px,
              inset rgba(255,255,255,0.2) 0px 12px 10px;
}
#main-window[privatebrowsingmode] #appmenu-toolbar-button:hover {
  background-color: #933acb !important;
}
#main-window[privatebrowsingmode] #appmenu-toolbar-button[open] {
  box-shadow: inset rgba(0,0,0,0.6) 0px 0px 2px,
              inset rgba(255,255,255,0.1) 0px 12px 0px,
              inset rgba(255,255,255,0.2) 0px 12px 10px;
}

#appmenu-toolbar-button > .toolbarbutton-text {
  color: #ffffff;
  font-weight: bold;
  text-shadow: #000000 0px 1px 1px !important;
  padding-left: 12px !important;
  padding-right: 15px !important;
}
#main-window[privatebrowsingmode] #appmenu-toolbar-button > .toolbarbutton-text {
  padding-left: 35px !important;
  padding-right: 10px !important;
}

#appmenu-toolbar-button > .toolbarbutton-menu-dropmarker {
  display: none !important;
}
