css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stumbleupon.com") {
* {
outline:none !important;}

#tb-stumble A {
border:0px !Important;
box-shadow:none !important;
border-radius:2px !important;
margin-top:6px !important;
padding-left:20px !important;
background:none !important;
font-size:0px !important;}

#tb-stumble .tb-btn-ico {
left:30px !important;}

#tb-toolbar, #tb-toolbar-left {
background: -moz-linear-gradient(top, rgba(46,46,46,1) 0%, rgba(29,29,29,1) 100%) !important;
box-shadow: 0 -1px 0 rgba(0, 0, 0, 0.20) inset !important;}

#tb-like > A {
width:15px !important;}

#tb-notlike > A {
width:23px !important;}

#tb-stumble-disable {
background-color:#000 !important;}

#tb-stumble {
text-shadow: 1px 1px 0px #565656 !important;}

#tb-likemore + .tb-space.tb-hide-visitor, #tb-share + .tb-space.tb-hide-visitor {
width:6px !important;}

#tb-interests {
float:right !important;
margin-right:30px !important;
font-size:12px !important;}

#tb-home a {
width:47px !important;}

#tb-sponsored, #tb-notlikemore, #tb-likemore, #tb-notlikemore + .tb-space.tb-hide-visitor, #tb-userlabel, #tb-share, 
#tb-interests + .tb-space.tb-hide-visitor, #tb-explore-box, .tb-space-solid, 
#tb-comment, #tb-comment + .tb-space.tb-hide-visitor, #tb-notification, #tb-share-facebook,
#tb-lists, #content > #tb-toolbar.tb-user.tb-has-notifications {
display:none !important;}

#tb-like .tb-btn-text {
display:none !important;}

#tb-toolbar {
z-index:1 !important;
overflow:hidden !important;
background-color:transparent !important;
height:37px !important;
margin-top:-2px !important;}

}
