css:
/* 
@namespace url(http://www.w3.org/1999/xhtml);
  2019.07.21 * hotfix
  2017-12-12 * перемены в яндексе
  2017-11-10 * спрятана реклама яндекс браузера
  2017-10-05 *** стрелочка в иконке для правой колонки
*/

@-moz-document url-prefix("http://yandex.ru/yandsearch")
,regexp("https?://(www\\.)?yandex\\..+/(yand)?search.+")
{
div.content__left {
 /* 2 колонки результатов */
-moz-column-count:  /*[[column-count]]*/ 2 /* (два) */ !important;
-webkit-column-count:  /*[[column-count]]*/ 2 /* (два) */!important;
 column-count: /*[[column-count]]*/ 2 /* */!important;
/* */
}

div.content__left  {
 -moz-column-gap:6px!important; 
 -moz-column-rule:  thin dotted gray !important;
 -webkit-column-gap:6px!important;
 -webkit-column-rule:  thin dotted gray !important;
/* */
 column-gap:6px!important;
 column-rule:  thin dotted gray !important;
/* */
 width: auto !important;
 min-width: 1px !important;
 border-top: thin dotted gray !important;
}
/* left column *************** */
.main__left:before{
position: absolute !important;
visibility: visible !important;
/* */
top: 0px  !important;
left: 0 !important; /* */
right: auto !important; bottom: auto !important; /* */
z-index: 247 !important;
background-color: rgba(255, 240, 220, .7) !important;
opacity: .9 !important;
content: "•••" !important;
width:  80px !important; 
height: 1.2em !important;
text-align: center !important;
border: thin solid grey !important;
border-radius: 16px 0px 16px 0px !important;
}
.main__left{
position: absolute !important;
/* */
top: 390px !important;
left: 0 !important; /* */
right: auto !important; bottom: auto !important; /* */
z-index: 2147483647 !important;
background-color: rgba(255, 240, 220, .9) !important;
opacity:  1 !important;
visibility: hidden !important;
border: 1px solid grey  !important;
border-radius: 16px 0px 16px 0px !important;
margin: 0 !important;
padding: 0 0 1.2em 0 !important;
transition: visibility 0s linear 0.75s !important;
}
.main__left:hover {
visibility: visible !important;
transition: visibility 0s linear 0.3s !important;
}

.main__left:hover:before { 
visibility: hidden !important;
transition: visibility 0s linear 0.25s !important;
}
/* 23-03-15 ??!1 */
.main__left:hover .navigation__more {visibility: visible !important;}
.main__left .navigation__more {visibility: hidden !important;}
.serp-item__wrap{ 
padding: 8px 0 0 12px !important;
}
/* */
.main__center-inner {margin-left: 10px !important;}
.header__wrapper{ position: absolute !important;}

.content__right:before {
position: absolute !important;
visibility: visible !important;
top: 40px !important;
right: 0px !important;
left: auto !important; bottom: auto !important;
width: 64px !important;
height: 1em !important;
border: 1px solid #444 !important;
border-radius: 16px 0 16px 0 !important;
background: rgba(236, 236, 254,.9) !important;
opacity: .9 !important;
z-index: 47483647 !important;
content: "" !important; 
}
.content__right{ /* */
position: absolute !important;
visibility: hidden !important;
opacity: 1 !important;
top:   350px !important;
right: 0 !important; 
left: auto !important; bottom: auto !important;
min-height: 1.2em !important;
border: 1px solid #444 !important;
border-radius: 16px 0 16px 0 !important;
background: rgba(236, 236, 254, .9) !important;
z-index: 249500 !important;
margin: 0 0 0 0 !important; 
padding:12px !important;
/* 151119 */
width: auto !important; 
min-width: 40px !important;
/* */
transition: visibility 0s linear 0.75s !important;
}
.content__right:hover{ 
visibility: visible !important;
margin-right: 0px !important;
transition: visibility 0s linear 0.3s !important;
}
.content__right:hover:before{
visibility: hidden !important;
transition: visibility 0s linear 0.4s !important;
-webkit-transition: visibility 0s linear 0.2s !important;
}
/* 2012-12-12 */
.content__left div.favicon{ 
left: auto !important; right: 1px !important; top:12px  !important;
padding-right: 2px !important;
margin-right: 0 !important;
} 
/* footer */
.content__left .pager:before{
 position: fixed !important;
 background: rgba(255, 219, 76, .5) !important;
 opacity: .8 !important;
 color: red !important;
 z-index: 248 !important;
 right: 0 !important; left: auto !important;
 visibility: visible !important; 
 padding: 0 !important; margin: 0 4px 4px 0 !important;
 border:thin solid grey !important;
 border-radius: 0 12px  0 12px !important;
 top: auto !important; bottom: 0 !important;
 width: 32px !important; height: 32px !important;
 content: url(data:image/png;base64,) !important;
}
.content__left .pager{
 position: fixed !important;
 z-index: 248 !important;
 padding: 0 4px 0 0 !important; margin: 0 !important;
 width: auto !important; min-width: 300px !important;
 bottom: 0 !important; 
 right: 1px !important; 
 top: auto !important; left: auto !important;  
 background: rgba(236, 236, 254,.9) !important;
 border: 1px solid #777 !important;
 border-width: 1px 0 0 1px !important;
 border-radius: 0 16px 0 16px !important;
 visibility: hidden !important;
 transition: visibility 0s linear 0.6s !important;
 -webkit-transition: visibility 0s linear 0.6s !important;
}
.content__left .pager > *{
 text-align: right !important;
}
.content__left .pager:hover {
 visibility: visible !important;
 transition: visibility 0s linear 0.25s !important;
 -webkit-transition: visibility 0s linear 0.25s !important;
}
.content__left .pager:hover:before {
 visibility: hidden !important;
 opacity: 0 !important;
 transition: visibility 0s linear 0.25s !important;
 -webkit-transition: visibility 0s linear 0.25s !important;
}
.map__overlay-pane,
.ymaps-copyright-legend-container,
.map__controls{
 opacity: .3 !important;
 transition: visibility 0s linear 0.5s !important;
 -webkit-transition: visibility 0s linear 0.5s !important;
}
.map__overlay-pane:hover,
.ymaps-copyright-legend-container:hover,
.map__controls:hover{
 opacity: 1 !important;
transition: opacity 0s linear 0.2s !important;
-webkit-transition: opacity 0s linear 0.2s !important;
 transition-delay: .2s !important;
}
.main__center-inner >  .intents
{ display: none !important; } 
.main__center,
.serp-list { padding: 0 !important;}
.main.layout .main__center { 
 margin-top: 4px !important; }
/* ...glitches... */
.footer__col {
    padding-bottom: 0 !important;
}
div.navigation,
div.main  {background-color: transparent !important;}
/* 2014-07-20 */
.serp-item__extra-wrap .serp-item__data {
 overflow: visible !important; }
.serp-item_glue_fresh .serp-item__extra_type_right {
margin: 0 !important;  padding: 0 4px !important; text-align: right !important;width: auto !important; }
.b-serp-item__date {color: #789 !important; }
.serp-navigation {
    margin-top: -8px !important;
    margin-bottom: 22px !important;
}
.b-page_baseline_serp3 .z-news__links {
    margin-top: -8px !important;
}
body .serp-item__greenurl{
 margin-top: -1px !important;
}
.serp-item__passages {
    color: #888888;
    margin-top: 0 !important;
}
.serp-list ~ .intents {display:none !important; }

.serp-list .serp-item { /* 2015-07-20 */
 background-image: linear-gradient(to top, 
 rgba(255,255,255, .5), rgba(224,224,255, .5) ) ;
 border-radius: 8px 16px 0 0 ;
 padding: 4px 2px 4px 2px !important;
}
.intents__container {
 margin-top: 4px !important;
 margin-bottom: 4px !important;
 }
 /*140905 */
.z-images{ 
 padding-left: 0 !important; 
 padding-right:0 !important;}
/* 2014-10-18 */
.z-entity__right-list {margin-left: 0 !important;}
/*141105 */
.z-images.i-bem div.serp-url {
 margin-left: 0.1em !important; max-width:49vw !important;
 overflow-x: hidden !important;
}
/* 150104 */
.z-related-list {
  margin-left:-4px !important;
}
.footer {line-height: 14px !important;
 margin-top:4px !important; padding:2px !important;
}
.content__left .region-change, .content__left .competitors
 { display: inline-block !important; padding-left: 2em !important;}
/* 151119  .z-entity__content-right */
.content__right .serp-list_right_yes:before
{ 
display: block!important;
visibility: visible !important;
position: absolute !important;top:40px!important; right:0 !important; 
z-index: 147483647 !important;
/*content: "\21e9" */
content: url(data:image/png;base64,
)
}


.content__right:hover .serp-list_right_yes:before
{
visibility: hidden !important;
transition: visibility 0s linear 0.3s !important;
-webkit-transition: visibility 0s linear 0.3s !important;
}
.z-related-list {margin-top:0 !important;}
/* 150227.17:00 */
.main {
    min-height: calc(100% - 0.1em) !important;
}
.ymaps-map, .map__dynamic-map {
width: 100% !important;
}
/* 2015-03-24 */
.z-news__title a.b-link{
max-width: 42vw !important;
}
/* 2015-06-03 */
.serp-item__extra_type_right { 
margin-left: 1px !important;
}
.serp-item__extra_type_right .serp-item__mime {
display: inline-block !important; position: relative !important;
}
.serp-item__extra_type_right .serp-item__mime-size{
 display: block !important;
 position: absolute !important;
 line-height: 12px !important;
 bottom: -.3em !important; right: 1px !important;
 background: rgba(227,227,227,.4) !important;
 border-radius: 10px;
 font-size: x-small !important;
}

h2.serp-item__title >a{
max-width: 505px !important;
max-width: 47vw !important;
overflow: hidden !important;
} 
/* 150819 */
.b-page_initial-scroll_yes{ overflow-y: auto !important;}
.content__left .intents {
    padding: 5px 0px 0px 8px !important;
}
body .header_fixed_yes 
{ min-height: 64px !important;}
/* 150824 */
.main__center {
    margin-left: 3px !important;
    padding-left: 0 !important;
}
.main_distro-footer_yes {
    padding-top: 410px !important;
}
.serp-item {
    margin-bottom: 0px !important;
}
.intents {
    margin-bottom: 2px !important;
}
.content__left .related { margin-bottom: 2px !important; }
.footer .footer__line { display: inline-block !important; padding-left: 2em !important; }
/* AdBlock rule: yandex.ru##[class^="distro"] */
  .serp-footer__main,
  .footer .distro { display: none !important; }
/* */
.footer__inner{padding:  0 !important;}
.footer__line {line-height: 1em !important;}
.main {padding-bottom: 0px !important;}
.main__center{ min-height: calc(84vh - 1em) !important;}
.serp-meta {white-space: normal !important;}
.pager .pager__item {text-align: center !important;}
.serp-item{  max-width: 49vw !important; } /* 15-12-06 */
.related .related__column {
-moz-column-count: 2 ;
-webkit-column-count: 2 ;
column-count: 2 ;
-moz-column-width: 23vw !important;
-webkit-column-width: 23vw !important;
column-width: 23vw !important;
margin-left: 4px !important;
}
.audio-track, .audio-playlist { margin-right: 0 !important; }
/* 16-01-18 */
.serp-header__wrapper{position:absolute!important;}
/* 16-02-29 */
.organic__outside-wrapper-right{ 
display: inline-block !important; width: auto !important; 
margin: 0 0 0 4px!important;
}
.organic__outside-wrapper-left{ margin-right: 0 !important;}
/* 160429 */
.z-video_design_new, 
.z-images__thumbs-list {width: auto !important;} 
.z-video_carousel-size_small {max-width: none !important;}
/* 161023 */  
.template-fact,
.z-maps .map,
.z-maps .map *[style^="width"],
.navigation
{width:auto !important;}
/* 170803 */
.main.main_logo-description_yes{
    margin-top: -430px !important;
}
.serp.serp__spin.i-bem.serp_js_inited.serp_loading_yes.serp__spin_progress_yes{z-index:-1!important;}
.navigation_type_horizontal{ /* 170827 */
  position: relative!important;
}
/* .service__name, .navigation__more-label
  {     margin-top: -8px !important;} /* */
a.service__url {
    text-decoration: none !important;
}
.navigation_type_horizontal {box-shadow: none !important;}
.service__name::after{display:none!important;}
.distr-popup__content {display:none!important; visibility:hidden!important;}
.map2__custom-buttons,
.Scroller-Arrow,.scroller__arrow,.fold__show-more,.Fold-Arrow,.LinkMore-Link
   { opacity:0.4 !important;  }
.Scroller-Arrow_direction_right,.scroller__arrow_direction_right{ margin-right: 20px!important;}
.ya-chat-widget{display:none!important;}
}

@-moz-document regexp("https?://(www\\.)?yandex\\.com/(yand)?search.+"){
 div.main .main__left,
 div.main .content__right
 { top: 168px !important;}
}
/* */
