/* Variable definitions
   ====================
   /* ---( page defaults )--- */
body {  margin: 0px;  padding: 0px;  text-align: left; font-family: verdana; background: #baedf6 url('http://4.bp.blogspot.com/_4Unsme79etg/TFU3bZSTnmI/AAAAAAAAADY/AbDfi2Ydpew/Business+Theme_08_body.jpg') repeat-x top center; }
blockquote {  margin: 0 0 0 30px;  padding: 0px 0 0 20px;  line-height: 1.5em; }
blockquote p {  margin-top: 0;  }
abbr, acronym {  cursor: help;  font-style: normal;  }
code {  color: #f63;  }
hr {  display: none;  }
img {  border: none;  }

/* unordered list style */
ul {  list-style: none;  margin-left: 7px;  padding: 0;  }
li {  list-style: none;  padding-left: 9px;  margin-bottom: 3px;  }

/* links */
a:link {  color: #86bdc9; text-decoration: none; font-weight: bold; }
a:visited {  color: #86bdc9; text-decoration: none; font-weight: bold;  }
a:hover {  color: #ffffff; text-decoration: underline; font-weight: bold;  }
a:active { color: #ffffff; text-decoration: underline; font-weight: bold;  }

/* ---( layout structure )---*/
#outer-wrapper {  width: 1002px;  margin: 0px auto 0;   text-align: justify; }
#content-wrapper {  margin: 0px; /* to avoid the border image */  width: 100%; }

#main {  float: left;  width: 892px;  margin: 0px 0px 0 55px; padding: 0px 0px 0px 0px; line-height: 1.5em; border: 0px solid #777777;  word-wrap: break-word; /* fix for long text breaking sidebar float in IE */  overflow: hidden;     /* fix for long non-text content breaking IE sidebar float */  }

#center { background: url('http://2.bp.blogspot.com/_4Unsme79etg/TFU3hRA54MI/AAAAAAAAADo/TC9dDSkc3GA/Business+Theme_08_center.png') repeat-y top center; }


/* ---( header and site name )--- */
#header-wrapper {  margin: 0;   }
#header {  margin: 0; height:254px; width:1002; color: #000000; background: url('http://2.bp.blogspot.com/_4Unsme79etg/TFVWqSmpDMI/AAAAAAAAAEI/1r3FSBimCCI/Business+Theme_08_cabecera2.jpg') no-repeat top center; }
#header h1 { width: 562px; font-size:48px; font-family: 'Tangerine', century gothic, verdana; font-weight:bold; color: #ffffff; text-decoration: underline; line-height: 1em;  margin: 0px 0px 0px 430px; padding-top: 30px; padding-bottom: 0px;  text-align: right; border: 0px solid #cc0000; }
#header h1 a {  font-size:48px; font-family: 'Tangerine', century gothic, verdana; font-weight:bold; color: #ffffff; text-decoration: underline; }
#header h1 a:hover {  font-size:48px; font-family: 'Tangerine', century gothic, verdana; font-weight:bold; color: #a9d8ef; text-decoration: underline; }
#header .description { margin: 35px 0px 0px 440px; padding-top: 0px; text-align: right; width:552px; font-size:16px; font-family: century gothic, verdana; font-weight:bold; color: #dbf9ff; line-height: 1em;  border: 0px solid #cc0000;}

.menu3 {padding:0px 0 0 0px; margin: 0 0 0 60px; list-style:none; height:30px; width:870px; }
.menu3 li {float:right; }
.menu3 li a {display:block; float:left; height:20px; color:#ffffff; text-decoration:none; font-size:13px; font-family: century gothic, verdana; font-weight:bold; text-align:center; line-height:30px; padding:0px 0 8px 0px; border-left: 1px solid #ffffff;  cursor:pointer;}
.menu3 li a b {float:left; display:block; padding:0 10px 0 17px;}
.menu3 li.current a {color:#aaa; }
.menu3 li.current a b {}
.menu3 li a:hover {color: #86bdc9; text-decoration: underline; background: none no-repeat top center;}
.menu3 li a:hover b {}
.menu3 li.current a:hover {color: #86bdc9; cursor:default;}
.menu3 li.current a:hover b {}

/* ---( main column )--- */
h2.date-header { margin-top: 0px;  padding-left: 0px; margin-bottom: 0px; padding-top: 0px; padding-bottom: 0px;  font-size:13px; font-family: trebuchet, verdana; font-weight:bold; color: #7b7b7b; line-height: 1.4em; text-align: right; }
.post h3 {  margin: 8px 0 0px 0;  padding: 7px 12px 7px 12px; font-size:17px; font-family: century gothic, verdana; font-weight:bold; color: #ffffff; text-decoration: none; text-align: center; border: 1px solid #315c69; background: url('http://1.bp.blogspot.com/_4Unsme79etg/TFU8zYrU7MI/AAAAAAAAADw/nBbT_NPQINU/Business+Theme_08_titulopost.jpg') no-repeat top center; }
.post { text-align: justify; margin-left:0; margin-right:0; margin-top:0px; margin-bottom: 20px; padding-left: 15px; padding-right: 15px; padding-top:0px; padding-bottom: 0px; font-size:13px; font-family: century gothic, verdana; font-weight:normal; color: #ffffff; }
.post h3 a {font-size:17px; font-family: century gothic, verdana; font-weight:bold; color: #ffffff; text-decoration: none; }
.post h3 a:hover { font-size:17px; font-family: century gothic, verdana; font-weight:bold; color: #dbf9ff; text-decoration: none;  }
.post-footer {  margin: 0;  padding-left: 10px; padding-right: 10px; padding-top: 0px; padding-bottom: 0px;  font-size: 95%; text-align: left;  color:#ffffff;  }
.post img {  padding-right: 5px;  border-top: 0px solid #ddd;  border-left: 0px solid #cccccc;  border-bottom: 0px solid #c0c0c0;  border-right: 0px solid #c0c0c0;  }
.feed-links {  clear: both;  line-height: 1.5em; visibility:hidden;
display:none;}
.blog-feeds { text-align: right; }
#blog-pager-newer-link {  float: left; margin-left: 10px; font-size: 90%;}
#blog-pager-older-link {   float: right; margin-right: 10px; font-size: 90%;}
#blog-pager {text-align: center; font-size: 90%; margin: 0px 15px 0px 15px; padding-top: 10px; border-top: 2px solid #315c69; }


/* Comments
----------------------------------------------- */
#comments {
margin-left:15px; margin-rigth: 0px;
font-size:13px; font-family: century gothic, verdana; color: #ffffff;}
#comments h4 {
margin:0 0 10px;
padding-top:.5em;
padding-left: 30px;
line-height: 1.4em;
}
#comments-block {
line-height:1.6em;

}
.comment-author {

margin:.5em 0 0 20px;
padding:0 0 0 40px; 
font-weight:bold;
}
.comment-body {
margin:0;
padding:7px 7px 7px 47px; 
}
.comment-body p {
margin:0 0 .5em;
}
.comment-body-author{
 
margin-left:20px;
padding:7px 7px 7px 50px; 
}
.comment-body-author p {
font-size:100%;
margin:0 0 .2em 20px;
text-decoration:bold;
}
.comment-footer {
margin:0 0 .5em;
padding:0 0 .75em 40px;
color:#ffffff;
}
.comment-footer a:link {
color:#ffffff; 
}
.deleted-comment {
}

/* ---( sidebar )--- */
.sidebar h2 {   margin: 0 0 10px 0;  padding: 0; font-size:17px; font-family: century gothic, verdana; font-weight:bold; color: #ffb401; text-decoration: none; text-align: left; border-bottom: 1px dashed #ffb401; }
.sidebar .widget {  margin: 0px 0 0 0;  padding: 0px 10px 70px 10px; font-size:13px; font-family: century gothic, verdana; font-weight:normal; color: #ffffff; background: url('http://4.bp.blogspot.com/_PsKtPH1i3UA/S_UHl9oV5iI/AAAAAAAAALI/asOqhohwNqg/Businness_m_black_gadgetbottom.jpg') no-repeat bottom center; }
.sidebar a {color: #ffb401; font-weight:bold; text-decoration: none;}
.sidebar a:hover {color: #ffffff; font-weight:bold; text-decoration: underline;}
.sidebar li { padding-left: 5px;  }
.profile-textblock {  margin:.5em 0 .5em; text-aling: justify;   }
.profile-img { float: left;  margin: 0 5px 5px 0;  border: 0px solid #ddd;  padding: 4px;}

/* ---( footer )--- */
.clear { /* to fix IE6 padding-top issue */  clear: both;  }
#footer-wrapper {  margin: 0px 0 0px 0px ;  width: 1002px;  padding: 0px 0 42px 0px;  font-size:12px; font-family: century gothic, verdana; font-weight:normal; color: #ffffff; text-align: center; line-height: 1.6em; background: url('http://3.bp.blogspot.com/_4Unsme79etg/TFVIvn4tdsI/AAAAAAAAAEA/FZWFjbRsRyw/Business+Theme_08_footer.png ') no-repeat bottom center; }
#footer { margin: 0px 0 0 0px ;  width: 1002px;  padding-bottom: 0px;  font-size: 100%;   }

/** Page structure tweaks for layout editor wireframe */
body#layout #outer-wrapper,
body#layout #main{  padding: 0;}

body#layout #sidebar {   padding-top: 0;  margin-top: 0; width: 200px}
body#layout #outer-wrapper,body#layout #content-wrapper {   width: 950px;}
body#layout #sidebar {   margin-right: 0;  margin-bottom: 1em; width: 200px }
body#layout #header,body#layout #footer,
body#layout #main {  padding: 0;width: 950px;
 }
