css:
@-moz-document domain("armagedomfilmes.biz") {
body { background: #D2D9DF; color: #666!important; }
* { border-radius: 0!important; box-shadow: none!important; opacity: 1!important; text-shadow: none!important; }
a { color: #555!important; }

#header { background: #33454E!important; border-bottom: 2px solid #FFF; height: 150px; }
#header_centro { background: #33454E!important; height: 100%; }
#header_centro_logo { height: 52px; margin: 50px 0 0; }
#header_centro_logo a { background:url("http://goo.gl/IL7D7") no-repeat 0 ; display: block; height: 55px; width: 300px; }

#header_centro_submenu { margin: 15px 0 0; background: #FAFAFA!important; border: 1px solid #FFF; border-right:0!important; width: auto; height: 23px; }
#header_centro_submenu ul { margin-top: 0px; }
#header_centro_submenu ul li a { background: #FAFAFA!important; border-right: 1px solid #DDD; padding: 10px 14px; }
#header_centro_submenu ul li a:hover { background: #F6F6F6!important; border-color:#CCC; text-decoration:none;}
#header_centro_search { margin: 57px 7px 0 0;}
#header_centro_search #subbox { font-family: arial; border: 1px solid #FFF; text-indent: 10px; width: 80%; background:#FFF; }

.enviar {background: #222 url("http://www.baixaki.com.br/img/v10/ic-header-v11.png") no-repeat 20px -18px; height: 29px; width: 56px; margin: 3px 0;}

#footer { background: #FFF!important; border-top: 2px solid #BBB; height: 55px; }
#footer_centro { background: #FFF!important; }
#footer_centro #copy { color: #555; margin: 10px 0 0 0; text-shadow: none; }

#anuncio-armagedom-top, #text-46, #text-47, #text-52, #text-58, #text-59, .footer-sidebar, #footer_centro #desc, #anuncio-rodape2, .fb_iframe_widget, .div-coment, .repeat-coment center, .rodape-single, div.topsy_widget_data, div.topsy_widget_shortcode, #recent-posts-3, #header_centro_redes, #header_centro_compartilhar, #header_centro_logo img, #header_centro_menu, .wp-pagenavi span.extend, .cleverd, .cleverbar, .cleverside-position-right, .cleverad-control-space, .ads-foda-fx { display: none!important; }

#wp-navation-at { background: none!important; }
.wp-pagenavi { font-size: 12px; margin-left: -2px; text-align: left; }
.wp-pagenavi a { color:#777!important; }
.wp-pagenavi span.pages, a.nextpostslink, .wp-pagenavi a, .wp-pagenavi span.extend { background: #FFF; border: 1px solid #BBB; color: #777; }
.wp-pagenavi a:hover, .wp-pagenavi span.current { background: #F6F6F6; border: 1px solid #AAA; color: #777; }

span.pages, .wp-pagenavi a, .wp-pagenavi span.current { padding: 5px 10px!important; margin: 2px!important; }


.titulo-post-nf a, .titulo-post-us a { color: #666; }
#novos-filmes {width: 780px;}
#novos-filmes ul li, #ultimos-filmes ul li, #ultimos-seriados ul li { background: #FFF; border: 1px solid #BBB; margin-right: 8px; width:146px; }
.title-category { background: #FFF!important; border: 1px solid #BBB; height: 36px!important; padding-left: 14px; width: 97%!important; }
.title-category span { margin: 5px!important; padding: 0!important; }
.title-category span a { font: bold 16px/25px arial!important; }

#novos-filmes .title-nfilmes, #ultimos-seriados .title-useries,#ultimos-filmes .title-ufilmes { background: #F3F3F3!important; border: 1px solid #BBB; font: bold 14px/35px arial; height: 35px; padding-left: 14px; width: 97%!important; }
#novos-filmes .title-nfilmes:before { content: "Novos Filmes em Lançamento"!important; }
#ultimos-seriados .title-useries:before { content: "Últimos Seriados Adicionados"!important; }
#ultimos-filmes .title-ufilmes:before { content: "Últimos Filmes Adicionados"!important; }
.capa-post-us {  }
.author-us, .author-nf { color: #000; }
.views-nf { color: #369307; }
.cat-item.cat-item-70 { border: none; }
.title-single-top { background: #FFF!important; border: 1px solid #DDD; height: 80px; overflow: hidden; }
.title-single { background: #FAFAFA!important; border-bottom: 1px solid #CCC; color: #555; margin: 0; padding: 15px 15px 0; text-shadow: none; width: 100%; }
.repeat-single, .repeat-coment { background: #FFF!important; border-top: 0; border: 1px solid #DDD; padding: 20px 15px; }
.capa-single { background: #FAFAFA; border: 1px solid #CCC; width: 174px; }
.capa-single  .aling-img-cs { margin: 5px 0 0 5px; }
.player-single { background: #555; }
.widgettitle-single { background: #FAFAFA; border: 1px solid #CCC; color: #555; height: 20px; margin: 0 0 5px; padding: 5px 10px; width: 140px; }


#sidebar { background: #FFF!important; border: 1px solid #BBB; }
.widgettitle { background: #FAFAFA!important; border-bottom: 1px solid #CCC; color: #666; font-size: 14px; height: 35px; line-height: 35px; margin-bottom: 10px; padding: 0; text-shadow: none; }
.sidctn { background: #FFF!important; }
.sidctn span { color: #444!important; }
.sidctn ul li a { line-height: 25px;  font-size:12px; padding:0; background:none;}


.coment-nf {
background: #FAFAFA;
border: 1px solid #DDD;
height: 20px;
padding: 7px 0 0;
width: 20px;
}
}
