css:
@-moz-document url-prefix("http://www.tumblr.com/"),
               url-prefix("http://tumblr.com/"),   
               domain("www.tumblr.com")  

, url-prefix("https://www.tumblr.com/") {

body {
background: #000000 top left !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:#000000 !important; 
border:1px #000000 solid
}

#posts {
background:#000000 !important
}

#logo {
height: 0 !important;
width: 0 !important;
padding-left: 210px !important;
padding-top: 49px !important;
background: url(http://media.tumblr.com/tumblr_lpp5k1eHFE1qbor6h.png) no-repeat !important;
}

.controls_section.selected.following {
background:transparent !important
}

.notification {
background:#AB98AA !important
}

#right_column li {
background:#AB98AA !important; 
opacity:0.7;
}

#right_column a {
color:white !important; 
text-shadow: 0px 0px 0px transparent !important; 
opacity:0.7
}

#right_column li.selected {
opacity:1.0; 
background:url(http://media.tumblr.com/tumblr_lpp4ddqAlU1qbor6h.png) !important; 
background-size: 100% 100%  !important;
}

#right_column li.selected:hover {
opacity:1.0
}

#right_column li:hover {
opacity:0.9
}

#right_column li.forever_alone {
opacity:1.0; 
background:url(http://media.tumblr.com/tumblr_lpp4ddqAlU1qbor6h.png) !important; 
background-size: 100% 100%  !important;
}

.notification  {
border:0px !important
}

.notification a{
border:0px !important; 
text-decoration:underline !important
}

.notification a:hover{
border-bottom:1px #AB98AA solid !important
}

.notification blockquote {
border-left:2px #AB98AA solid  !important
}

.permalink {
background:transparent !important
}

.mceContentBody {
color: rgba(0, 0, 0, 1.0) !important;
background: rgba(255,255,255, 1.0) !important;
}
IMG[src*='http://assets.tumblr.com/images/content_top_edit_form.png'], 
IMG[src*='http://assets.tumblr.com/images/content_bottom_edit_form.png']{
display:none !important;
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://media.tumblr.com/tumblr_lpp4h2jbtG1qbor6h.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}

.new_post_label{
font-size:0px !important;
padding-top:10px !important;
font-family:Georgia, Times New Roman, sans-serif !important;
font-style:italic !important;
text-transform: lowercase !important;
color:#fff !important;
letter-spacing:1px !important
}

.selection_nipple.white, 
.selection_nipple:not([class="selection_nipple white"]) {
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px #aa8ea3 !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important;
}

}


@-moz-document url-prefix("http://www.tumblr.com/new"),
url-prefix("http://www.tumblr.com/reblog"),
url-prefix("http://www.tumblr.com/tumblelog") , url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/tumblelog") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding-top:20px !important; 
border-radius: 20px
}
}
