css:
@-moz-document url-prefix("http://www.gizmodo.jp/"), url-prefix("http://www.lifehacker.jp/"), url-prefix("http://www.kotaku.jp/") {
div#wrap > ul,
div#head > div[class*="ad"],
div#headbar > :not(h1)
{display:none !important;}


div.entrylists > div:not(.clfx),
div#entry_actions,
div.entrybox,
div#main > div[class*="ad"],
div.entry_side,
div.entry > div[class*="custom_ads"],
div.entry.clfx.clr,
div#custom_ads,
div.afcbox,
div.entry_body > p.EntryMoreBanner,
div.amazon_ranking,
div.linkwithin_div,
div.rakuten_widget,
div[class^="ad_"],
div.facebook_block,
div.entry_actions,
[id^="aswift_"]
{display:none !important;}


div#side{display:none !important;}


div#foot{display:none !important;}


div#wrap > div#body{background-image:none !important;}

div#wrap > div#head > div#headbar,
div#wrap > div#body{
width:850px !important;
}
div.entrylists > div{width:820px !important;}
div#main > div#entry_detail{width:820px !important;}

body{
	background: #fff !important;
}
}
