css:
@-moz-document domain('www.google.com') {

/* ==== [Google] [UTF-8] ===============================================
 Top Page
===================================================================== */
/* Block [Let's install Google Chrome] for IE at google.co.jp */
#pmocntr2, .pmoabs { display: none !important; }

/* Block [+You] */
#gb_119.gbzt { display: none !important; }

/* Block [Click here if you start shareing with Google+] at google.co.jp */
#gbx1, #gbx2, #gbpr { display: none !important; }

/* Block footer */
#footer { display: none !important; }

/* Block PR message */
#prm { display: none !important; }

/* Navigation bar background color: white */
#gbx3, #gbx4 { background-color: #FFFFFF !important; border-color: #DAE2F2 !important; }

/* Navigaton bar color: blue */
.gbts, #gbi5 { color: #295FCC !important; }

/* Navigation bar(hover) color: gray */
.gbts:hover, #gbi5:hover { background-color: #F0F0F0 !important; }

/* Adjust the width of input box */
.big.jhp .tsf-p { padding-left: 327px !important; padding-right: 326px !important; }

/* Block [Login] button in new UI */
#gbu { display: none !important; }

/* =====================================================================
 Searich Results
===================================================================== */
/* Block logo */
#logocont { display: none !important; }

/* Block right advertisement */
#rhscol { display: none !important; }

/* Block preview */
.vspib, #nyc { display: none !important; }

/* Block place pages */
#lclbox { display: none !important; }

/* Block map link */
.osl { display: none !important; }

/* Block sponser link */
#taw, #bottomads { display: none !important; }

/* Block [SafeSearch]  */
#ab_ctls { display: none !important; }

/* Block [Block all xxxxxxxx results] */
.kob { display: none !important; }

/* White background color of input box */
.sfbgg { background-color: #FFFFFF !important; }

/* Not conspicuous the number of search results */
.sfbgg { border-bottom: 0 !important; }
#ab_name { display: none !important; }
#resultStats { left: 228px !important; top: 60px !important; height: 20px !important; margin-left: 0 !important; }

/* Fix left menu */
#leftnav { z-index: 5 !important; position: fixed !important; top: 50px !important; }

/* Maximize the width of search results */
.mw, #center_col { margin-right:0 !important; max-width: none !important; width:91% !important; }
.s { max-width: none !important; }

/* Fix navigation bar */
#mngb { position: fixed !important; top: -0.5px !important; width: 100% !important; }
#gbx4 { position: fixed !important; width: 100% !important;  }

/* Fix input box */
#sfcnt { position: fixed !important; top: 50px !important; }

/* Fix the number of search results */
#appbar { position: fixed !important; }

/* Adjust search results location */
#rcnt { top: 78px !important; }

/* Fixed area */
#appbar { z-index: 2 !important; opacity: 1.0 !important; top: 30px !important; height: 88px !important; max-width: none !important; }
.big .tsf-p, #gbc.tsf-p, .tsf-p, td#sftab.lst-td, .lst-d.lst-tbb, table.lst-t, td.gsib_a, input#lstib.gsfi, table#sftab, td.lst-td.lst-tbb, input#lst-ib.lst.lst-tbb { z-index: 5 !important; }

/* Adjust fotter location */
.big #foot, #foot { padding-top: 60px !important; }

/* Apply for New UI */
span#gbql { display: none !important; }
#center_col { padding: 10px !important; }

/* =====================================================================
 Image Search
===================================================================== */
/* Move obstructive object on input box */
#rshdr.rshdr, .sfcc, #subform_ctrl { top: 5px !important; }

/* Adjust the location of search results */
#rcnt { margin-top: 20px !important; }

}
