@namespace url(http://www.w3.org/1999/xhtml);



/* 

This should make it only work for Google searching, and should not apply to images and the like. 



I'm releasing this in Creative Commons Attribution-NonCommercial-ShareAlike license. Feel free to do what you like with it, as long as you are not making money off of this and you give proper attribution to the creator of the style (netMASA) and the image (happydragon101 on DeviantArt) 



I would have base64'd all the things, but userstyles wouldn't accept it as it make the stylesheet too big in filesize. TT

*/



@-moz-document domain("www.google.com"), domain("google.com"), domain("google.co.uk"), domain("google.ca")

{

  /* Remove the old logo. */

  #hplogo, #logo img, #logocont.nojsv img, #gbqlw img

  { 	

    height: 0 !important;

    width: 0 !important;

    display: none;

  }



  /* Replace the logo */

  #logo, #logocont.nojsv, #gbqlw

  {

    width: 113px !important;

    height: 50px !important;

    padding-top: 25px !important;

    font-size: 0 !important;

  }



  /* All the things */

  #logocont, #gbqlw

  {

    margin-left: 25px !important;

    margin-top: -5px !important;

  }



  /* New Google Header Logo */

  #gbqlw

  {

    padding-right: 65px !important;

    padding-top: 72px !important;

  }

  

  /* Move the searchbar over for the new Google style */

  #gbqfw

  {

     margin-left: 50px !important;

  }





  /* Hide the floating Google logo */

  .gbqfh #gbql, #gbql

  {

     display: none !important;

  }



  /* Replace the images, feel free to change the urls to datauris. */

  #body[class="ctr-p"]

  {

    /* DeviantArt resized (http://fc04.deviantart.net/fs71/i/2011/177/b/d/google_is_magic_vector_by_happydragon101-d3k0laj.png), reuploaded here: http://i.imgur.com/s1oE0.png */

    background: url(http://i.imgur.com/s1oE0.png) no-repeat center top !important;

  }

  #logocont, #gbqlw

  {

    /* Scaled version found here: http://i.imgur.com/0KZr1.png */

    background: url(http://i.imgur.com/0KZr1.png) no-repeat left top !important;

  }

}



/* Encrypted google has different dimensions */

@-moz-document domain("encrypted.google.com") 

{

  #logocont.nojsv

  {

    width: 180px !important;

    margin-left: 0 !important;

  }

}
