css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.verycd.com") {

/*
* UserStyles.org: USo Stylish (Style 58018)
* Author      : tumuyan
* Version     : 2.2
* Description : release
* Usage       : Opera & chrome are perfect。Firefox just so so。
*
*/
body{background-color:#ffffee !important;}
    
    dl.indexLeftItem dd {

    border-bottom: 1px solid #aaa;padding:20px 0px


}
    

    #navside > ins,div[id^="BAIDU_PAGE_AD"],div[id^="TANX_PAGE_AD"]
,p > a.firefoxplus,
.aside
,div[style^="width:250px;height:200px"]
,div[style^="width:250px;height:250px"]
,img[src^="http://v4.vcimg.com/images/banner"]
,div[style="float:right;padding:5px 0px 5px 0px;"]
,iframe[src^="http://rcm-cn.amazon.cn/e/"]
,div[id="favriteuserd"]
,iframe[src^="http://sp.vcimg.com"]
,#bottom_div
,#top_ad
,iframe[src~="http://cpro.baidu.com/cpro/"]
,div[style^="width:960px;height:90px"]
,.rightBox[style^="width:198px"]
,iframe[src^="http://sp.vcimg.com/sto"]
,div#userres + div
,a[href^=" http://www.amazon.cn"]
,#feedbackspan
{display:none !important;position:absolute !important;}



.link_line.hot_search_keywords {
font-size: 17px;
color:#999999 ;
width:110% !important;
margin-left:-90px !important;
}
 
Div#nav_div
{background:#ffffff !important;
box-shadow: 3px 3px 15px #ddddff;
}
    #navside
{background:#fff !important;
box-shadow: 3px 3px 8px #ddddff;
}
 
#page-top,#mainDiv block{
box-shadow: 3px 3px 15px #ddddff;
background:#ffffff !important;
}
 #content{background:#fff !important;}
 
div[ID="contentInfo"],ol{padding: 20px 0px !important;border:0px !important;}

#wrap{padding-bottom:50px !important;margin-right:-20px;padding-right:20px;}

DIV[ID="content"]{
    width: 800px ;
    background-color: #ffffff;
    border-radius: 20px !important;
    box-shadow: 3px 3px 15px #aaaaaa ;
padding-bottom: 60px !important;
}
 
 #page-bottom{position:absolute;display:none;z-index:-2}
 
 
div[class~="rightBox"]{
background:#eeeeee !important;
margin-top:5px !important;
border-radius: 10px;
padding:10px 0px !important;
}



.topic-list {margin-top: 0px !important;padding-top:20px;margin-left:0px;}
.left_class_order{ margin:0px 0px 10px -10px !important; width:100% !important;}


}
