css:
/*
    christopher o'doyle
    LinuxQuestions Redesign
    Cuts the crap
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linuxquestions.org")

{


/* Hide these elements */

td.page>table:nth-of-type(n+2) { display: none !important; }
body > table { display: none !important; }
td.page[valign="top"][width="165px"] { display: none !important; }
div.KonaBody > table.tborder { display: none; }
body > div:nth-of-type(3) { display: none; }
form[action="/questions/"] { display: none !important; }
div.page > div[align="left"] > table:nth-of-type(2) { display: none; } /* purposefully excessive */



/* Body & General */

body {
    width: 900px !important;
    margin: 0 auto !important;
}

* {
    background-image: none !important;
}

td.smallfont > a > img { display: none !important; }
td.smallfont > a:before {
    content: "Reply";
    
}

/* Header Redesign */

div#header:before { 
    content: "LinuxQuestions.org";
    font-size: 35px;
    font-family: Inconsolata, Monaco, Consolas, monospace;
    font-weight: 100;
}

div#header {
    margin: 10px 0 25px;
}


}
