css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url('https://www.tumblr.com/login'),

  url('https://www.tumblr.com/'),

  url('https://www.tumblr.com/register'),

  url('http://www.tumblr.com/dashboard'),

  url('http://www.tumblr.com/dashboard/'),

  url('http://www.tumblr.com/new/blog'),

  url('http://www.tumblr.com/inbox'),

  url('http://www.tumblr.com/help'),

  url('https://www.tumblr.com/preferences'),

  url('https://www.tumblr.com/lookup'),

  url('http://www.tumblr.com/explore'),

  url('http://www.tumblr.com/following'),

  url('http://www.tumblr.com/likes'),

  url-prefix('http://www.tumblr.com/new/'),

  url-prefix('http://www.tumblr.com/reblog/'),

  url-prefix('http://www.tumblr.com/blog/'),

  url-prefix('http://www.tumblr.com/tumblelog/'),

  url-prefix('http://www.tumblr.com/tagged/'),

  url-prefix('http://www.tumblr.com/spotlight/'),

  url-prefix('http://www.tumblr.com/edit/') {



/*sets text color for ALL text*/

/*

* {

  color:rgba(255, 255, 255, 1.0) !important;

}

*/



/*BIG BACKGROUND begin - background for the entire screen*/

body {

  background:url("http://farm8.staticflickr.com/7169/6768500437_96a20d1086_b.jpg") repeat fixed !important;

  background-color: rgba(44,71,98, 0.95) !important;

  background-position: 0% 0% !important;

  background-size: 100% 100%  !important;

}

/*BIG BACKGROUND end*/



/*LOGO begin - tumblr logo*/

#logo {

  width: 0px !important;

  height: 0px !important;

  background:none !important;

  width: 196px !important;

  height: 44px !important;

}

/*LOGO end*/



/*ICON BAR begin - new post icons bar - graphic replacement*/

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

opacity: 0.00 !important;  

}

#new_post{

  background-image: url(http://assets.tumblr.com/images/new_post_icons_no_labels.png) !important;

  background-position: center center !important; 

  background-repeat: no-repeat !important;

}

/*ICON BAR  end*/



/*ICON BAR BACKGROUND begin - background for the icon bar*/

#new_post {

  background-color: rgba(255, 255, 255, 1.0) !important;

}

/*ICON BAR BACKGROUND end*/



/*AVATAR ARROW begin - arrow to the left of the icon bar*/

#new_post .arrow {

  opacity: 1.0 !important;

  margin-left: 0px !important; 

}

/*AVATAR ARROW end*/



/*CONTENT begin - background for posts, icon bar, sidebar*/

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(255,255,255, 0.0) !important;

}

/*CONTENT*/ 



/*POST BACKGROUND begin*/

#posts LI[class*=" post"] {

  background: rgba(255, 255, 255, 1.0) !important;

}

/*POST BACKGROUND end*/



/*SELECTION ARROW begin - points to dashboard selection*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(255,255,255,1.0) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(255,255,255, 0.08)  !important;

}

/*SELECTION ARROW end*/



/*RADAR begin - removes radar blue frame - softens appearance*/

.post.photo_post .photo, .post.photo_post .video_thumbnail {

  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}

/*RADAR end*/



/*NEW CONTENT begin - text post message body background*/

.mceContentBody {

  color: rgba(0, 0, 0, 1.0) !important;

  background: rgba(255,255,255, 1.0) !important;

}

/*NEW CONTENT end*/



/*DO NOT DELETE THIS BRACKET*/}



.permalink {

  background: none !important;

  width: 16px !important;

  height: 16px !important;

  background: rgba(170,179,188, .85) !important;

  border-bottom-left-radius: 10px !important;

  border-top-right-radius: 24px !important;

  margin-top: 6px !important;

  margin-right: 6px !important;

}

.permalink:hover {

background: rgba(152,143,127, 1.0 ) !important;

}



.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}

.post.photo_post .photo, .post.photo_post .video_thumbnail {

  -moz-box-shadow: 2px 2px 2px rgba(0,0,0, 0.50) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}



#left_column #posts .notification {

  background-color:#582e26 !important;

  color:#666 !important;

  border-bottom-color: #4c2821 !important;

  border-top-color: #4c2821 !important;

  border-right-color: #4c2821 !important;

  border-left-color: #4c2821 !important

  

}

#left_column #posts .notification .block {

  color:#582e26 !important;

}

#left_column #posts .notification.alt {

  background-color: #582e26 !important;

}

#left_column #posts .notification.last_notification,

#left_column #posts .notification.single_notification {

  border-bottom-color:#4c2821 !important;

}

#left_column #posts .notification a {

  color:#c6b7b2 !important;

  border-bottom:0px !important;

}

#left_column #posts .notification blockquote {

  border-left-color:#522626 !important;

}

#left_column #posts .notification .avatar {

  border-color: !important;

  background-color:#522626 !important;

  opacity:1 !important;

}

#left_column #posts .notification .avatar:hover {

  opacity:1 !important;

}



.nipple {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  border-right: solid 6px rgba(88,46,38, 1.00) !important;

  border-bottom: 6px solid transparent !important;

  border-left: 0px solid transparent !important;

  border-top: 6px solid transparent !important;

  margin-top: 0px !important;

}

.nipple.border {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  border-right: solid 6px rgba(79,42,34, 1.0) !important;

  border-bottom: 6px solid transparent !important;

  border-left: 1px solid transparent !important;

  border-top: 6px solid transparent !important;

  margin-top: 1px !important;

}



*{

  text-shadow:none !important;

}



IMG[src="http://assets.tumblr.com/images/content_top_edit_form.png?alpha"],



IMG[src="http://assets.tumblr.com/images/content_bottom_edit_form.png?alpha"] {



  display: none !important;



}
