css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("songshuhui.net") {

/*
* UserStyles.org: USo Stylish (Style 58237)
* Author      : tumuyan
* Version     : 2.0.0
* Description : (Release)
* Usage       : Firefox is not so perfect display as Chrome or Opera
*
*/


body{background:#eeeeee !important;} 
#flink{display:none !important;}
}

@-moz-document url-prefix("http://songshuhui.net/archives/") {
body{background:#eeeeee !important;} 
 
#wrapper { width: 90%  !important;}
 
#content {
width: 85%  !important;
background:#ffffff !important;
box-shadow: 3px 3px 15px #ccccff;
 
position:relative  !important;
left:7%  !important;
margin:30px 0px  !important;
padding:0% 2%  !important; 
}
 
.atrctitle h2 span.contenttitle {
width: 68%  !important;
position:relative !important;
left:10% !important;
text-align: center !important;
font-size: 30px !important;
}
 
 
#sidebar,#flink,#footerBao{display:none !important;}


/* 
 * old
 #respond{width:80%;position:relative;left:12%;}
 #commentform textarea { padding: 7px 3%;margin:0%;width:80%; }
*/


/*
 * 屏蔽相关文章,便于evernote剪藏
.bshare-custom > a,.bshare-custom > span,.my-related-posts-box
{display:none !important}
*/



}
