css:
@-moz-document url-prefix("http://icrontic.com") {
body {
 background-color: black;
}
#vanilla_discussions_index{
 float: left !important;
 width: 98% !important;
 padding-left: 1% !important;
 padding-right: 1% !important;
}
h1, h2, h3, h4, h5, h6{
 color: white !important;
}
.Navigation{
 background-image: none !important;
}
.Navigation .Navbar a{
 color: green !important;
 border-width: 2px !important;
 border-style: dashed !important;
 border-color: green !important;
}
.Navigation a:hover{
 color: blue !important;
 background-color: black !important;
}
.Navigation .Navbar li.Here a{
 background-color: navy !important;
 color: green !important;
 border-style: solid !important;
}
#Menu a{
 color: green !important;
 font-weight: normal !important;
}
#Menu a:hover{
 color: blue !important;
 background-color: black !important;
}
.retainer{
 width: 97% !important;
 min-width: 955px !important;
}
.Head.retainer{
 background-color: black !important;
}
.Logo{
 background-image: url(http://ardichoke.info/img/IC-term.png);
 height: 77px;
 width: 190px;
}
.Logo img{
 height: 0;
 width: 0;
}
.Slogan{
 color: green;
}
#Body{
 float: left;
 width: 97% !important;
 min-width: 955px !important;
 background-color: black !important;
 color: green !important;
 font-family: Courier New, monospace !important;
}
#Panel{
 width: 20% !important;
 min-width: 210px !important;
}
#Content{
 width: 75% !important;
 min-width: 475px !important;
}
body.Profile #Content{
 width: 68% !important;
 min-width: 550px !important;
}
#Body a{
 color: green !important;
 font-weight: bold !important;
}
#Body a:hover{
 color: blue !important;
}
#Body h4{
 color: green !important;
}
.DataList a.Title{
 font-size: 12px !important;
 font-weight: normal !important;
 }
.TabLink,
.TabLink:link,
.TabLink:hover,
.TabLink:active,
.TabLink:visited{
 background: black !important;
 font-weight: bold !important;
 border-width: 2px 2px 0px 2px !important;
 border-style: dashed !important;
 border-color: green !important;
}
.Active .TabLink{
 background: Navy !important;
 border-style: solid !important;
}
.Tabs.CommentTabs .TabLink{
 border-width: 2px !important;
 border-color: green !important;
}
.Tabs,
.DataList .Item,
.PanelInfo li{
 border-bottom-color: green !important;
}
.Discussion .Category a{
 background: silver !important;
}
.Discussion .Category a:hover{
 background: navy !important;
}
.PanelInfo .DiscussionLink{
 font-size: 12px !important;
}
.Discussion .Message {
 font-size: 12px !important;
}
.NumberedPager a, .NumberedPager span{
 font-size: 12px !important;
 font-weight: normal !important;
}
.NumberedPager span, .NumberedPager a.Highlight{
 font-size: 16px !important;
 font-weight: bolder !important;
}
body ul div.Comment blockquote.UserQuote{
 background-color: black !important;
 color: green !important;
 border-width: 2px !important;
 border-style: dashed !important;
 border-color: green !important;
}
div.Comment blockquote.UserQuote div.QuoteText{
 color: silver !important;
}
.Activities li.Condensed .Excerpt{
 color: green !important;
}
code, pre{
 background-color: transparent !important;
 font-size: 12px !important;
 border-width: 0 !important;
}
a.BigButton{
 background-color: black !important;
 border-width: 2px !important;
 border-style: dashed !important;
 border-color: green !important;
 background-image: none !important;
 box-shadow: none !important;
 text-shadow: none !important;
}
a.BigButton:hover{
 box-shadow: none;
 text-shadow: none;
 border-width: 2px !important;
 border-style: solid !important;
 border-color: green !important;
}

/* Code specifically for styling the WP side */
.page{
 background-color: black !important;
 font-family: Courier New, monospace !important;
 color: green !important;
}
.headline{
 border-color: green !important;
}
.feature{
 border-color: green !important;
 border-style: dashed !important;
}
a, a:visited, .meta .unmeta a{
 color: white !important;
 font-weight: bold !important;
}
#commentform{
 background-color: black !important;
}
}
