css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
body {
background: url('http://i1127.photobucket.com/albums/l622/mycuppycake-/pink.png') top left #000000 repeat fixed !important;
}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(255,255,255,0.3) !important;
}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(255,255,255,0.3) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 20px 20px;
}
img#content_top, img#content_bottom {
display: none;
}

h1 {
color: #666666;
text-shadow: 0 0 3px #ffffff;
}
.no_posts_found, h1.dashboard_header {
color: #666666;
}
.left_column {
background: transparent !important;
border:0px!important;
}
.right_column {
background: transparent !important;
}
#right_column .controls_section .recessed{
background-color:transparent!important;
}
#right_column .controls_section .recessed:active{
border-top:1px solid rgba(255,0,0,0.2)!important
}
#right_column .controls_section .selected{
background:rgba(255,255,255,0.1)!important;
background-image:none!important;
border-top:1px solid rgba(0,0,0,.09)!important;
}
#right_column .dashboard_nav_item {
background: none;
background-attachment: initial;
background-position-x: 50%;
background-position-y: 100%;
background-origin: initial;
background-clip: initial;
background-color: initial;
}
#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: transparent;
}
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column 
.dashboard_nav_item .dashboard_controls_radar_media.video {
background: rgba(0,0,0,0.1);
}
a, a:visited{color:#fca5a2!important;text-decoration:none!important
}
*{text-shadow:none !important; 
color:#0000 !important;
}
div.l-header .selected .selection_nipple {
background: transparent!important;
}
#nav .nav_item.active {
background: transparent!important;
}
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent!important;
}
#nav .nav_item .nav_menu {
background: transparent !important;
border-bottom:0px!important;
}
.as_links a{
background-color:transparent!important
}
#posts .post .footer_links.with_tags .source_url{
background-color: transparent!important;
}
ol#posts li.notification.alt {
background-color: rgba(0,0,0,0.0);
}
ol#posts li.notification {
background: rgba(255,255,255,0.4) !important;
border:none !important;
}
ol#posts li.notification.first_notification {
background-color: rgba(255,255,255,0.4);
}
ol#posts li.notification {
background-color: rgba(255,255,255,0.4);
border-top:0px!important;
border-bottom: 0px solid #000000;
}
ol#posts li.notification.last_notification {
border-top:0px!important;
border-bottom: 0px solid #000000;
border-bottom-width:0px!important;
}
ol#posts li.post .permalink {
background: rgba(0,0,0,0.0);
}
#posts .notification .nipple{
border:none!important;
}
#posts .notification.alt .nipple{
border:none!important;
}
#posts .notification .nipple.border{
border:none!important;
}
#posts .notification.single_notification,
#posts .notification.last_notification {
border-bottom:0px!important;
}
#posts ol.notes li.reblog,
#posts ol.notes li.like {
background: transparent !important;
border-bottom:0px;
}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.1);
border: solid #000000;
}
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 0px solid #000000;
border-bottom: 0px solid #000000;
}
ol#posts li.post.new_post {
background: #ffffff url('http://i41.tinypic.com/adf128.png') center center no-repeat;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
width:0;
height:70px;
padding-right:250px;
opacity: 0;
}
.blue_note{
background:transparent!important;
}
.new_post_label{
font-size:14px !important;
padding-top:7px !important;
font-family:trebuchet ms !important;
font-style: !important;
text-transform: lowercase !important;
color:transparent!important;
letter-spacing:1px !important
opacity: 0;
}
form.dashboard_options_form {
background-color: rgba(0,0,0,0.1);
border-bottom: 0px solid #000000;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}
#right_column .radar .content .mask{
background:transparent!important
}
#right_column .radar .content{
background:transparent!important
}
form.dashboard_options_form .option_container {
border-top: 1px solid #000000;
}
.no_posts_found, h1.dashboard_header {
color: #666666;
}
.avatar_frame{
background-color:transparent!important;
border:none!important;
}
.notification_type_icon{
opacity:0 !important;
}
#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:transparent !important;
}

}
