/* div.r {font-color:#600000!important;} */

/* Higlights comments --Credit to Yaro */

@-webkit-keyframes comHighlight{
    from{ -webkit-box-shadow:
        inset 0px 2px rgba(0,0,0,0.05),
        inset 0px 1px rgba(0,0,0,0.5),
        inset 0px -2px rgba(0,0,0,0.025),
        inset 0px -1px rgba(0,0,0,0.5),
        0px 0px 20px rgba(0,0,0,0.25),
        inset 0px 0px 100px rgba(0,0,0,0.25);}
    to{ -webkit-box-shadow:
        inset 0px 2px rgba(255,255,255,0.05),
        inset 0px 1px rgba(255,255,255,0.5),
        inset 0px -2px rgba(255,255,255,0.025),
        inset 0px -1px rgba(255,255,255,0.5),
        0px 0px 20px rgba(255,255,255,0.75),
        inset 0px 0px 100px rgba(255,255,255,0.25);}
    }

