/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i52.tinypic.com/rvvnnp.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}





@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
h1,		h2,		h3,		h4,		h5,		h6,
		H1,		H2,		H3,		H4,		H5,		H6,
		h1 > *,	h2 > *,	h3 > *,	h4 > *,	h5 > *,	h6 > *,
		H1 > *,	H2 > *,	H3 > *,	H4 > *,	H5 > *,	H6 > *,
		[class*="header"],		[id*="header"],
		[class*="header"] > *,	[id*="header"] > *,
		[class*="title"],		[id*="title"],
		[class*="title"] > *,	[id*="title"] > *
		{
			text-align:		center		!important;
			padding:			0 0 0 0		!important;
			margin:			auto			!important;
			vertical-align:	middle		!important;
			text-indent:		0			!important;
			font-weight:		bolder		!important;
	}
		h1,	h2,	h3,	h4,	h5,	h6,
		H1,	H2,	H3,	H4,	H5,	H6,
		[class*="header"],		[id*="header"]	{
			display:		block;
			margin:		auto			!important;
			padding:		0 0 0 0		!important;
			text-align:	center		!important;
			text-indent:	0			!important;
			font-weight:	bolder		!important;
	}
}
