css:
@-moz-document url-prefix("http://www.tumblr.com/") , url-prefix("https://www.tumblr.com/") {
body {
background: url('/*[[Rin]]*/') bottom right #191919 no-repeat fixed !important;
}

.l-content {
background:rgba(0,0,0,0.5) !important;
}

#logo > .logo_anchor {
padding-left: 230px !important;
padding-top: 50px !important;
background: url('http://static.tumblr.com/emp1nqf/ouimz6oqc/25jwnqe_1_.png') no-repeat !important;
}

#logo {
overflow: visible !important;
}

#footer, #footer a {
color:#666666 !important;
background-image:none !important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.5) !important;
}

#dashboard_index div.l-content, #dashboard_posts_editing div.l-content, #dashboard_useraction_followers div.l-content, #notifications_index div.l-content, #dashboard_drafts div.l-content, #dashboard_group_members div.l-content, #dashboard_post_queue div.l-content, #dashboard_posts_likes div.l-content, #dashboard_messages_inbox div.l-content, #dashboard_messages_submissions div.l-content, #tagged_display #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.75) !important;
border: 1px solid rgba(255, 255, 255, 0.1);
}


a:hover {color:#52b1e7!important}

.search_form_row {
background-color: rgba(255,255,255,.1) !important;
}
		
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px white !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important; 
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px #111111 !important;
}	

.new_post_label { 
	font-size: 0 !important; 
}

.new_post_label > I:before {
	content: none !important;
	
}
    
.new_post_label > I {
	background-image: url('http://static.tumblr.com/emp1nqf/M4Kmz6s2n/aoexposticons.png') !important;
	transition-property: background-position !important;  
  	background-size: auto !important;
  	z-index: 1;

	}


.new_post_label:nth-child(1) > I {background-position: 2px -5px !important}
.new_post_label:nth-child(2) > I {background-position: -73px -5px !important}
.new_post_label:nth-child(3) > I {background-position: -148px -5px !important}
.new_post_label:nth-child(4) > I {background-position: -223px -5px !important}
.new_post_label:nth-child(5) > I {background-position: -298px -5px !important}
.new_post_label:nth-child(6) > I {background-position: -373px -5px !important}
.new_post_label:nth-child(7) > I {background-position: -448px -5px !important}

.new_post_label:nth-child(1):hover > I {background-position:  2px -9px !important}
.new_post_label:nth-child(2):hover > I {background-position: -73px -9px !important}
.new_post_label:nth-child(3):hover > I {background-position: -148px -9px !important}
.new_post_label:nth-child(4):hover > I {background-position: -223px -9px !important}
.new_post_label:nth-child(5):hover > I {background-position: -298px -9px !important}
.new_post_label:nth-child(6):hover > I {background-position: -373px -9px !important}
.new_post_label:nth-child(7):hover > I {background-position: -448px -9px !important}

.new_post_label:nth-child(1):active > I {background-position: 2px -5px !important}
.new_post_label:nth-child(2):active > I {background-position: -73px  -5px !important}
.new_post_label:nth-child(3):active > I {background-position: -148px -5px !important}
.new_post_label:nth-child(4):active > I {background-position: -223px -5px !important}
.new_post_label:nth-child(5):active > I {background-position: -298px -5px !important}
.new_post_label:nth-child(6):active > I {background-position: -373px -5px !important}
.new_post_label:nth-child(7):active > I {background-position: -448px -5px !important}

.post_permalink {
background: left url(http://static.tumblr.com/emp1nqf/kAUmz6ui0/doggy.png) !important;
}
.post_permalink:before {
border-right: none !important;
}

div#right_column,div#right_column:after{
background: none !important;
}

.login.sign_up,
A[href="/forgot_password"] {
display: none !important;
}

div #right_column a:before {
background:none!important;
border-right:none!important;
}
	
#right_column .controls_section .selected {
background:rgba(0,0,0,0.5)!important;
background-image:none!important;
}

#right_column .controls_section .recessed {
background-color:transparent!important;
}
	
#posts .post .post_avatar {
border: 0px!important;
background-color: #222222!important;
}

#left_column #posts .notification .avatar {
border-color: #000000 !important;
background-color: #222222 !important;
}

#left_column #posts .post .permalink {
background-color:#3e3e40; 
}

#left_column #posts .post .permalink {
background: rgba(0,0,0,0.8) !important
}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#3e3e40 !important;
background-image:none !important;
border:0px !important
}

.notification_type_icon {
display:none !important  
}

#left_column #posts .notification {
background: rgba(0,0,0,0.8) !important;
color:#666 !important;
border-color: #333 !important;
}

#new_post_notice_container {
color:#fff !important 
}

#new_post_notice_container a {
color:#fff !important
}

}
