css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kotaku.com"),
               domain("io9.com"),
               domain("gizmodo.com"),
               domain("lifehacker.com"),
               domain("jezebel.com"),
               domain("jalopnik.com"),
               domain("deadspin.com"),
               domain("fleshbot.com"),
               domain("gawker.tv"),
               domain("gawker.com")

{

/* Remove Stupid Useless Items */
#header               {display:none !important;}
#header_container     {display:none !important;}
#sidebar              {display:none !important;}
.page_navigation      {display:none !important;}
#postMeta             {display:none !important;}
#post-supplement      {display:none !important;}
.metadata             {display:none !important;}
#tead                 {display:none !important;}
#footer               {display:none !important;}
#dfpad                {display:none !important;}
#comments             {display:none !important;}
.separator            {display:none !important;}
.post-supp            {display:none !important;}
.info.top             {display:none !important;}
#sitetags             {display:none !important;}
#hidescroll           {display:none !important;}
#tomorrowsnews        {display:none !important;}
.rightBarControl.cn_pager.lower.modfont  {display:none !important;}
.topchrome.chrome.marked-head.ui-border  {display:none !important;}
#date-nav             {display:none !important;}
.sitescontainer       {display:none !important;}
.related-heading.modfont.ui-light        {display:none !important;}
.related              {display:none !important;}


/* Removing righcontainer causes endless loading, boo :(
#rightcontainer       {display:none !important;} */

/* Fix Stupid Color Scheme*/
blockquote            {background-color:black !important;}
table                 {background-color:black !important;}
.marked-head          {background-color:#191970 !important;}
.inner                {background-color:black !important;}
#container            {background-image:none !important;color:white !important;background-color:black !important;}
#content_container    {background-image:none !important;color:white !important;background-color:black !important;}
#main                 {color:white !important;}
html,body             {font-size:14px !important;background-color:black !important;}

/* Fix Stupid Font */
p                     {font-family:"Tahoma", sans-serif !important; color:white !important;}
.post h1 a            {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
.post h2 a            {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
.headline             {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
.title                {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
UL                    {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
ol                    {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
h3                    {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}
h4                    {font-family:"Tahoma", sans-serif !important; color:#e0e4d6 !important;}


}
