css:
body {
  background: url(http://static.allmystery.de/upics/BzhL5N_wallpaper.png) top left;
}
.mcell_inhalt, .mcell_inhalt2, .mcell_inhalt3 {
  -webkit-border-bottom-left-radius: 10px !important;
  -webkit-border-bottom-right-radius: 10px !important;
  -moz-border-radius: 0 0 10px 10px !important;
  background: rgba(00,00,00,0.5)!important;
  -webkit-box-shadow: 2px 2px 15px #000;
  -moz-box-shadow: 2px 2px 15px #000;
}
.mcell, .mcell_status, .mcell2, .mcell3 {
  -webkit-border-top-left-radius: 10px !important;
  -webkit-border-top-right-radius: 10px !important;
  -moz-border-radius: 10px 10px 0 0 !important;
  opacity: 0.6;
}
.header {
  background: url(http://static.allmystery.de/upics/cHO7In_header.jpg) !important;
  -webkit-border-top-right-radius: 10px;

}
.tabnav a {
  background: -webkit-gradient(linear, right bottom, right top, from(#313131), to(#858585));
  background: -moz-linear-gradient(bottom, #313131, #858585) !important;
}
.tabnav a:hover {
  background: -webkit-gradient(linear, right bottom, right top, from(#414141), to(#959595));
  background: -moz-linear-gradient(bottom, #414141, #959595) !important;
}
.userfunction a, .remove_on_search center a, .remove_on_search, .footer a, center a, .artikelmain_wrap a, #blogs-functions a, small a, span a, #se_content a, .gb_post_text a, .gb_post_text_np a{
  color: #58B1EA !important;
}
table, tbody, #overview_content {
  
  border-radius: 10px !important;
}
td a, a, .fh a, big, big b {
  -webkit-transition-property: color, text-shadow, background-color, opacity;
  -webkit-transition-duration: 0.5s;
  -moz-transition-property: color, text-shadow, background-color, opacity;
  -moz-transition-duration: 0.25s;
}
a:hover, big:hover, big b:hover, td a:hover {
  -webkit-transition-property: color, text-shadow, background-color, opacity;
  -webkit-transition-duration: 0.5s;
  -moz-transition-property: color, text-shadow, background-color, opacity;
  -moz-transition-duration: 1s;
}

#moreMenu {
  background: rgba(00,00,00,0.85) !important;
  box-shadow: 2px 2px 10px #000 !important;
  -webkit-border-bottom-left-radius: 5px !important;
  -webkit-border-bottom-right-radius: 5px !important;
  -moz-border-radius: 0 0 5px 5px !important;
 }
#moreMenu a {
  background: transparent !important;
}

.paginator {
 opacity: 0.8;
  -webkit-border-top-left-radius: 5px !important;
  -webkit-border-top-right-radius: 5px !important;
  -moz-border-radius: 5px 5px 0 0 !important;
}
table {
  opacity: 0.95;
}
.userfunction a:hover, .remove_on_search center a:hover, .remove_on_search:hover, .footer a:hover, center a:hover, .artikelmain_wrap a:hover, #blogs-functions a:hover, small a:hover, span a:hover, #se_content a:hover, .gb_post_text a:hover, .gb_post_text_np a:hover {
   color: #ACDEF9 !important;
   text-shadow: 0 0 10px #fff !important;
}
#chat_messages {
  background: rgba(00,00,00,0.7) !important;
}
 td {
  border: 1px solid #404040;
  padding: 5px;
}
#se_content, #se, .quick_item, .quick_item_header {
  z-index: 9999 !important;
  opacity: 1.0;
}
.post {
  opacity: 0.8;
}
