css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("4chan.org") {



/* LOGO */

.logo > FONT:first-of-type > B > SPAN {

  position: absolute !important;

  right: 605px !important;

  top: 40px !important;

  font: 26px Verdana !important;

  color: transparent !important; 

    text-shadow: 0px 0px 1px #EEE, 0px 0px 1px #01609F, 1px 1px 5px #A66 !important;}







.logo > FONT:nth-of-type(2) {

  position: absolute !important;

  right: 452px !important;

  top: 80px !important;

  text-align:center !important;

  width: 500px !important;

  color: #666 !important; 

  text-shadow: 0px 0px 1px #01609F !important;}



body[bgcolor]


div.logo img,

hr,

hr[size="1"][width="90%"],

div.logo {

display:inline !important;

}



	body {

		background: #2C2C2C !important;

		color: #eee !important;

	}





.logo,.logo font b span{

 color: #fff !important;

}





	

	#header {

		position: fixed !important;

		left: 0 !important;

		right: 0 !important;

		top: 0 !important;

	}



	.bf, blockquote embed, a[href*="affiliates.jlist.com"], img[src*="http://static.4chan.org/support/"], #recaptcha_logo, #recaptcha_tagline, .pages td:first-of-type, .pages td:last-of-type {

		display: inline !important;

	}



	.postarea form div {

		border: none !important;

	}



	div.postarea {

		padding: 0 !important;

	}



	* {

		font: 10pt "Verdana";

	}



	a {

		color: #6f0 !important;

	}



	hr {

		display: none !important;

	}



	#recent-images li {

		background-color: transparent !important;

	}

	

	#navtop, #navbot {

		background: #000;

		color: #000 !important;

		width: 100%;

		text-align: center;

		height: 12pt;

		font-size: 7pt !important;

	}



	#navtop *, #navbot * {

		color: #eee;

		font-size: 7pt;

		display: inline;

	}



	#navtop a:hover, #navbot a:hover {

		background: #555;

	}



	form {

		margin: 1em;

	}



	.inputtext, textarea {

		width: 295px;

	}



	.rules {

		display: inline;

	}



	td.postblock {

		background: #444 !important;

		border: 1px solid #666 !important;

		color: #aaa !important;

	}



	td.postblock * {

		font-size: 6pt;

	}



	h1, h2, h3, h4 {

		color: gray !important;

	}

	

	dt, .boxcontent, #updater {

		color: #fff !important;

	}



	.menubutton {

		background-image: none !important;

	}



 	#updater:hover, div#thread_filter.autohide:hover, .box-outer, #preview-tooltip-nws, #preview-tooltip-ws, #disclaimer-dialog, .yuimenu .bd, .reply, .replyhl, #backwash_tooltip {

		background-color:#333 !important;

		border: 1px solid #555 !important;

		color: #eee !important;

		-moz-border-radius: 8px;

		-webkit-border-radius: 8px;

		-o-border-radius: 8px;

		border-radius: 8px;

	}



	td.replyhl {

		background-color:#4B4B4B !important;

		border: 1px solid #4B4B4B !important;

		color: #eee !important;

	}



	.warning {

		color: gray !important;

	}



	.filesize, span.filetitle, .filesize a, .replytitle, .omittedposts, .abbr {

		color: #fff !important;

		font-size: 10pt;

	}



	.postername, .commentpostername, .abbr {

		color: #008080 !important;

		font-size: 10pt;

font-weight:bold;



	}





.admin,[class*="postername"] [style*="color:"] {



font-size: 10pt;



font-weight:bold;



}



	a.quotejs, .abbr {

		color: #3C9 !important;

		font-size: 10pt;

	}





	.unkfunc {

	    color: #6c6 !important;

	}





	a.linkmail, .abbr {

		color: #09f !important;

		font-size: 10pt;

font-weight:bold;



	}





	.commentpostername a.linkmail[href$='sage']:before {

	    color: #f90;

	    content: '?????';

	    text-shadow: 0 -1px 1px #f00;

font-weight:lighter;





	}







	span.filetitle, .replytitle {

		color: #C71585 !important;

	}



	#hd, small, font[size="2"], font[size="3"], #ft, #option-button, #navtopr, .doubledash, img.rotating, td[align="center"] {

		display: inline !important;

		text-align: center !important;

	}



	.pages {

		background: #444 !important;

		border: 1px solid #555 !important;

	}



	.pages td {

		color: #444 !important;

		font-size: 4pt;

	}



	.pages td a, .pages td b {

		color: #eee !important;

		font-size: 8pt;

	}



	.pages td b {

		background-color: #000;

	}



	.pages input {

		display: none !important;

	}



	.pages td:first-child {

		display: none;

	}



	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {

		-moz-appearance: none;

		-webkit-appearance: none;

		font-size: 9pt ! important;

		color: #fff !important;

		background: #333 !important;

		border-color: gray !important;

		border: 1px solid;

	}



	#filter-container .yuimenuitem-selected {

		border-color: gray !important;

	}



	.boxbar, #preview-tooltip-nws h2, #preview-tooltip-ws h2, #disclaimer-dialog h2, .hd {

		background-color: transparent !important;

	}



	dd {

		color: #fff !important;

		border-bottom: 1px gray solid !important;

	}



	input[type="submit"], input[type="button"], select {

		background: #444 !important;

		color: #aaa !important;

		border: 1px solid gray;

		-moz-appearance: none;

		-webkit-appearance: none;

		font-size: 9pt;

	}



	input[type="checkbox"] {

		-moz-appearance: none !important;

		-webkit-appearance: none !important;

		background: rgba(0,0,0,.4) !important;

		border: 1px solid rgba(255,255,255,.3) !important;

		height: 10px !important;

		width: 10px !important;

		-moz-border-radius: 2px !important;

		-webkit-border-radius: 2px !important;

		border-radius: 2px !important;

	}



	.reply input[type="checkbox"] {

		border: 1px solid rgba(255,255,255,.3) !important;

	}



	input[type="checkbox"]:checked {

		background: rgba(255,255,255,.4) !important;

	}

	

	div[style="width: 100%; font-size: 4px; background-color: rgba(224,64,0,1);"] {

		display: none !important;

	}



	th {

		background: #222 !important;

	}



	th font {

		font-size: 7pt;

	}



	td.deletebuttons {

		font-size: 8pt !important;

		color:#eee !important;

	}

	

	img#recaptcha_reload,

	img.refreshBtn {

		height: 0px !important;

		padding-top: 18px !important;

		width: 25px !important;

		background-image: url(data:image/png;base64,) !important;

	}



	img#recaptcha_switch_audio {

		height: 0px !important;

		padding-top: 15px !important;

		width: 25px !important;

		background-image: url(data:image/png;base64,) !important;

	}

	

	img#recaptcha_whatsthis {

		height: 0px !important;

		padding-top: 16px !important;

		width: 25px !important;

		background-image: url(data:image/png;base64,) !important;

	}



	.pages, .pages table, .pages tr, .pages td {

		width: 100% !important;

	}



	.pages td, #paging td {

		padding: 1px 5px;

		text-align: center;

	}	

	

	.new {

		background: rgba(255,255,255,.4) !important;

		-moz-border-radius: 5px;

		-webkit-border-radius: 5px;

		border-radius: 5px;

	}



	.postarea  td.recaptcha_image_cell, #quickrep td.recaptcha_image_cell,  td[style="padding-left: 7px;"] {

		padding: 0 !important;

	}



	#recaptcha_image img {

		opacity: 0.8;

	}



	.recaptchatable #recaptcha_response_field, #quick_recaptcha_response_field {

		margin-top: 3px !important;

	}



	.recaptcha_input_area {

		padding: 3px 0 0 0 !important;

	}



	tr[height="73"], tr[height="73"] td {

		height: 59px !important;

	}

}



@-moz-document domain("dis.4chan.org") {

	.thread, .newthread, .head {

		background-color:#333 !important;

		border: 1px solid #555 !important;

		color: #eee !important;

		-moz-border-radius: 8px;

		-webkit-border-radius: 8px;

		-o-border-radius: 8px;

		border-radius: 8px;

	}



	.border, .hborder {

		background: none !important;

		border: none !important;

	}

}
