css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com") {

ul.b-pager-pages, ul.b-pager-pages a {
font-size: 11px !important;
background-image: none !important;
background-color: #eeeeee !important;
color: #333333 !important;
}

.b-pager, .b-xylem {
background-image: none !important;
background-color: #ffffff !important;
margin-top: 0px !important;
font-size: 12px !important;
}

.b-addcomment {
font-size: 12px !important;
color: #777777 !important;
height: 0px !important;
}

.b-addcomment-icon {
margin-top: 8px !important;
}

.b-watering-wrapper {
padding: 35px 10px 10px 10px !important;
}

.b-pager-pages {
max-width: 700px !important;
}

.b-xylem {
padding: 2px 0 15px !important;
}

.b-leaf-header, .b-massaction, .b-watering-wrapper {
background-image: none !important;
background-color: #eeeeee !important;
}

.b-leaf-header .b-leaf-actions, .b-leaf-controls {
visibility: visible !important;
}

.b-leaf-article, .b-leaf-username, .b-leaf-createdtime {
font-size: 12px !important;
}

 .b-leaf-controls {
 float: right  !important;
 margin: 0px !important;
 }

.b-massaction {
background-image: none !important;
}

.b-ljbutton-submit {
padding-top: 0px !important;
margin: -10px 0px -5px -7px !important;
background-color: #eeeeee !important;
background-image: none !important;
}

.b-leaf-actions {
color: #777777 !important;
}

.b-watering-bar {
padding-top: 0px !important;
height: 20px !important;
background-image: none !important;
background-color: #eeeeee !important;
font-size: 15px !important;
}

.b-watering-buttons {
height: 30px !important;
border: 0px !important;
}

.b-watering-wrapper {
margin: 0px !important;
padding-bottom: 0px !important;
}

.b-watering-userpic {
background-color: #eeeeee !important;
border: 0px !important;
}

.b-watering-arrows {
display:none !important;
}

h1 {
font-size: 16px !important;
}

td.b-singlepost-author-userinfo {
font-size: 12px !important;
}

.b-singlepost {
font-size: 12px !important;
}

table.container {
background-color: #eeeeee !important;
background-image: none !important;
}

.standout {
height: 5px !important;
}
}
