css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tppcrpg.net")
{

body {
  overflow-x: hidden;
}

#header {
  -moz-transition: all 0.6s ease-in-out;
  position: absolute !important;
  top: 0;
  width: 100%;
  border-bottom: 1px solid #000;
}

#right, #left {
  -moz-transition: all 0.6s ease-in-out;
  position: absolute;
  top: 100px;
}

#left {
  left: -160px;
  margin-left: 0px !important;
}

#left:hover {
  left: 0px;
}

#right {
  right: -160px;
  margin-right: 0px !important;
}

#right:hover {
  right: 0px;
}

#tAoDp {
  display: none !important;
}

#body {
  margin-top: 100px;
}

#inner {
  margin: 0 50px !important;
}

#footer {
  margin-left: 20px;
  margin-right: 20px;
}

}
