css:
@-moz-document domain("erepublik.com")
 {
*{text-shadow : 0px 0px 0.1px  #000  !important}
span{color:#9E7BFF!important}
a{color:#C8B560!important}
*{color:#C68E17}
h1,h2,h3,h4,h5,h6{color:#C68E17 !important}
small{color:#FF0000 !important}

/*PULSE PROJECT*/
div.pulse_section.user_section{float:right !important;position:relative!important;left:160px;bottom:200px}

/*背景圖*/
body{background-image: url("http://i.imgur.com/x0yNS.jpg")!important;
background-repeat: no-repeat ;background-attachment: fixed !important;
background-position: center center;background-color:#000 !important }

/*頁面底部連結*/
div#footer{background:none !important}

/*頭像欄*/
div#large_sidebar{background-image:url("http://i.imgur.com/lIYrd.jpg")!important}
div#large_sidebar{padding:0 0 0 0 !important; width:780px !important;height:120px !important;float:left !important;position:relative}
.citizen_sidebar{float:right !important}
div#large_sidebar .simple_yellow{top:120px !important}
div.user_notify{float:none !important;position:relative!important;top:80px !important;right:-600px!important}
div.user_health{position:relative!important;top:50px !important;right:152px!important;}
div.user_finances{position:relative!important;top:0px !important;right:150px!important;}
div#large_sidebar>a.logout{display:none !important;float:none !important;position:relative!important;top:25px !important;right:-200px!important}
div.end{background-image:none !important}
div.user_missions.extended>*>*,div.user_missions.extended>*>*>*{width:300px!important}
div.user_missions.extended{float:right !important;width:300px !important;background-image:none !important}
div#large_sidebar>*{background-image:none  !important;background-color:#000 !important;opacity:0.85!important}

/*shout牆*/
#citizen_feed.duo .wall_post{width:450px !important}
#citizen_feed.duo .post_content{width:385px !important}
div.inner,div#citizen_feed.duo{width:450px !important}
div.post_content{float:right !important}
div.inner>*>*>*>*>*>*>*>*>p{font-size:1.3em !important;color: #1589FF !important}
div.inner>*>*>*>*>p{font-size:1.3em !important;color: #1589FF !important}
#citizen_feed .vote_bar{background-color:#000 !important}
#citizen_feed.duo{background:none !important;opacity:0.85!important}
div.inner{background-image:none !important;background-color:#000 !important;opacity:0.85!important}
.articlecomtent{display:block !important;}

/*home,myland,war等*/
div#menu{float:left !important;width:800px !important}
li#menu1>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/Fi6tB.png") !important}
li#menu2>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/TbOLa.png") !important}
li#menu3>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/8jMOK.png") !important}
li#menu4>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/RpxJK.png") !important}
li#menu5>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/EcKD9.png") !important}
li#menu6>a{width:128px !important; height:128px!important;background-position:0px 0px!important;background-image:url("http://i.imgur.com/g8yBt.png") !important}


/*首頁NEWS欄*/
div#articles, a.mbutton, div.missions_end{background:none !important}
td.m_stock{color:#C68E17!important}


/*倉庫*/
div.items_holder{background-image:none !important}

/*就業市場*/
tr.alt>*{background:none !important}

/*輸入文字區*/
textarea{font-size:1.3em;background-color:#000 !important}


}

@-moz-document url-prefix("http://www.erepublik.com/en/military/battlefield"){
/*戰鬥頁面*/
div#pvp{background-image:url("http://i.imgur.com/Gr8ks.jpg") !important;background-position:center!important}
div.mask.left_side, div.mask.right_side{background-color:#000000 !important;opacity:0.8!important}
strong#fighter_skill, strong#enemy_skill, em{background:none  !important}
div.accountdisplay{background-color:#463E41}

}

/*報紙頁面*/
@-moz-document url-prefix("http://www.erepublik.com/en/article"), url-prefix("http://www.erepublik.com/en/news"){
div.full_content, div.commentscontent, p.smallholder{font-size:18px!important;color: #1589FF !important}
div.commentscontent{float:none !important}

}

/*MY LAND*/
@-moz-document url-prefix("http://www.erepublik.com/en/economy/myCompanies"){
div.area{background-color:#D4E2EE !important}
div.list_group{background-color:#ddd!important}
div.area>*>*>*>*{background-color:#D4E2EE !important}
}
