css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("seattle.bibliocommons.com") {

* {
	color: black;
	/* font-weight: bold; */
}


.messaging {
        color: white !important
}

.displayMessage  .messageContent, .top_message {
        color: green !important;
}

.top_message   {
        background-color: white !important;
}

.top_message p {
        color: black !important;
        font-weight: bold !important;
}

#footer {
  color: #FFF;
	background: white !important;  /* #999999; */
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	border-radius: 3px;
}

#footer a, #footer a:hover {
	color: black !important;  /* #FFF; */
}

.itemDetailsContent p.noContent, .help, .finePrint, a.flagButton, p.disclaimer, span.disclaimer {
	color: black !important;  /* #999; */
        /* font-weight: bold !important; */
        font-size: 100% !important;
}

.shelfBrowserButtonDisabled, .preTitle {
        color: black !important;
}

.shelfBrowserButtonDisabled p {
        color: black !important;
}

.format {
        color: black !important;
}

.listItem, .itemButtons, .hoverItem, .selectedItem {
        opacity: 1 !important;
}

.legend {
        /* font-size: 150%  !important;  */
}

li span.overdue {
      background-color: white !important;
      color: red !important;
      font-size: 150% !important;
      vertical-align: top !important;
      margin: 5px !important;
      outline: red double thick !important;
}

.item_not_available, span.value.overdue, span.not_yet_available, span.high {
        font-size: 150% !important;
}

/* 1-25-2012 yellow in-transit (and other) usage needs contrast - change to orange */

ul.legend li span.payable, ul.legend li span.medium, ul.legend li span.comingDue {
	background: #FFA500 !important;
}
span.in_transit {
	color: #FFA500 !important;
}

/* 1-25-2012 emphasize availability green to bold (size too?) */

.item_available, .item_available_in_local_branch, .item_available_in_home_branch,.item_available_in_preferred_branch, span.value.coming_due, span.ready_for_pickup, span.low {
	/* color: #00BF13; */
        /* font-size: 150% !important; */
        font-weight: bold !important;
}


}
