css:
@-moz-document domain("babelfish.yahoo.com") {

/*
* UserStyles.org: USo Stylish (Style 58670)
* Author      : tumuyan
* Version     : 0.3.3
* Description : release
* Usage       : Opera & chrome & Firefox 
*
*/


#ad, .ad, #adeast, #YAD-TARGET-east, #footerWrapper, #ybf-ads
{display: none !important;}

#ybf-url-form{width:0px !important;height:0px !important;}

table[width="760"]{position:fixed !important;z-index:6 !important;
box-shadow: 3px 2px 3px #ccc !important;
background:#fff;
top:-5px !important;
}

#ygma{position:fixed !important;top:-25px !important;}
#ygma:hover{top:0px !important;z-index:110 !important;
box-shadow: -5px 6px 4px #aaaaaa !important;
background:#fff !important;
-moz-transition:  0.5s linear !important;
-webkit-transition: all .5s ease-in-out  !important;
-o-transition: all .5s ease-in-out  !important;
}
#doc-body{top:55px!important;z-index:5 !important;background:#fff}
td[rowspan="2"],img#ygmalogo,td#ygmagreet{z-index:2 !important;}
form[name="frmTrURL"]{
position:fixed !important;
top:70px;right:20%;width:20%;height:85px;
background:#eeeeee !important;
padding:30px !important;
z-index:10;
float:right;
box-shadow: -3px 5px 5px #aaaaaa !important;
border-radius:13px 0px !important;
}

form[name="frmTrURL"]:hover{
top:70px;right:20%;width:56%;
border-radius:13px 13px !important;
z-index:11;
background:#dddddd !important;
-moz-transition:  0.5s linear !important;
-moz-transition-timing-function: cubic-bezier(0.05, 1.4, 0.25, 1) !important;
-webkit-transition: all .5s ease-in-out  !important;
-o-transition: all .5s ease-in-out  !important;
}

#ybf-tips{
z-index:8;
border-radius:13px 0px !important;
position:fixed !important;
top:190px;right:20%;width:20%;height:17px;
padding:30px !important;
overflow:hidden  !important;
background:#fff !important;
}

#ybf-tips:hover{
height:60%;
background:#ffd !important;
z-index:9 !important;
-moz-transition:  2.5s linear !important;
-moz-transition-timing-function: cubic-bezier(0.05, 1.4, 0.25, 1) !important;
-webkit-transition: .5s ease-in-out  !important;
-o-transition:  .5s ease-in-out  !important;
}


#ybf-tips h1{text-align:center !important;}
tr[valign="top"]{font-size:16px !important;z-index:0;}

img[src="http://us.i1.yimg.com/us.yimg.com/i/us/av/i/bf/systran-big-logo2.gif"]{
position:relative !important;
top:-35px !important;
z-index:6 !important;}


/*
