css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chron.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, div#flashcontent

{
color: white !important;
background: black !important;
}

select, button, div.header, div.hst-siteheader, div.container, div.blogauthor, div#branding, div.hdn_slideshow_inner, div.twtr-hd, div.twtr-tweets, div.twtr-ft

{
background: black !important;
}

span, a

{
background-color: black !important;
}

legend, select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

div#hst-centerpiecewide-container

{
border: 0px solid black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
