css:
@-moz-document domain(netvibes.com) {

.moduleHeader .unread {
color: #000 !important;

padding: 1px !important;
 
-moz-border-radius: 5px !important;
text-shadow: none !important;
}

#topLinks {
  padding-top: 0px !important;
}

#divTabs
{
  margin-top: 2px !important;
  border-bottom: 0px !important;
}

#divTabs .icon {
  margin-top: 0 !important;
}

#divTabs .actions-tab-close {
  margin: 0 0 4px 8px !important;
}

#divTabs .actions-tab-options {
  margin-bottom: -5px !important;
}

#divTabs li {
  padding: 3px 5px !important;
}

#divTabs #aNewTab {
  padding: 4px 0 !important;
  text-transform: lowercase !important;
  border: none !important;
}

#footer {
  margin: 5px 0 !important;
}

#divTabsOuter
{
visibility: hidden;
}

#tooltip
{ 
  box-shadow: 0 2px 5px 0px rgba(0,0,0,1); 
  border: 1px solid rgba(0,0,0,.4)!important;

}
#feedReader
{
  box-shadow: 0 2px 10px 2px rgba(0,0,0,1); 
}



.moduleHeader .title, .moduleHeader .title a, .moduleHeader .title a:hover, .moduleHeader .title em, .moduleHeader .title em a {
 text-shadow:0px 0px 2px #000000, 0px 0px 2px #000000,0px 0px 2px #000000 ;
 font-size: 13px;
}



div[class="moduleHeaderContainer"]
{
border-bottom: 2px solid #000;
}

div[class="moduleFrame moduleunHighlight"]
{
  box-shadow: 0 2px 10px 2px rgba(0,0,0,0.8); 
//  border: 1px solid #000 !important;
}


.moduleHeader, .white-module .moduleHeader {
   padding: 2px 0;
}


#footer {display: none !important}

#topLinks {display: none !important}

.powered, #userPages {display: none !important}

div[class="magazineView blankMagazine"] {
color: white !important;
background-color: #111 !important}

img[title="Share"] {display: none !important;}

h3[style="cursor: pointer;"] {color: white !important;}
div.article span, a.share, div.article p, div.title span, div.description p a.share, div.description p {color: #777 !important;}

span.unread {color: yellow !important;
  font-size: 1em !important;
  padding: 1px 3px 2px 5px !important;}

div.barNav {  background-color: #444 !important; }


div[class="headline unread"] a {color: white !important; text-decoration: underline !important;}

}
