css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("digg.com"){

#top_ad,#sidebar,#footer{width:0!important; display:none!important;}

.banner_ad_unit, .sub-menu,.extra-nav, .copyright{width:0!important; display:none!important;}

body {background: #fff none!important; margin-left:0!important; margin-right:0!important; padding-left:0!important; padding-right:0!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important; width:85%!important;} 

#header {float:left!important; padding:0!important; width:97%!important; margin-left:0!important; margin-right:0!important; padding-left:0!important; padding-right:0!important;}

#header-primary a, #header-secondary a {font-size: 10px!important; font-weight:normal!important; margin-left:0!important; margin-right:0!important; padding-left:0!important; padding-right:0!important; display:none!important;}

.sidebar, .warning, .positive, .tool, .help, .instruction, .news-digg{display:none!important;}

#top-keywords {width: 140px!important;}

#container {margin-left:0!important; margin-right:0!important; padding-left:0!important; padding-right:0!important; width:100%!important;}

#contents{float: left!important; margin-left:0!important; margin-right:0!important; padding-left:0!important; padding-right:0!important; background: none!important;}

#wrapper{clear:both!important; background: none!important; margin-left:0!important; margin-right:0!important; padding-left:10px!important; padding-right:10px!important; width:100%!important;}

.main{margin-right:0!important; width:100%!important;}

.extra-nav{display:none!important; background: none!important;}

.sub-menu{display:none!important; background: none!important;}

.sub-nav{display:none!important; background: none!important;}

.side-header{float:left!important;}

#item_ad,.single_ad_unit,#comments_ad,.tower_ad_unit,.top_ad_image,.comments_ad_image{display:none!important;}

.digg-count, .digg-it, span.little, a.simple, a.tight, .news-submitted a, .comments, .tool, ul.probdrop {display: none!important;}

.news-body {margin:0!important; padding:0!important;}

.news-summary {padding:0!important; margin-top:20px!important;}

a:link {color: #003!important; font-weight: bold!important; letter-spacing: 1px!important;}
a:visited {color: #999!important; letter-spacing: 1px!important;}
a:hover {color: #f00!important; letter-spacing: 1px!important;}
a:active {color: #0f0!important; letter-spacing: 1px!important;}
h3 {font-size: 13px!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important;}
}
