css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cafelibraries.org") {

/*Main Text*/
za,zp,za#text {color: deepskyblue !important; }

* {
  font-family: Arial, Verdana, Helvetica, sans-serif !important;
 }

.defaultstyle {
	background-color: #000000 !important;
	color: #ffffff !important;
}

.headerblack, .bodyfont 
{
color: #ffffff !important;
}

table {
	background-color: #000000 !important;
	color: #ffffff !important;
}


.itemlisting, label.itemlisting {
	font-size: 14px !important;
	background-color: #000000 !important;
	color: #ffffff !important;
}

.itemlisting2, label.itemlisting2 {
	font-size: 14px !important;
	background-color: #004517 !important;
	color: #ffffff !important;
}

.searchsum {
	color: #ffffff  !important;
	font-size: 14px  !important;
	background-color: #000000  !important;
}

.searchsum a:link, .searchsum a:active, .searchsum a:visited
{
   font-size: 16px  !important;
   color: red !important;
}
.searchsum a:hover
{
   background-color: red !important;
   font-size: 16px  !important;
   color: #2D2DB3 !important;
}
.searchsum b
{
   font-size: 16px  !important;
}


.pagecontainer {
	background-color: #000000 !important;
	color: #ffffff !important;
}

.pagecontainer3pg {
	background-color: #000000 !important;
	color: #ffffff !important;
}



.enrichheader, .enrichheader a {
	background-color: #000042 !important; 
	color: #006B24 !important; 
}

/* error in card cat display, stupid reason ref .enrichheader */
.panel .enrichheader {
	background-color: #ffffff !important; 
	color: #000066 !important; 
}
.defaultstyle .enrichheader {
	color: #51B573 !important; 
}


.enrichcontent {
	background-color: #000042 !important; 
	color: #ffffff !important; 
} 

.enrichmentservices {
	background-color: #000000 !important;
	color: #000042 !important;
}

.enrichtagline {
	background-color: #000042 !important;
	color: #888888 !important;
}




.searchservices, .searchheader 
{
    background-color: #145028 !important;
    color: #51B573 !important;
}

.searchcontent 
{
    background-color: #004517 !important;
    color: #ffffff !important;
}

a.searchlinks:link, a.searchlinks:active, a.searchlinks:visited
{
   background-color: #006B24 !important;
   color: #51B573 !important;
}

a.searchlinks:hover
{
   background-color: #2DB55B !important;
   color: #004517 !important;
}

.rootbarcell, .gatewaystyle
{
   background-color: #006b31 !important;
}

.rootbarcell a.rootbar:link, .rootbarcell a.rootbar:visited, .rootbarcell a.rootbar:active,
.rootbarcell a.blastoff:link, .rootbarcell a.blastoff:visited, .rootbarcell a.blastoff:active
{
    background-color: #145030 !important;
    color: #ffffff !important;
}
a.rootbar:hover, a.blastoff:hover
{
    background-color: #2DB55B !important;
}

a:link, a:active, a:visited
{
color: #006699 !important;
}
a:hover
{
    color: #ff0000 !important;
    background-color: #ffffcc !important;
}


html, body {
  background: #000000 !important;
  color: #ffffff !important;
 }

}
