css:
@-moz-document domain("lewrockwell.com") {

/*
- LewRockwell.com Reader
- Created by Jaison De Montalegre of http://www.theworldisnotready.com
*/

h1 font {
font-size: 28px !important;
line-height: 36px !important;
}

h1 {
border-top: 1px dashed #ccc;
border-bottom: 1px dashed #ccc;
padding: 10px 0;
}

p, font, a {
font-family: verdana !important;
font-size: 12px !important;
line-height: 22px !important;
text-align: justify;
}

p img {
text-align: center !important;
max-width: 600px;
}

a, a:visited, a:active {
color: #6699cc;
}

table, tr, td {
border: none !important;
border-color: none !important;
}

body {
background: #ccc;
margin: 0 auto;
}

table {
background: #fff;
padding: 10px 0;
width: 600px;
}

font img {
/* display: none; */
}
}
