body 
{ 
	background: #fff url("http://i.imgur.com/jyTXAtg.jpg") no-repeat top center !important; 
	color: #333 !important;
	margin: 0 !important;
}

#menu { background: transparent !important; height: 32px !important; width: auto !important;  }

#menu_left { background: transparent !important; width: auto !Important; margin-left: 135px !important; float: left !important; }

#nav li { margin-top: 5px !important;  width: auto !important; background: transparent !important;}
#nav li:hover { -moz-border-radius-topleft: 4px; -moz-border-radius-topright: 4px; margin-top: 8px !important; margin-bottom: -8px !important; -moz-box-shadow: 0px 0px 25px #e3edfa;}
#nav li:hover a { color: #2c64b5 !important; line-height: 20px !important; }
#nav li a { width: auto !important; height: 26px !important; line-height: 26px !important; padding: 0 5px 0 0 !important;  display: block !important; background: none !important;}  
#nav li ul { background-color: #fff !important; -moz-border-radius-bottomleft: 2px; -moz-border-radius-bottomright: 2px; margin-top: -2px !important; }
#nav li ul li { height: 24px !important; width: 120px !important; background: none !important;  margin-top: 0 !important; margin-bottom: 0 !important; padding: 0 0 0 0 !important; }
#nav li ul li:hover { -moz-border-radius: 0; margin-top: 0 !important; margin-bottom: 0 !important; -moz-box-shadow: none;}
#nav li:hover ul li a { line-height: 24px !important; }
#nav li:hover ul {-moz-box-shadow: 0px 2px 6px #bbb;}
#nav li:hover { background-color: #e3edfa !important; }
#nav li:hover a:hover {color: #2c64b5 !important; }

#nav li a { color: #fff !important; font-family: Arial !important; font-size: 1.1em !important; font-weight: bold !important; text-transform: lowercase !important; }
#nav li ul li a { color: #2c64b5 !important; font-family: "Arial", "Meiryo", "YDIYGO530-Screen", "Malgun Gothic" !important; font-size: 1em !important; font-weight: normal !important; text-transform: none !important; }

#nav ul.wider span.edit a { height: 18px !important; line-height: 18px !important; margin: 3px 2px 0 0 !important; font-size: 0.8em !important; }
#nav ul.wider span.edit a:hover { background-color: #fff !important; -moz-border-radius: 2px; }

#header, #headerSmall   { position: absolute !important; background: none !important; }
#header a, #headerSmall a { width: 135px !important; height: 32px !important; background-image: url(http://i.imgur.com/ZdOcYfD.png) !important; display: block !important; position: absolute !important; background-color: transparent !important; background-position: 0px 0 !important; }
#header a:hover, #headerSmall a:hover { background-position: 0px 0 !important; }
#header a:active, #headerSmall a:active { background-position: 0px 0 !important; }

#menu_right { width: 350px !important; display: block !important; background-color: transparent !important; }

#myanimelist
{
	margin-top: 156px !important;
	background: transparent !important;
}

#content
{ 
	border-bottom: 1px solid #2c64b5 !important;
	-moz-border-radius-bottomleft:	4px !important;
	-moz-border-radius-bottomright:	4px !important;
}

a, a:hover, a:visited
{ 
	color: #2c64b5 !important; 
}

a.lightLink {
	color: #888 !important;
	font-size: 10px !important;
}

div.membersBar
{
	background: rgba(44,100,181, 0.6) !important;
}

div.updatesBar
{
	background: rgba(44,100,181, 0.3) !important;
}

.hoverinfo .hovertitle
{
	color: #fff !important;
}

h1
{
	background-color: #e3edfa !important;
	border-bottom-color: #2c64b5 !important; 
}

.goodresult, .successQuery
{
	background-color: #e3edfa !important;
	border-color: #2c64b5 !important; 
}

.goodresult a:link,.goodresult a:active,.goodresult a:visited
{
	color: #2c64b5 !important; 
}

.badresult
{
	background-color: #e3edfa !important;
	border-color: #2c64b5 !important; 
}

.badresult a:link,.badresult a:active,.badresult a:visited 
{ 
	color: #2c64b5 !important; 
}

#dialog 
{
	border: 10px solid #e3edfa !important; 
}

#dialog td 
{
	border: 1px solid #2c64b5 !important; 
}

#dialog td td 
{
	border: 0 !important; 
}

.row_highlighted 
{
	background-color: #e3edfa !important;
}

#leftcontent, #rightbody {
	background-color: transparent !important;
}

#horiznav_nav, #horiznav_nav ul li 
{
	border-color: #2c64b5 !important;
	background:transparent !important;
}

#horiznav_nav ul li a
{
	-moz-border-radius: 4px 4px 0 0 !important;
	color: #2c64b5 !important;
}

#horiznav_nav ul li a:hover
{
	background-color: #2c64b5 !important;
	color: #fff !important;
}

#horiznav_nav ul li .horiznav_active
{
	background-color: #2c64b5 !important;
	color: #fff !important;
}

#profileRows a
{
	border-color: #2c64b5 !important;
	color: #2c64b5 !important; 
}

#profileRows a:hover
{
	background-color: #2c64b5 !important; 
	border-color: #2c64b5 !important;
	color: #fff !important; 
}

.inputButton
{
	border-top-color: #0f73da !important;
	border-left-color: #0f73da !important;
	border-bottom-color: #094888 !important;
	border-right-color: #094888 !important;
	background-color: #1a71bb !important;
}

#topSearchText, #topSearchValue
{
	background: #2b64b5 !important;
	color: #fff !important;
	border-color: #5284cc !important;
}

#topSearchValue
{
	padding: 1px !important;
}

#topSearchText:focus, #topSearchText:hover, #topSearchValue:focus, #topSearchValue:hover 
{ 
	-moz-box-shadow: 0px 0px 25px #7398cd !important; 
}

.button_add
{
	color: #888 !important;
}

.button_add:hover
{
	color: #fff !important;
	background-color: #2c64b5 !important;
}

.button_edit
{
	color: #888 !important;
}

.button_edit:hover
{
	color: #fff !important;
}

.button_form
{
	color: #888 !important;
}

.button_form:hover 
{
	color: #fff !important;
	background-color: #2c64b5 !important;
}

/* Forums */
#TB_window {
	color: #2c64b5 !important;
}

#forum_options_container ul li
{ 
	border-color: #2c64b5 !important;
}

.forum_category, .forum_category a
{
	background-color: #2c64b5 !important;
	color: #fff !important;

}

.forum_boardrowheader
{
	background-color: #2c64b5 !important;
}

.forum_postusername a, .forum_postusername a:visited
{
	color: #444444 !important;
	text-decoration: none !important;
}

.forum_postusername a:hover
{
	color: #444444 !important;
	text-decoration: underline !important;
}

#copyright:after {
	content: " Air Theme by suffix [v2.0.0] - Based on codes by Zealotus & kimatg" !important;
	color: #2c64b5 !important;
	display: block !important;
}

}


