@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
.watch-headline.watch-headline, #watch-headline.watch-headline, #watch-video.wide, #watch-main.wide
.watch-headline.watch-headline, #watch-headline.watch-headline, #watch-video.medium, #watch-main.medium
{
    width: auto !important;
}

.watch-headline.watch-headline *, #watch-headline.watch-headline *, #watch-headline.watch-headline *
{
    float: left !important;
}

div#watch-video,
#watch-video-container > #watch-video.medium #watch-player,
#watch-video-container > #watch-video.wide #watch-player
{    clear: both !important;    }


meta[itemprop="width"][content="640"] ~ #watch-video-container > #watch-video.medium #watch-player,
meta[itemprop="width"][content="640"] ~ #watch-video-container > #watch-video.medium,
meta[itemprop="width"][content="640"] ~ #watch-video-container > #watch-video.large #watch-player,
