css:
@charset "utf-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {
/*
===== CSS Information ====================================

creat        2008/03/22 - maplebread
lastupdate   2008/03/22 - maplebread
Ver          1.0
URL          http://my.opera.com/oddeye/

==========================================================
*/

html {
min-height:101% !important;
height: 101% !important;
}

* {
font-size:14px !important;    /* base-font-size   */
line-height: 1.4 !important;  /* base-line-height */
font-weight:normal !important;
background:none !important;
border:none !important;
position:static !important;
top: auto !important;
right: auto !important;
color:black !important;
margin:0px !important;
padding:0px !important;
width :auto !important;
}

div#friends{display:none !important; }/* friendä¸€è¦§ã‚’è¡¨ç¤ºã™ã‚‹ã¨ãã¯ã“ã®è¡Œã‚’å‰Šé™¤ã—ã¦ä¸‹ã•ã„*/

div.tab br,div.tab hr,div.tab p,form#search_form,
div.section-header h1,h1#header,h2.thumb,
div.section ul li form#send_via_form,
div.section br,div.intro img,a.entry-date,
div.bottom_nav p,div.bottom_nav hr,
div.bar h3,div.badge,div#whatistwitter,
div#side div.user_icon,div#navigation ul li strong,div#navigation h3,
div#footer,p.teaser,table tr.hentry td img#profile-image,ul#accessibility,
a.section-links
{display: none !important;}

a:hover {text-decoration:none !important; }

div#side               {margin-left:10px !important; float:left !important; }
div#side ul.stats li   {text-align:left !important; float:left !important; padding-right:20px !important; }
div#side ul.stats li a {padding-right:20px !important; }

div#side div.section small a                 {float:left !important; }
div#side div.section form input#user_search_q{border:1px solid #ccc !important; }

div.bar strong{margin-left:10px !important; }

div.hfeed p           {float:left;margin-left:10px !important; }
div.hfeed div.desc    {border-top:1px solid #ccc !important; margin-left:10px !important; }

div.info{float:left !important; border:1px solid #ccc !important; margin-left:10px !important; }

form#signin                   {float:left !important; margin-left:10px !important; }
form.signin table             {padding-right:10px !important; }
form#signin input             {border:1px solid #ccc !important; }
form.signin table tr td input {border:1px solid #ccc !important; }

input.update-button{float:left !important; margin-left:10px !important; }

table.doing                              {padding:3px 10px !important; width:100% !important; clear:both !important; }
table.doing tr.hentry:hover              {background-color:#ddd !important; }
table.doing tr.hentry td.content         {white-space: nowrap !important; width:auto !important; display:block;}
table.doing tr.hentry td.content strong  {width:120px !important; display:block !important; text-align:right !important; padding-right:10px !important; float:left !important; }
table.doing tr.hentry td.content .meta   {font-size:10px !important; padding-left:10px !important; }
table.doing tr.hentry td.content .meta a {font-size:10px !important; }
table.doing tr.vcard:hover               {background-color:#ddd !important; }

ul.tabMenu             {border-top:1px solid #ccc !important; text-align:left !important; margin-left:10px !important; margin-top:10px !important; clear:left !important; }
ul.tabMenu li a        {color:#ccc !important; background-color: #ddd !important; padding:1px 10px !important; }
ul.tabMenu li a:hover  {color:#000 !important; background-color: #bbb !important; }
ul.tabMenu li.active a {color:#000 !important; }

div#navigation               {width:auto !important; }
div#navigation ul            {margin-left:10px !important; }
div#navigation ul li         {display:block !important; float:left !important; }
div#navigation ul li a       {color:#ccc !important; background-color: #eee !important; padding:1px 10px !important; margin-right:4px !important; }
div#navigation ul li a:hover {color:#000 !important; background-color: #bbb !important; }



}
