css:
@-moz-document url("http://www.drudgereport.com/") {
* {
	font: 12px Georgia, "Times New Roman", Times, serif !important;
	line-height: 1.5;
}

body {
	margin: 1em;
}

a {
	color: black;
	text-decoration: none !important;
}

a:hover {
	text-decoration: underline !important;
}

a:visited {
	color: purple !important;
}

font[size="+7"] {
	font-size: 17px !important;
}

hr {
	border: 0 solid #ccc;
	border-top-width: 1px;
	margin: 1.5em 0 !important;
}

table td[width="30%"],
table td[width="40%"] {
	padding: 1em;
	width: 33%;
}

table td:nth-child(even) {
	display: none;
}

#drudgeTopHeadlines a {
	font-weight: bold !important;
}

#drudgeTopHeadlines br {
	display: none;
}

#drudgeTopHeadlines tt br {
	display: block;
}

#drudgeTopHeadlines center {
	padding-bottom: 2em;
}

#drudgeTopHeadlines img {
	padding-bottom: 1em;
}
}
