css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://docs.oracle.com/javaee/5/tutorial/") {

    html body * {
        font-size: 14pt;
    }
    
    html body table tbody tr td:first-child {
        max-width: 200px;
    }
    
    html body table {
        position: relative;
    }
    
    html body table tbody tr td:last-child {
        position: relative;
    }
    
    .maincontent {
        position: relative;
    	-moz-column-width: 16em;
    	-moz-column-gap: 2em;
    	-moz-column-rule: 1px solid #ddd;
    	-webkit-column-width: 16em;
    	-webkit-column-gap: 2em;
    	-webkit-column-rule: 1px solid #ddd;
    	max-width: 60em;
        min-height: 40em;
    }

    .maincontent img, .maincontent table {
       display: block;
       width: 100%;
    }
           
    .maincontent img:active {
        position: relative;
        width: auto;
        padding: 5px;
        background: white;
    }
    
    .maincontent table {
        font-size: .7em;
        overflow: auto;
    }
    
    .maincontent pre {
        font-size: .7em;
        overflow: auto;
        background: #eee;
        padding: 4px;
    }
    
    .maincontent tt {
        font-size: .8em;
        white-space: pre-wrap;
        background: #eee;
    }
    
    .maincontent p {
        font-family: serif;
        line-height: 1.4em;
    }
    
    .maincontent li p {
        line-height: 110%;
    }
    
    .maincontent li {
        padding: 0 !important;
        margin: -.5em 0 -.5em 1em !important;
    }
    
    .maincontent *:hover {
        background: lightblue;
    }

    p.caption {
        font-size: .9em;
    }
    
    h1, h2, h3, h4, h5 {
        font-size: 1em !important;
        margin: 1.5em 0 .5em 0 !important;
    }
    
    p.toc.level1 a {
        font-weight: bold;
        font-size: 1.2em;
    }
    
    p.toc.level2 a {
        font-weight: bold;
        font-size: 1.0em;
    }
    
    p.toc.level3 a {
        font-weight: bold;
        font-size: .8em;
    }
    
    p.toc.level4 a {
        font-weight: normal;
        font-size: .9em;        
    }
    
    p.toc.level5 a {
        font-weight: normal !important;
        font-size: .7em !important;            
    }
    
    .onpage a {
        font-style: italic;
    }

}
