css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/*
AWW HEEEEEL YEAAAAAAAAH DAWG lol
*/

body {
background-color:#FFFFFF !important;
background-image:url('http://i.imgur.com/0YCYo.gif') !important;
background-repeat:repeat !important;
background-attachment:fixed !important;
background-position:center !important;
}

.topNavLink a, .blueName:hover {
color: #FFFFFF !important;
}

#rightCol {
padding-top: 0px !important;
}

.fbCurrentThickline {
display: none !important;
}

.navSubmenu a:hover {
color: #660066 !important;
}

.navigation a, .blueName {
color: #660066 !important;
}

#profile_stream_container, #left_column, #contentArea , #leftCol, #info_tab{
background-color:none !important;
border:#B856A1 solid 2px;
background-image:url('http://i.imgur.com/4pBX2.png') !important;
}

#rightCol, #right_column {
background-color:none !important;
background-image:url('http://i.imgur.com/4pBX2.png') !important;
}

#contentCol {
background:none !important;
padding-top: 0px !important;
}

.presenceIndicator, .online, .mobile {
background-image: url("http://i.imgur.com/FUIQg.png") !important;
}

#pageLogo a {
background-color: transparent !important;
}
    
#pageLogo a:hover {
background-color: #cc00cc !important;
}

a, h1, h2, h3, h4, h5 {
color: #660066 !important;
}
    
a:hover {
color: #cc00cc !important;
}

.jewelHighlight li a:hover {
background-color: #cc00cc !important;
}

.fbNubButton {
background: -moz-linear-gradient(center top , #660066, #111111) !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}
    
.fbNubButton:hover {
background: #cc00cc !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}

.item:hover {
background: #cc00cc !important;
color: #FFFFFF !important;
}

.selectedItem a {
background: #660066 !important;
color: #FFFFFF !important;
}

a.jewelButton {
background-image: url("http://i.imgur.com/5n3xX.png") !important;
}   

a.jewelButton:hover {
background-color: #FFFFFF !important;
}  

.profile-picture img {
max-width: 174px !important;
}
 
#blueBar {
border-color: #660066 !important;
}

.fbNubFlyoutTitlebar {
background: -moz-linear-gradient(center top , #660066, #111111) !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}
    
.fbNubFlyoutTitlebar:hover {
background: #cc00cc !important;
}

.uiSideNavCount {
background-color: #660066 !important;
color: #FFFFFF !important;
}

.textInput {
color: #660066 !important;
font-family:Lucida Console !important;
}

.uiSideNavCount:hover {
background-color: #FFFFFF !important;
color: #660066 !important;
}

.count strong {
color: #FFFFFF !important;
}

.fbCurrent, .fbCurrentStory, .fbCurrentTitle {
color: #660066 !important;
}

.uiHeaderSection, .uiSideHeader {
background-color: #FFFFFF !important;
border: 1px solid #660066 !important;
color: #000000 !important;
}      

.submitBtn, .uiButtonConfirm {
background-color: #660066 !important;
background-image: none !important;
}

.uiBoxLightblue {
background-color: #660066 !important;
border: 1px solid #cc00cc !important;
color: #FFFFFF !important;
}

.uiBoxLightblue:hover {
background-color: #cc00cc !important;
border: 1px solid #660066 !important;
color: #FFFFFF !important;
}
    
.uiUfi, .ufiItem {
background-color: #FFFFFF !important;
}

#pageNav a:hover {
color: #FFFFFF !important;
background-color: #cc00cc !important;
}
	
#blueBar {
background: -moz-linear-gradient(center top , #660066, #111111) repeat scroll 0 0 transparent !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
}

.uiLinkButton input, .like_link {
color: #660066 !important;
}

.uiLinkButton input:hover, .like_link:hover {
color: #cc00cc !important;
}

body:not(.fixedBody) 

#content {
width: 1000px !important;
margin-top: 20px !important;
}

.stat_elem, .as_link, .cmnt_like_link {
color: #660066 !important;
}

.stat_elem:hover, .as_link:hover, .cmnt_like_link:hover {
color: #cc00cc !important;
}

#blueBarHolder {
background: -moz-linear-gradient(center top , #660066, #111111) repeat scroll 0 0 transparent !important;
}

.loggedout_menubar_container{
height:900px !important;
}

.loggedout_menubar{
padding-top: 100px !important;
}



}
