@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ferris.edu"),
domain("ferrisstatebulldogs.com"),
domain("ferris.orgsync.com"),
domain("ferris.bncollege.com"),
domain("ferrisalumni.org"),
domain("ferrisstatebulldogs.blogspot.com"),
domain("fsutorch.com"),
domain("ferrisformula.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, .taboff, .ferris_nav_item a, #ferris_footer_copyright, #ferris_container, #ferris_upper_header, div#yui-main div, div#Footer-Wrapper, div#PageContent-Wrapper, div#MainContent, div#Page-Content, div#Header-Wrapper, div#MainMenu-Wrapper, #MainMenu-Content, div#PageTitle, div#content, div#content_area, div#container, div#sponsors-slideshow, div.top-header, div#menu, #globalNav div a, ul.MenuBarHorizontal a.MenuBarItemSubmenu, .backgroundColour, div.mysidecolumn div, div.mycentercolumn div, .flypull, div.breadcrumbs, a.action, a.action:link, a.action:visited, a.action:active, div.newHeader, .searchsection, .fltFix, .structFooter #ContentFooterNav, .structLeft #ContentLeft2, .structLeft #ContentLeft1, .structBody #ContentLogin, .structLeft h1#ContentLeftHeading, #ContentTopNav, div#main1colTan, div#main1colLeft, div.text5padd, div#sectionTextRlg, div#sectionTextOleft, div.sectionHeading, div.sectionProHeading, div.tbListHolding, div#leftNav, div.optAccessories, div#toomuch, div.taskbuttondiv, div.success, a.actionlight, a.actionlightdisabled, a.detailsbutton, div#header-wrapper, div#content-wrapper, div.feed-links, div#main-wrapper, div#sidebar-wrapper, div.descriptionwrapper, div.tabdiv, div#sectionCornerRleft, div#sectionCornerRright, div.actionbuttondiv, div.togglediv2, div.myprofile, a.toggon2, div.emptytext, a.daylink:hover, td.offday a, div.entrydiv, div.entrytext, div.closewindow, div.activetogglediv, a.idletoggle, th.sorted a, tr#row1 th a, tr#row1 td a, tr#row2 th a, tr#row2 td a, tr.data th a, div.loginfront2, div.twtr-hd, div.twtr-bd, div.twtr-ft, div#banner, ul#navlist li a, #tabContent, a.tab span, #tabContentContainer

{
background: black !important;
}

span, div.channel-window-inside, div.channel-content-border, div.miscForm, table.loginForm, #MenuBar1 li ul li a, #container #head, div.nav, div.resources, .arrowlistmenu ul li a, div#page, div#footer, .flylinkct, .flylinkctmo, .flylinkctover, .expmenu, div#sectionTextright, div.alert

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, address, legend

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, .tabon, .taboff:hover, .ferris_nav_item a:hover, ul.ferris_dropdown_nav_item li a:hover, div#MainMenu-Content ul li a:hover, div#SubMenu ul li a:hover, #globalNav div a:hover, .flylinkct:hover, #ContentTopNav ul li a:hover, ul#navlist li a:hover

{
background: #333333 !important;
}

/*border junk*/

#SubMenu ul li a:hover

{
border-left: 3px solid black !important;
}

}
