@charset "utf-8";
/* ----------------------------------------------------------------------------
 * Wikipedia Tribute Stylesheet by Matthias Brischwein is licensed under a 
 * Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
 * See http://creativecommons.org/licenses/by-nc-sa/3.0/ 
 * for further details on this licence.
 * ----------------------------------------------------------------------------
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"), 
 domain("wikinews.org"),
 domain("wikimedia.org"),
 domain("mediawiki.org"),
 domain("wikibooks.org"),
 domain("wikiquote.org"),
 domain("wiktionary.org"), 
 domain("wikisource.org"),
 domain("wikiversity.org"),
 domain("wikimediafoundation.org"),
 domain("wiki.openstreetmap.org") 
{ 

/* --- Overall / Element Settings --- */
body:not(#www-wikipedia-org) { 
 height:auto !important;
 min-height:100% !important;
 font-size:100% !important;
 font-family:"Times", serif !important;
 padding:1px 45px !important;
 margin-top:1.95em !important; 
 background-image:url(data:image/png;base64,) !important;
 background-repeat:no-repeat !important;
 background-attachment:fixed !important;
 background-position:bottom center !important; 
}

:target {
  padding-top:2.2em !important;
}

body#www-wikipedia-org { 
  padding:1px !important;
  margin-top:0px !important;
}

body#www-wikipedia-org #p-search>h5 {
  display:none !important;
}

a { font-style:italic !important; }

a:hover {}

a.new {}

font[color] {}

hr {
 height:1px !important;
 overflow:hidden !important;
 padding:0 !important;
 margin:1px 12px !important;
 background-color:#aaa !important; 
}

img {
  /* rescale image if doesn't fit the column*/
  max-width:100% !important;
  height:auto !important;
}

ul,
ul li,
#toc ul li,
.search-types li {
 list-style:none inside none !important;
 border-collapse:collapse !important;
}

table {
 border-collapse:collapse !important;
}

input {
  -moz-appearance:none !important;
  -o-appearance:none !important;
  -webkit-appearance:none !important;
  appearance:none !important;
}

/* Input elements */
input[type="submit"],
input[type="button"] {}

textarea {}

blockquote {
 font-style:italic !important;
}

/* --- search form / top bar --- */
#mw-head {
 position:fixed !important;
 height:1.9em !important;
 background-color:inherit !important;
 z-index:100 !important;
 border-bottom:1px solid rgba(128,128,128,0.5) !important;
}


div#mw-head *,
div#mw-panel * {
  font-size:1em !important;
  line-height:1em !important;
}


/* ----- Panels ----- */
#mw-panel,
#mw-panel.collapsible-nav {
 position:fixed !important;
 top:0px !important;
 left:2.8em !important;
 right:35% !important;
 width:auto !important;
 height:1.9em !important;
 padding:0px !important;
 margin:0px !important;
 z-index:101 !important;
}

#mw-panel>div.portal,
#mw-panel.collapsible-nav>div.portal {
float:left !important;
}

#mw-panel>div.portal:not(#p-tb):not(#p-coll-print_export)>div.body {
position:absolute !important;
top:1.9em !important;
}

/* free the space occupied by the indicator arrows 
(otherwise displaces buttons when JavaScript is enabled) 
*/
#mw-panel.collapsible-nav div.portal{
 display:block !important;
 background:transparent none !important;
 padding:0px!important;
 margin:0px!important;
 height:1.9em !important;
}

/* --- Search Form --- */
#mw-head #p-search { 
 position:fixed;
 top:0.25em;
 right:2.8em;
 margin:0px !important;
 padding:0px !important;
 z-index:104 !important;
 width:19.3% !important; 
}

body:not(#www-wikipedia-org) #searchform,
#mw-head #searchInput,
#mw-head #simpleSearch {
 position:relative !important;
 top:0px !important;
 left:0px !important;
 right:0px !important;
 margin:0px !important;
 padding:0px !important;
 width:100% !important;
 height:1.25em !important;
}

#mw-head #searchInput {
 padding:0.35em 0em 0em .35em !important;
 font-size:0.8em !important;
}

#simpleSearch {}

#simpleSearch:hover,
#simpleSearch:focus,
#simpleSearch:active {}

#simpleSearch * {}

#searchButton img {
visibility:hidden !important;
}

#searchButton {
background-repeat:no-repeat !important;
background-position:50%, 50% !important;
height:100% !important;
width:12% !important;
}

#simpleSearch:hover #searchButton,
#simpleSearch:focus #searchButton,
#simpleSearch:active #searchButton {
background-image:url("data:image/png;base64,") !important;
background-repeat:no-repeat !important;
background-position:50%, 50% !important;
}


/* --- Search results --- */
.searchresults * {
width:auto !important;
}

/* ---- left menu ---- */
#left-navigation {
 position:fixed !important;
 top:5px !important;
 left:9px !important;
 width:1px !important;
 height:1px !important;
}


/* --- Mood bar --- */
#p-moodbar {
  position:fixed !important;
  top:33px !important;
  right:45px !important;
  left:auto !important;
  margin:0px !important;
  padding:0px !important;
  width:0px !important;
}

#p-moodbar *{
  position:relative !important;
  top:0px !important;
  right:0px !important;
  margin:0px auto !important;
  float:right !important;
}

#mw-moodBar-overlayWrap {
  position:fixed !important;
  top:85px !important;
  right:50px !important;
  left:auto !important;
}

#mw-moodBar-pokey {
  position:absolute !important;
  top:-59px !important;
  right:55px !important;
  left:auto !important;
}

/* --- Drop-Down settings --- */
#mw-panel div.portal,
#mw-head div.vectorTabs,
#mw-head div.vectorMenu {
 /*Important to limit sensitivity area of dropdown buttons*/
 height:0px !important;
}

div.portal a,
div.vectorTabs a,
div.vectorMenu a,
#p-personal a,
#bodyContent table#toc a {
 font-style:normal !important;
 color:#FFF !important; 
}

div.portal ul a:hover,
div.vectorTabs a:hover,
div.vectorMenu a:hover,
#p-personal a:hover,
#bodyContent table#toc a:hover {
}

#mw-panel div.portal div,
#mw-head div.vectorTabs ul,
#mw-head div.vectorMenu ul,
#mw-head #p-personal ul,
#bodyContent table#toc tbody {
 position:relative !important;
 top:0px !important;
 width:0px !important;
 height:0px !important;
 display:block !important;
 margin:0px !important;
 padding:0px !important;
 overflow:hidden !important;
 clear:both !important; 
 line-height:1.0em !important;
}

#bodyContent table#toc caption {
 display:none !important;
}

#mw-panel div.portal:hover div,
#mw-head div.vectorTabs:hover ul,
#mw-head div.vectorMenu:hover ul,
#mw-head #p-personal:hover ul,
#bodyContent table#toc:hover tbody {
 width:auto !important;
 height:auto !important;
 padding:3px 5px 3px 5px !important; 
 background:#000 !important;
 max-height:500px !important;
 overflow:auto !important;
 z-index:105 !important;
 border:1px solid transparent !important; 
}

#mw-panel div.portal:hover,
#mw-head div#p-views:hover,
#mw-head div#p-cactions:hover,
#mw-head div#p-personal:hover,
#bodyContent table#toc:hover {
 width:auto !important;
 height:auto !important;
 display:block !important;
}

#mw-panel div.portal ul,
#mw-head div#p-personal ul,
#mw-head div.vectorTabs ul,
#mw-head div.vectorMenu ul {
 font-size:.85em !important; 
}

#mw-panel div.portal div ul li,
#mw-head div.vectorTabs ul li,
#mw-head div.vectorMenu ul li,
#mw-head div#p-personal ul li,
#bodyContent table#toc tbody>ul>li
{
  list-style:none !important;
  margin:0px !important;
  padding:3px !important;
}

#mw-panel div.portal div *,
#mw-head div.vectorTabs ul *,
#mw-head div.vectorMenu ul *,
#mw-head #p-personal ul *,
#bodyContent table#toc tbody * {
 clear:both !important;
 background:none !important;
}

#mw-panel div.portal h3,
#mw-panel div.portal h5 {
 display:block !important; /* Necessary when JavaScript is enabled */
 padding:0.65em 0.7em 0em 0.7em !important;
 margin:0px -1px 0px 0px !important;
 background:inherit !important; 
 font-size:0.8em !important;
 height:1.7em !important;
}

#mw-panel div.portal:hover h3,
#mw-panel div.portal:hover h5 {
 background-color:rgba(0,0,0,0.1) !important;
}

#mw-panel.collapsible-nav .portal h3,
#mw-panel.collapsible-nav .portal h5 {
  background:none !important;
}


/* --- Specific Drop-Down settings:Language --- */
#p-lang:hover div {
 -moz-column-count:4 !important;
 -moz-column-gap:0em !important;
 -webkit-column-count:4 !important;
 -webkit-column-gap:0em !important;
 column-count:4 !important;
 column-gap:0em !important;
 z-index:103 !important;
}

/* Good article / Fantastic Article */
#p-lang .GA,
#p-lang .FA {}

#p-lang .GA a:hover,
#p-lang .FA a:hover {}

#p-lang .GA a { }
#p-lang .FA a { }

/* --- Drop-Down settings:Article Options --- */

#p-tb,
#p-views,
#p-cactions,
#p-coll-print_export,
#bodyContent table#toc {
 height:24px !important;
 width:24px !important;
 padding:0px !important;
 border:solid 0px transparent!important;
 background:transparent !important;
 box-shadow:none !important;
}

#mw-panel div#p-tb.portal h3,
div#mw-head div#p-views h3,
div#mw-head div#p-cactions h3,
div#mw-panel div#p-coll-print_export.portal h3,
#mw-panel div#p-tb.portal h5,
div#mw-head div#p-views h5,
div#mw-head div#p-cactions h5,
div#mw-panel div#p-coll-print_export.portal h5,
#bodyContent table#toc h2 {
 display:none !important;
 z-index:100 !important;
}

/*--- Specific Dropdown Settings Rating ---*/
#mw-articlefeedback>div.articleFeedback-panel:before {
  display:block !important;
  position:absolute;
  top:-20px !important;
  left:0px !important;
  right:0px !important;
  width:100px !important;
  margin:0px auto !important;
  content:"Rate this article";
  background-color:#fff !important;
  text-align:center !important;
}

#mw-articlefeedback>div.articleFeedback-panel:hover:before{
  opacity:0.0 !important;
}

#mw-articlefeedback:hover {
  height:auto !important;
}

#mw-articlefeedback {
  position:fixed !important;
  bottom:0px !important;
  left:0px !important;
  right:0px !important;
  width:52% !important;
  height:0px !important;
  margin:0px auto !important;
}

#mw-articlefeedback>div.articleFeedback-panel {
  width:100% !important;
  margin:0px auto !important;
}

/*--- Specific Drop-Down settings:TOC ---*/
#bodyContent table#toc {
 position:fixed !important;
 top:75px !important;
 left:10px !important;
 display:block !important;
}

#bodyContent table#toc:before {
 content:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAYAAADgdz34AAAAAXNSR0IArs4c6QAAAAZiS0dEAP8A/wD/oL2nkwAAAAlwSFlzAAAN1wAADdcBQiibeAAAAAd0SU1FB9sMEg4qMb/m1UEAAABBSURBVEjHY2AYBQMNGNH4zgwMDH8oNJOFgYFhLzIHGdhQyeFwC5hoHUToPjhCpSAaTUWjqWg0FY2motFUNApQAACsOw5Z1oJokAAAAABJRU5ErkJggg==) !important;
}

#bodyContent table#toc:hover:before {
 content:url(data:image/png;base64,) !important;
}

/*--- Specific Drop-Down settings:Toolbox ---*/
#p-tb {
 position:fixed !important;
 top:145px !important;
 left:10px !important;
}

#p-tb:before{
 content:url(data:image/png;base64,) !important;
}

#p-tb:hover:before {
 content:url(data:image/png;base64,) !important;
}

/*--- Specific Drop-Down settings:Print/Export ---*/
#p-coll-print_export {
 position:fixed !important;
 top:110px !important;
 left:10px !important;
}

#p-coll-print_export:before{
 content:url(data:image/png;base64,) !important;
}

#p-coll-print_export:hover:before {
 content:url(data:image/png;base64,) !important;
}

/*--- Specific Drop-Down settings:Namespaces ---*/
#p-namespaces {
 position:fixed !important;
 top:0px !important;
 left:11px !important;
 width:0px !important;
 height:0px !important;
}

#p-namespaces h3,
#p-personal h3,
#p-namespaces h5,
#p-personal h5 {
 display:block !important;
 width:1.55em !important;
 height:1.9em !important;
 margin:0px !important;
 padding:0px !important;
}

#p-namespaces h3:before,
#p-personal h3:before,
#p-personal h3:hover:before,
#p-namespaces h5:before,
#p-personal h5:before,
#p-personal h5:hover:before {
 padding:0.5em !important;
 display:block !important;
 content:"▼" !important;
 font-size:0.8em !important;
 z-index:203 !important;
 height:1.9em !important;
}

#p-namespaces:hover h3:before,
#p-personal:hover h3:before {}

/*--- personal portlet ---*/

#p-personal {
 position:fixed !important;
 top:0px !important;
 right:8px !important;
 width:0px !important;
 height:0px !important;
}

#p-personal h3,
#p-personal h5 {
 float:right !important;
}

#p-personal li {
 display:block !important;
 clear:both !important;
}

/* ###### ---- Document Menu Tabs ---- ######*/
div.vectorTabs a span,
div.vectorTabs li a,
div.vectorMenu a span,
div.vectorMenu li a 
{
 background-color:transparent !important;
 margin:0px !important;
 padding:0px !important;
 height:auto !important;
 width:auto !important;
}

#p-views ul li.selected a,
#p-cactions ul li selected a,
#p-namespaces ul li.selected a {}

#p-views ul li.selected:hover a,
#p-views ul li.selected:hover a,
#p-cactions ul li.selected:hover a,
#p-cactions ul li.selected:hover a,
#p-namespaces ul li.selected:hover a {}


/*--- Specific drop-down settings:Views ---*/
#p-views { 
 position:fixed !important;
 top:185px !important; 
 left:10px !important;
}

#p-views:before {
 display:block !important;
 content:url(data:image/png;base64,) !important;
 
}

#p-views:hover:before {
 content:url(data:image/png;base64,) !important;
}

/*--- Specific Settings:p-cactions ---*/
#p-cactions {
 position:fixed !important;
 top:225px !important;
 left:10px !important;
}

#p-cactions:before{
 content:url(data:image/png;base64,) !important;
}

#p-cactions:hover:before {
 content:url(data:image/png;base64,) !important;
}



li#pt-userpage,
li#pt-anonuserpage,
li#pt-login {
 background:transparent none !important;
 padding:0px !important;
}

li.interwiki-en,
li.interwiki-de {}


#content {
 position:relative !important;
 top:0px !important;
 margin:20px auto 90px auto !important;
 padding:20px !important;
 border:solid 1px transparent !important;
 font-size:1.09em !important;
 background-color:#fff !important;
 background-image:none !important;
}

.mw-body {
 border:solid 1px transparent !important;
}

/*--- Article related suggestions for improvement ---*/
table.ambox {
  margin:1px 0px !important;
}

/*--- Layout-Article ---*/

#bodyContent #contentSub {
 margin-left:0px !important;
}

#bodyContent>div p, span {
 text-align:justify !important;
}

/*--- Specific settings:Breadcrumbs ---*/
#bodyContent .subpages {
 position:fixed !important;
 bottom:15px !important;
 left:0px !important;
 display:block !important;
 margin:0px auto !important;
 padding:5px 10px 6px 10px !important; 
 padding-left:60px !important; 
 border:solid 1px !important;
 border-left:solid 0px transparent !important; 
 z-index:205 !important;
 font-size:1.1em !important; 
}

#bodyContent .subpages a,
#footer #footer-places a {
}

#bodyContent .subpages a:hover,
#footer #footer-places a:hover 
{}


/*--- Specific settings:Footer ---*/
#footer {
 height:0px !important;
 border:solid 0px !important;
 margin:0px !important;
 padding:0px !important;
 font-size:1.0em !important;
}

#footer #footer-places {
 position:fixed !important;
 top:0.25em !important;
 right:19.2% !important;
 z-index:200 !important;
 display:block !important;
 margin:0px 2.8em 0px 0px !important;
 padding:0.1em 0px 0.15em 0px !important; 
 border:1px solid rgba(128,128,128,0.5)!important;
 line-height:1em !important;
}

#footer #footer-places:hover {}

#footer #footer-places li {
 float:right !important;
 margin:.1em 0.55em 0px 0px !important;
 padding:0px !important; 
 font-size:.8em !important;
 line-height:inherit !important;
 text-indent:0px !important;
 display:none !important;;
}

#footer #footer-places:before {
 font-size:1em !important;
 content:"i" !important;
 float:left !important;
 width:1.5em !important;
 text-align:center !important;
}

#footer #footer-places:hover:before {}

#footer #footer-places:hover li {
 display:block !important;
}

#bodyContent th[style],
#bodyContent table[class] th {
 border:solid 1px !important;
}

#bodyContent th[style] font,
#bodyContent table[class] th font,
#bodyContent th[style] span,
#bodyContent table[class] th span
{}

.navbox-abovebelow,
.navbox-group,
.navbox-subgroup .navbox-title {}

.navbox-subgroup .navbox-group,
.navbox-subgroup .navbox-abovebelow {}

.navbox-even {}

#bodyContent table td.navbox-list,
#bodyContent table .navbox-columns-table td,
#bodyContent table.gallery td 
{}

#bodyContent pre {
overflow:auto !important;
}

#bodyContent p:first-letter {
 font-weight:bold !important;
}

#bodyContent ul li {}

/*--- Images ---*/

#bodyContent img {
 vertical-align:top !important;
}

.topicon {
 width:16px !important;
 height:16px !important;
 background:url(data:image/png;base64,) !important;
}

.topicon:hover {
 background:url(data:image/png;base64,)!important;
}

.topicon img {
 visibility:hidden !important;
}

/*-###- Content - TABLE - Colors -###-*/
#bodyContent table tr[style~="background:"],
#bodyContent table td[style~="background:"],
#bodyContent table tr[style~="background-color:"],
#bodyContent table td[style~="background-color:"],
#bodyContent table td[class][style=""]
{}

#bodyContent table.infobox td[style] {}

#bodyContent table[class~="float-right"] .navbox-group {}

table.taxobox,
table.taxobox th,
table.taxobox tr,
table.taxobox td 
{}

/* Table layouts are very heterogenous. Enforce some consens.*/
#bodyContent table[class],
#bodyContent table[style],
#bodyContent table[class] tr,
#bodyContent table[style] tr,
#bodyContent table[class] td,
#bodyContent table[style] td,
#bodyContent table[style] th,
#bodyContent table[class] th,
table.wikitable,
table.wikitable td,
table.wikitable th,
table.wikitable tr,
table.wikitable tbody {}

table.prettytable th {}

#bodyContent table[class~="float-right"] a>font {}

#bodyContent table[class~="float-right"] a+font {}


/*-###- Suggestions -###-*/
div.suggestions {
 position:fixed !important;
 right:45px !important;
 top:1.9em !important;
 float:right !important;
 margin:0px !important;
}

div.suggestions-results,
div.suggestions-special {
 border:solid 1px !important;
 font-size:0.9em !important;
}

div.suggestions-results {
 border-bottom:solid 0px transparent !important;
}
div.suggestions-special {
 border-top:solid 0px transparent !important;
}

div.suggestions-results span{}

/* div.suggestions-results .highlight {} */

.suggestions-result-current,
div.suggestions-special.suggestions-result-current
{}

/*-###- Elements Display None -###-*/
#mw-page-base,
#mw-head-base,
#mp-strapline,
#footer>*:not(#footer-places),
.emptyPortlet {
 display:none !important;
}

img[src *="Bluebg_rounded"] {
 visibility:hidden !important;
}

/*--- Site Notice ---*/
#content #siteNotice {
 position:relative !important;
 top:-35px !important;
 left:0px !important;
 right:0px !important;
 margin:0px auto !important;
 width:100% !important;
 z-index:99 !important;
}

#centralNotice,
#localNotice {
 width:100px !important;
 margin:0px auto !important;
}

#centralNotice>div,
#localNotice>div {
 display:none !important;
 border:solid 3px !important;
 margin-top:20px !important;
}

#content #centralNotice:before,
#content #localNotice:before {
 content:" Notice " !important;
 padding:3px 5px !important;
 background:#fff;
}

#centralNotice:hover,
#localNotice:hover {
 height:auto !important;
 width:100% !important;
}

#centralNotice:hover>div,
#localNotice:hover>div {
 display:block !important;
}

#content #centralNotice:hover:before,
#content #localNotice:hover:before {}

/*--- Book Creator ---*/
#siteNotice div.collection-creatorbox {
 position:fixed !important;
 top:73px !important;
 right:45px !important;
 left:auto !important; 
 text-align:left !important;
}

#siteNotice div.collection-creatorbox:hover {}

#siteNotice div.collection-creatorbox>div {
 display:none !important;
 text-align:left !important;
 padding:3px 5px !important;
 width:0px !important;
 height:0px !important; 
}

#siteNotice div.collection-creatorbox:before {
 content:"" !important;
 position:fixed !important;
 top:75px !important;
 right:15px !important; 
 left:auto !important;
 width:32px !important;
 height:24px !important;

 background-image:url(data:image/png;base64,) !important;
 background-repeat:no-repeat !important;
 background-position:center center !important;
}

#siteNotice div.collection-creatorbox:hover>div {
 display:block !important;
 width:auto !important;
 max-width:500px !important;
 height:auto !important;
}

#siteNotice div.collection-creatorbox:hover:before {
 background-image:url(data:image/png;base64,) !important;
 background-repeat:no-repeat !important;
 background-position:center center !important;
}

#siteNotice div.collection-creatorbox a {}

#siteNotice div.collection-creatorbox .collection-creatorbox-book {
 display:none !important;
}

#siteNotice #coll-book_creator_box a,
#siteNotice #coll-book_creator_box span {
 display:block !important;
}

/*
Try to fit formulas and definitions into column.
(Multi-column layout) Long formulas may get very 
small by that. So gives an option to scroll and 
zomm those big statements.
*/
#bodyContent dd,
#bodyContent .tex
{
 max-width:100% !important;
 height:auto !important;
 padding:0px !important;
}

#bodyContent dd:hover img,
#bodyContent p:hover img{
max-width:none !important;
height:auto !important;
overflow:auto !important;
}

#bodyContent img
{
 overflow:auto !important;
}
/*--- Editor tools ---*/
.wikiEditor-ui{}
.wikiEditor-ui-toolbar {}
.wikiEditor-ui-toolbar .sections .section {}
.wikiEditor-ui-text {}

#anoneditwarning,
.errorbox, 
