/* Effects */
@-moz-keyframes rollin-x {
  0% {max-width:0px;}
  100%{max-width:500px;}
}

@-moz-keyframes rollin-y {
  0% {max-height:0px;}
  100%{max-height:500px;}
}

@-moz-keyframes fadein {
   0% { opacity:0.0 !important; }
 100% { opacity:1.0 !important; }
}

@-moz-keyframes fadeout {
   0% { opacity:1.0 !important; }
 100% { opacity:0.0 !important; }
}

@-moz-keyframes glowButtons {
  0% { color:#D5DCE5 !important;
       text-shadow:
         0px -1px 1px #000,
         0px 0px 2px #DDD,
         0px 0px 0px #3BCEFF !important;
     }
 66% { color:#FFF !important;
       text-shadow:
         0px -1px 1px #3BCEFF,
         0px 0px 4px #3BCEFF,         
         0px 1px 2px #3BCEFF,
         0px 0px 7px #3BCEFF !important;     
     }       
100% { color:#D5DCE5 !important;
       text-shadow:
         0px -1px 1px #000,
         0px 0px 2px #DDD,
         0px 0px 0px #3BCEFF !important;
     }     
}

@-moz-keyframes glowNotice {
  0% { color:#222 !important;}
 50% { color:#930 !important;}
100% { color:#222 !important;}
}

@-moz-keyframes glowLinks {
  0% { color:#960 !important;}
 50% { color:#B90 !important;}
100% { color:#960 !important;}
}

@-moz-keyframes glowField {
 0% { 
  background:#EEE !important;
  border:solid 1px #7C8E9D !important;
  border-bottom-color:#9CCEDD !important;
  border-top-color:#5B7E9F !important;
  box-shadow:
  0px -3px 5px 0px rgba(0,0,0,0.3),  
  0px 7px 10px -3px rgba(200,200,200,0.4),
  inset 0px 0px 3px rgba(0,0,0,0.9),
  inset 0px 5px 25px rgba(200,200,200, 0.5) !important;
  }
50% { 
  background:rgb(59,206,255) !important;
  border:solid 1px #7C8E9D !important;
  border-bottom-color:#9CCEDD !important;
  border-top-color:#5B7E9F !important;
  box-shadow:
  0px -3px 5px 0px rgba(0,0,0,0.3),  
  0px 7px 10px -3px rgba(59,206,255,0.4),
  inset 0px 0px 3px rgba(0,0,0,0.9),
  inset 0px 5px 25px rgba(200,200,200, 0.5) !important;
}

100% {
  background:#EEE !important;
  border:solid 1px #7C8E9D !important;
  border-bottom-color:#9CCEDD !important;
  border-top-color:#5B7E9F !important;
  box-shadow:
