css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pixiv.net") {

body {
background-color:#2e2e2e !important;
color:#ffffff !important;
}

#global-header{
background: #1d1d1d !important;
border-bottom:2px #000000 solid !important;
width:auto !important;
padding:0px !important;
position:relative !important;
top:-28px !important;
}

#global-header .wrapper{
width:100% !important;
}

#page-setting-user, .settingAccessArea, .one_column_body{
background: #1d1d1d !important;
border:1px #000000 solid !important;
}


.one_column_top .setting_on, .settingProfile{
color:#000 !important;
background:rgba(255,255,255,.4) !important;
}

.one_column_top{
background: #1d1d1d !important;
border:1px #000000 solid !important;
}

#page-setting-user 
.one_column_body, 
.one_column_body h2, 
.one_column_body .help_title_question{
background:rgba(255,255,255,.4) !important;
border:1px #000000 solid !important;
}

#contents .ui-layout-west div:first-of-type{
background: #1d1d1d !important;
border:1px #000000 solid !important;
}

.site-menu{
position:fixed;
left:0px !important;
top:50px !important;
font-size: 12pt !important;
}

.home, .upload, #global-header .bookmark, .stacc{
position:relative !important;
left:-23px !important;
background:none !important;
margin-left:auto !important;
}

.category-menu, .count{
background:rgba(255,255,255,.4) !important;
}

.ui-search .container{
border:1px #000000 solid !important;
}

.ui-search .submit{
background:url(http://i.imgur.com/OjkXB.gif) !important;
border:1px #000000 solid !important;
}

.ui-search .submit:hover{
background:url(http://i.imgur.com/eKpwp.gif) !important;
}

h1, h2, h3, h4{
color:#ffffff !important;
}

a, a:link, a:visited, a:active{
color:#dddddd !important;
}

a:hover{
color:#ffffff !important;
text-decoration:none !important;
} 

#wrapper{
width:100% !important;
background:none !important;
margin-left:2px !important;
margin-right:2px !important;
}

.pager{
position:fixed !important;
bottom:-20px !important;
left: 35% !important;
background:#2e2e2e !important;
border:3px #000000 solid !important;
height: 50px !important;
-moz-border-radius: 10px 10px;
visibility:/*[[pgvisibility]]*/ !important;
}

.user-ad-container, 
.order, 
.bookmark_link, 
.images h1, 
.user,
#search-result div:not(.images),
.two_columnBottom,
#global-header h1,
.message,
.popboard,
.onecolumnBottom
{
display:none !important;
}

.extaraNavi{
border:0px !important;
position:relative;
top:-5px !important;
margin-left:27% !important;
margin-right: auto !important;
}

footer,
#global-footer{
visibility:hidden !important;
}

#search-result{
padding-top:8px !important;
z-index:-10 !important;
border:0px !important;
}
.extaraNavi .extra_on, .extaraNaviAlso{
background:rgba(255,255,255,.4) !important;
border:1px #000000 solid !important;
}

.upload_02, .upload_03{
background:rgba(255,255,255,.4) !important;
color:#000 !important;
-moz-border-radius: 5px !important;
}

.btn_type08{
background: -moz-linear-gradient(top, rgba(111,111,111,1), #2e2e2e);
border:1px #000000 solid !important;
-moz-border-radius: 5px !important;
}

.image{
width:160px !important;
margin-left: -8px !important;
margin-bottom: -65px !important;
}

.images img{
background:#000000 !important;
border:1px #000000 solid !important;
}

.images img:hover{
background:#000000 !important;
border:2px #888888 solid !important;
}


.related{
height:15px !important;
padding:4px 0 !important;
background:rgba(255,255,255,.4);
}

.column-main{
background:#2e2e2e !important;
border:0px #000000 solid !important;
border-top:1px #000000 solid !important;
width:100% !important;
}

.column-main header{
background: #1d1d1d !important;
height:99px !important;
border-top:1px #000000 solid !important;
border-bottom:1px #000000 solid !important;
}

.context{
background:rgba(255,255,255,.4) !important;
width:100% !important;
position:relative !important;
margin-left:auto !important;
margin-right:auto !important;
color:#aaaaaa !important;
}

.context a{
color:#ffffff !important;
}

.context a:hover{
color:#000000 !important;
}

.related dt{
color:#aaaaaa !important;
}

.tab{
position:absolute !important;
background:none !important;
top:34px !important;
right:100px !important;
height:0px !important;
width:350px !important;
border-bottom:0px !important;
}

.tab .current{
background: -moz-linear-gradient(top, rgba(111,111,111,1), #2e2e2e);
border:1px #000000 solid !important;
-moz-border-radius: 5px !important;
}

.stacc_status{
border-top:1px #000000 solid !important;
margin:5px 0 !important;
}

.stacc_east_area, .stacc_east_area_inner{
background: #2e2e2e !important;
border:1px #000000 solid !important;
}

.stacc_speak_form_button input{
background: -moz-linear-gradient(top, rgba(111,111,111,1), #2e2e2e) !important;
color:#fff !important;
}


.stacc_right_area,
#following-container .h1
{
background:#1a1a1a !important;
}

.stacc_right_menu_active{
background: #2e2e2e !important;
}

.area_new, #following-container, #user-recommend-container{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}


}
@-moz-document url("http://www.pixiv.net/"), url-prefix("http://www.pixiv.net/mypage"){

#wrapper{
width:970px !important;
background:none !important;
margin:10px auto 0px auto !important;
}

.ui-layout-east{
width:auto !important;
}

.profile_area{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.two_column_body{
width:auto !important;
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.two_column_top{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.area_new, #following-container, #user-recommend-container{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.contents-main .rounded, .contents-main #item-container, #column-misc .item{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.tagCloud a:hover{
background:rgba(255,255,255,0.3) !important;
color:#000000 !important;
text-decoration: none !important;
}

.area_title, 
#following-container .h1, 
#following-container .count, 
#user-recommend-container .h1,
#column-misc h1,
.baseTitle,
.item h1,
.NewsTop h1,
.info a:first-of-type
{
background:#1a1a1a !important;
}

.stacc_left_menu_active{
background:#2c2c2c !important;
}

.info a:first-of-type:hover{
background:#d2d2d2 !important;
color:#000000 !important;
}

.project_pixiv{
background:#1a1a1a !important;
border:2pt #000000 solid !important;
}

.rank-detail, .rank:nth-of-type(1n+2){
background:rgba(255,255,255,0.3) !important;
border-top:1px #000000 solid !important;
}


}

@-moz-document url-prefix("http://www.pixiv.net/member_illust"){
#wrapper{
width:970px !important;
background:none !important;
margin:10px auto 0px auto !important;
}
.ui-layout-east{
width:auto !important;
}

.profile_area{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.two_column_body{
width:auto !important;
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.two_column_top{
background:rgba(111,111,111,0.8) !important;
border: 2pt #000000 solid !important;
width:auto !important;
}

.area_new, #following-container, #user-recommend-container{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}

.contents-main .rounded, .contents-main #item-container, #column-misc .item{
background:#2c2c2c !important;
border: 2pt #000000 solid !important;
}
.worksShare{
background:#1a1a1a !important;
border: 2pt #000000 solid !important;
}

.works_tag{
background:rgba(255,255,255,0.3) !important;
margin-top:7px !important;
margin-bottom:3px !important;
}
.works_display{
margin:30px !important;
}

.area_title, 
#following-container .h1, 
#following-container .count, 
#user-recommend-container .h1,
#column-misc h1,
.baseTitle,
.item h1,
.NewsTop h1,
.info a:first-of-type
{
background:#1a1a1a !important;
}

.two_column_top3,
.two_column_body .extaraNaviAlso{
background: #1d1d1d !important;
border:1px #000000 solid !important;
width:auto !important;
}

.action .bookmark a,
.extra_on
{
background: -moz-linear-gradient(top, rgba(111,111,111,1), #2e2e2e) !important;
color: #ffffff !important;
border:1px #000000 solid !important;
}

.action .bookmark a:hover
{
background: -moz-linear-gradient(top, #2e2e2e, rgba(111,111,111,1)) !important;
color: #ffffff !important;
border:1px #000000 solid !important;
}

.two_column_body #f{
margin-top: 20px !important;
background:none !important;
color:#000000 !important;
}

.msgbox_bottom{
background:#1a1a1a !important;
border:1px #000000 solid !important;
}

.worksOption input:last-of-type{
background: -moz-linear-gradient(top, rgba(111,111,111,1), #2e2e2e) !important;
color: #ffffff !important;
}

.display_works li{
background:none !important;
border:0px #000000 solid !important;
margin:5px  !important;
}

.display_works a{
color:#2e2e2e !important;
}
}
