css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wunderground.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, #footer

{
color: white !important;
background: black !important;
}

select, button, #WunderMap .br5, div.brBot5, div.brTop5, div.headerOff, div#searchFields, #footer #goodies #adsales, div.featureGroup, div#goodies, #featureMenu .featureGroup .groupTitle, .headerOver, div.gmnoprint div div, div.alertBox, div#conds_stationselect, div#conds_dashboard, div#hourZZ, #conds_tabs, .titleSubtleBG, div#conds_details_cur_box, div#fct_titlebar, div#fct_days div, div.fctZoneHeader, div.cmSource, div.cmItem, div.cmTabs, div#airports_contain, div#infoPane

{
background: black !important;
}

span, a, #footer #signoff, .contentMod .cmData, div.h252, #photostrip .photoData

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
