css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.knuddels.de")

{

/* Links */
a {color: #2E3C9A !important; text-decoration:none !important}
a:hover {color: #6079DE !important;}
.bbutton a {color: #F9F1FD !important}
.bbutton .btmi {color: #F9F1FD !important}
#knavi .navi ul li .menua {color: #2E3C9A !important;}
#knavi .navi ul li .menua:hover {color: #6079DE !important;}
#kft .ftoutside .content a {color: #888 !important}
#kft .ftoutside .content a:hover {color: #666 !important}

/* Uservorschau */
#online_user .userimage{border:1px solid #000 !important;
padding: 0px !important;}
#online_user{background-image: none !important;
background: transparent !important;
top:-49px!important;}

 /* Hintergrund */
html, body {background: 
-moz-linear-gradient(top,  #D8E0F7,  #FFF) !important; 
background: -webkit-gradient(linear, left top, left bottom,
from(#D8E0F7), to(#FFF)) !important}

 /* Divfixes */
#kft .ftbox .l {width:29px;background: transparent  !important;}
#kft .ftbox .r {width:29px;background: transparent !important;
position:absolute; right:0;}
#kft .ftbox div.content{background:transparent !important;}

/*Navigation*/
#knavi {opacity: 0.8; -moz-transition:opacity .3s ease-in;}
#knavi a {background:#CCD2F5 !important; border-color:#D9E0F7!important;}
#knavi:hover {opacity:1.0 !important;}
#knavi .navi ul li .menua .border, #knavi .navi ul li .menub .border
{display:none !important;}
#knavi .navi li.first div.border {display:none !important;}
#knavi .border-top {background-color: #2E3C9A !important;
border-bottom: transparent !important}
#knavi .border-top .border {background: transparent !important}
#knavi .navi ul li a:hover,
#knavi .navi ul li.selected .menua {background: #D9E0F7 !important}

/* Weitere Anpassungen */ 
#bd3 {background-color: #ABB3EF !important;}
#khd div.content {background: transparent !important;}
#khd .r {background: transparent !important;}
#khd .l {background: transparent !important;}

/* Schriften */
h3 {color: #222 !important; text-shadow: 0 1px 0 white !important}
h2 {color: #222 !important; text-shadow: 0 1px 0 white !important}
h1 {color: #17226E !important; text-shadow: 0 1px 0 white !important}
.justified {color: #000 !important}
.date {color: #666 !important}
.author {color: #666 !important}
.categories .category_name {color: #2E3C9A !important;}
.categories .category_name:hover {color: #6079DE !important;}
.categories .category_usercount {color: #666 !important;}
.categories .category_usercount:hover {color: #000 !important;}
.mi {color: #000 !important}
.index .pwforgotten {color: #000 !important}
#form-reg .label, #form-reg .formlabel {color: #000 !important}
#form-reg .note, .note {color: #000 !important}
#kmain .blogentry .author .name {color: #2E3554 !important;}
#khd ul.hdm a {color:#2E3C9A !important;
border-bottom: 3px solid #6079DE !important}
#khd ul.hdm a:hover {color: #6079DE !important;
border-bottom: 3px solid #2E3C9A !important}

}
