css:
@-moz-document domain("mail.google.com") {
/************************* adding text for mail.google.com *************************/

.T-I {
    line-height: 22px !important;
    padding-top: 3px !important;
    height: 23px !important;
}

.asa {
    width: 65px !important;
    text-align: left !important;
    color: #000 !important;
}

.ash {
    width: 75px !important;
    text-align: left !important;
    color: #000 !important;
}

.T-I.J-J5-Ji.apF.T-I-Js-IF.T-I-ax7.L3 .asa:after { /* why not "before"? */
    content: " No/Split";
}

.Cr {
    padding-right: 15px;
}

.ar6,.ar8,.asl,.ar9,.ase,.asb,.asf,.aos,.hB {
    text-indent: 22px !important;
    width: 20px !important;
}

.aos {
    margin-right: 48px;
}

.ar6:before {content:"Back";}
.ar8:before {content:"Archive";}
.asl:before {content:"Spam";}
.ar9:before {content:"Delete";}
.ase:before {content:"Move to";}
.asb:before {content:"Labels";}
.asf:before {content:"Reload";}
.aos:before {content:"Settings";}
.hB:before {content:"Reply";}
.Ykrj7b {padding-left: 18px;}

div[title="Back to Inbox"] div .ar6:before,
div[data-tooltip="Back to Inbox"] div .ar6:before {
    content:"Inbox";
}



/************************* adding colours *************************/

div[aria-label="Delete"], 
div[aria-label="Löschen"],
div[aria-label="Smazat"],
div[aria-label="Odstránit"] {
    background-image: -moz-linear-gradient(center top , rgba(255, 128, 128, 0.6), rgba(220, 128, 128, 0.6)) !important;
}


.ar6,              
.ar8,              
.asl,              
.ar9,              
.ase,              
.asb,              
.asf,              
.adj,              
.adk,              
.aos,              
.hB,               
.Ykrj7b,
           
.gx,               
.gq,               
.g1,               
.gZ, .eI,          
.o3.T-I-J3,        
.ank, .aoc,        
.anp, .anl,        
.ana,              
.yE, .gW,          
.anh .ane,         
.end-of-list {
    background-image: url("data:image/png;base64,") !important;
    background-attachment: scroll !important;
    background-repeat: no-repeat !important;
    background-color: transparent !important;
}


.ar6 {   background-position: 0 -42px !important; }       
.ar8 {   background-position: -84px -21px !important; }   
.asl {   background-position: -42px -42px !important; }   
.ar9 {   background-position: -63px -42px !important; }   
.ase {   background-position: -84px -42px !important; }   
.asb {   background-position: -21px -42px !important; }   
.asf {   background-position: -63px -21px !important; }   
.adj {   background-position: -21px -21px !important; }   
.adk {   background-position: -42px -21px !important; }   
.aos {   background-position: 0 -21px !important; }       
.hB {    background-position: 0 -63px !important; } 
.mK {    background-position: -21px -63px !important; }       /* <= Reply to all */
.Ykrj7b { background-position: -87px -89px !important; }        


.gx {        background-position: 0 -84px !important; }      
.gq {        background-position: -21px -84px !important; }  
.g1 {        background-position: -84px -63px !important; }  
.gZ, .eI {   background-position: -63px -63px !important; }  
.o3.T-I-J3 { background-position: -21px 0px !important; }    
.ank, .aoc { background-position: -63px -84px !important; }  
.anp, .anl { background-position: -42px 0px !important; } 
.anl { background-position: -42px -84px !important; }        /* more actions */   
.ana {       background-position: -63px 0 !important; }      
.yE, .gW {   background-position: -2px 0 !important; }       
.anh .ane {  background-position: -84px 0px !important; }
}
