css:
/*

		MyAnimeList.net Miku@Luka vocaloids theme

		Miku Hatsune and Luka Megurine vocaloids theme for MyAnimeList
		and native theme for anime/manga lists (CSS3-features).
		AdBlock included.
		http://userstyles.org/styles/59136/myanimelist-net-miku-luka-vocaloids-theme

		License: CC-BY-SA (http://creativecommons.org/licenses/by-sa/3.0/)
		Report about bugs and feature requests to xmpp:fastpoke@fastpoke.org

	ToDo:
		* new submit button;
		* add 'compatibility' colors;
		* native theme for anime/manga lists;


	Changelog:
	['+' add / '-' del / '*' fix]
	
		v. 0.9.7 [19.01.2012] (RC3)
			+ add comments;
			* fix navi bar;
			* fix navi icons (add new);
			* some minor fixes;
			* full support for Chrome and Opera;

		v. 0.9.6 [14.01.2012] (RC2)
			+ new :hover for buttons
			* fix for forum style;
			* fix box-shadow on navi-bar;
			* replace navigation icons (16x16 px)
			* fix headers position;
			* fix error-message position;
			* other small fixes;

		v. 0.9.4 [11.01.2012] (RC1)
			+ new rss icon;
			+ navigation icons;
			+ correctly logo;
			* fixed buttons;
			* fixed top navi bar;
			* fixed border-radius;
			* other minor fixes;
			
		v. 0.9 [10.01.2012] (pre-release)
			+ new logo;
			+ fixed background;
			+ native color theme;
			+ new font;
	    	* fixed serach bar;
	    	* fixed buttons;
	    	* other minor fixes;
	    	- remove ads;

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url(http://myanimelist.net/),
    url-prefix(http://myanimelist.net/login),
    url-prefix("http://myanimelist.net/addtolist"),
    url-prefix("http://myanimelist.net/panel.php"),
    url-prefix("http://myanimelist.net/profile"),
    url-prefix("http://myanimelist.net//profile"),
    url-prefix("http://myanimelist.net/editprofile"),
    url-prefix("http://myanimelist.net/myfriends"),
    url-prefix("http://myanimelist.net/clubs"), 
    url-prefix("http://myanimelist.net/mymessages"),
    url-prefix("http://myanimelist.net/myreviews"),
    url-prefix("http://myanimelist.net/myrecommendations"),
    url-prefix("http://myanimelist.net/blog"),
    url-prefix("http://myanimelist.net/myblog"),
    url-prefix("http://myanimelist.net/anime.php"),
    url-prefix("http://myanimelist.net/manga.php"),
    url-prefix("http://myanimelist.net/people"),
    url-prefix("http://myanimelist.net/character"),
    url-prefix("http://myanimelist.net/fansub-groups"),
    url-prefix("http://myanimelist.net/topanime"),
    url-prefix("http://myanimelist.net/topmanga"),
    url-prefix("http://myanimelist.net/favorites"),
    url-prefix("http://myanimelist.net/reviews"),
    url-prefix("http://myanimelist.net/news"),
    url-prefix("http://myanimelist.net/forum/"), 
    url-prefix("http://myanimelist.net/users"),
    url-prefix("http://myanimelist.net/manga/"),
    url-prefix("http://myanimelist.net/anime/"),
    url-prefix("http://myanimelist.net/modules"),
    url-prefix("http://myanimelist.net/showreview"),
    url-prefix("http://myanimelist.net/about"),
    url-prefix("http://myanimelist.net/donations"),
    url-prefix("http://myanimelist.net/tou.php"),
    url-prefix("http://myanimelist.net/privacy"),
    url-prefix(http://myanimelist.net/dbchanges),
    url-prefix(http://myanimelist.net/comments),
    url-prefix(http://myanimelist.net/friends),
    url-prefix(http://myanimelist.net/showclubs),
    url-prefix(http://myanimelist.net/editclub),
    url-prefix(http://myanimelist.net/topusers),
    url-prefix(http://myanimelist.net/shared),
    url-prefix(http://myanimelist.net/history),
    url-prefix(http://myanimelist.net/recommendations),
    url-prefix(http://myanimelist.net/info.php),
    url-prefix(http://myanimelist.net/comtocom.php),
    url-prefix(http://myanimelist.net/register),
    url-prefix(http://myanimelist.net/admin),
    url-prefix(http://myanimelist.net/password),
    url-prefix("http://myanimelist.net/editlist.php"),
    url-prefix("http://myanimelist.net/ajaxtb.php"),
    url-prefix("http://myanimelist.net/import.php")
{
* {border: 0px solid !important; font-family: "Trebuchet MS", sans-serif, sans !important;font-weight: normal !important;}
/* background and logo */
html, body {background: #ffffff url("http://i.imgur.com/zcUr0.png") fixed no-repeat bottom center !important;}
#headerSmall {background: url("http://i.imgur.com/XdVrb.png") scroll no-repeat !important;padding-top: 100px !important;}

/* top menu */
#menu {
    background-color:rgba(158,207,208,0.90) !important;
    border: 0px transparent solid !important;
            border-radius: 2px 2px 0px 0px !important;
       -moz-border-radius: 2px 2px 0px 0px !important;
    -webkit-border-radius: 2px 2px 0px 0px !important;
         -o-border-radius: 2px 2px 0px 0px !important;
}
#menu_left {
    background-color: transparent !important;
            border-radius: 2px 2px 0px 0px;
       -moz-border-radius: 2px 2px 0px 0px;
    -webkit-border-radius: 2px 2px 0px 0px;
         -o-border-radius: 2px 2px 0px 0px;
}
#menu_right {background-color: transparent !important;}
#nav li {background-color: transparent !important;
            border-radius: 2px 2px 0px 0px;
       -moz-border-radius: 2px 2px 0px 0px;
    -webkit-border-radius: 2px 2px 0px 0px;
         -o-border-radius: 2px 2px 0px 0px;
}
#nav li a {background: transparent !important;color: #000000 !important;padding-left: 10px !important;}
#nav li a:hover {
    color: #0000ff !important;
    text-decoration: none !important;
       -moz-transition: 0.5s;
    -webkit-transition: 0.5s;
         -o-transition: 0.5s;
            border-radius: 2px 2px 0px 0px;
       -moz-border-radius: 2px 2px 0px 0px;
    -webkit-border-radius: 2px 2px 0px 0px;
         -o-border-radius: 2px 2px 0px 0px;
               box-shadow:  0px -10px 10px rgba(158,207,208,1) inset,
                            4px   0px 10px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset,
                           -4px   0px  5px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset;
          -moz-box-shadow:  0px -10px 10px rgba(158,207,208,1) inset,
                            4px   0px 10px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset,
                           -4px   0px  5px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset;
       -webkit-box-shadow:  0px -10px 10px rgba(158,207,208,1) inset,
                            4px   0px 10px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset,
                           -4px   0px  5px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset;
            -o-box-shadow:  0px -10px 10px rgba(158,207,208,1) inset,
                            4px   0px 10px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset,
                           -4px   0px  5px rgba(0,0,0,0.1) inset,
                            0px   4px  5px rgba(0,0,0,0.1) inset;
}
#nav ul li {border-bottom: transparent 0px !important;}
#nav ul a {padding: 4px 4px 2px 1px !important;}
#nav ul a:hover {
    background-color:rgba(158,207,208,0.8) !important;
    color: #0000ff !important;
    text-decoration: none !important;
       -moz-transition: 0.5s;
    -webkit-transition: 0.5s;
         -o-transition: 0.5s;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px -5px 15px rgba(158,207,208,1) inset,
                           3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 3px 2px rgba(0,0,0,0.2) inset,
                           -3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 5px 5px rgba(0,0,0,0.2) inset;
          -moz-box-shadow: 0px -5px 15px rgba(158,207,208,1) inset,
                           3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 3px 2px rgba(0,0,0,0.2) inset,
                           -3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 5px 5px rgba(0,0,0,0.2) inset;
       -webkit-box-shadow: 0px -5px 15px rgba(158,207,208,1) inset,
                           3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 3px 2px rgba(0,0,0,0.2) inset,
                           -3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 5px 5px rgba(0,0,0,0.2) inset;
            -o-box-shadow: 0px -5px 15px rgba(158,207,208,1) inset,
                           3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 3px 2px rgba(0,0,0,0.2) inset,
                           -3px 0px 2px rgba(0,0,0,0.2) inset,
                           0px 5px 5px rgba(0,0,0,0.2) inset;
}
#nav li a span.count {color: #aaa !important;}
#nav li .new a {color: #aaa !important;}
#nav li .new a:hover {color: #d7b090 !important;}

ul[style*="display: block;"], .wider, .wide {
    background-color:rgba(158,207,208,0.8) !important;
    border: 0px solid !important;
    margin-top: 0px !important;
       -moz-border-radius: 0px 0px 4px 4px;
            border-radius: 0px 0px 4px 4px;
    -webkit-border-radius: 0px 0px 4px 4px;
         -o-border-radius: 0px 0px 4px 4px;
}
#searchBar {padding: 7px !important;}


/* bottom menu */
#menu {
    border-bottom: #bbbbbb 1px solid !important;
            border-radius: 0px 2px 0px 2px;
       -moz-border-radius: 0px 2px 0px 2px;
    -webkit-border-radius: 0px 2px 0px 2px;
         -o-border-radius: 0px 2px 0px 2px;
}

h1 {
    border-left: rgba(158,207,208,0.90) 1px solid !important;
    border-right: rgba(158,207,208,0.90) 1px solid !important;
    border-bottom: rgba(158,207,208,0.90) 1px solid !important;
    background: #ffffff !important;
}

.horiznav_active, .horiznav_active a:hover {
    color: #000000 !important;
    border: 0px solid !important;
    background-color: #f7f7f7 !important;
    padding: 5px 12px 4px 11px !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 0px 5px rgba(0,0,0,0.3), 0px -10px 0px rgba(0,0,0,0.05) inset !important;
          -moz-box-shadow: 0px 0px 5px rgba(0,0,0,0.3), 0px -10px 0px rgba(0,0,0,0.05) inset !important;
       -webkit-box-shadow: 0px 0px 5px rgba(0,0,0,0.3), 0px -10px 0px rgba(0,0,0,0.05) inset !important;
            -o-box-shadow: 0px 0px 5px rgba(0,0,0,0.3), 0px -10px 0px rgba(0,0,0,0.05) inset !important;
}
#horiznav_nav ul li a,
#horiznav_nav ul li a[href*="?sd="],
#horiznav_nav ul li a[href*="?o="] {
    padding: 5px !important;
    border: 0px solid !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
}
#horiznav_nav ul li a:hover,
#horiznav_nav ul li a[href*="?sd="]:hover,
#horiznav_nav ul li a[href*="?o="]:hover {
    color: #000000 !important;
    background-color: rgba(158,207,208,0.20) !important;
    padding: 5px !important;
    border: 0px solid !important;
       -moz-transition: 0.5s !important;
    -webkit-transition: 0.5s !important;
         -o-transition: 0.5s !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 10px 2px rgba(0,0,0,0.07) inset, 0px -1px 1px rgba(0,0,0,0.1) inset;
          -moz-box-shadow: 0px 10px 2px rgba(0,0,0,0.07) inset, 0px -1px 1px rgba(0,0,0,0.1) inset;
       -webkit-box-shadow: 0px 10px 2px rgba(0,0,0,0.07) inset, 0px -1px 1px rgba(0,0,0,0.1) inset;
            -o-box-shadow: 0px 10px 2px rgba(0,0,0,0.07) inset, 0px -1px 1px rgba(0,0,0,0.1) inset;
}
div#horiznav_nav {border: 0px solid !important;}

/* main content */
#header {background: url("http://i.imgur.com/XdVrb.png") scroll no-repeat !important;}
#content {
    border-left: rgba(158,207,208,0.90) 1px solid !important;
    border-right: rgba(158,207,208,0.90) 1px solid !important;
    border-bottom: rgba(158,207,208,0.90) 1px solid !important;
}


/* left menu */

.profile_leftcell, #profileRows a, .profile_leftcell .borderClass {border: 0px solid !important;}
.profile_leftcell div[style="text-align: center;"] {padding-top: 10px !important;}
.profile_leftcell div[style="text-align: center;"] img,
.borderClass div[style="text-align: center;"] img,
.profile_leftcell .borderClass img{
    border: 0px solid !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
          -moz-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
       -webkit-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
            -o-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);

}
#profileRows {border: 0px solid !important;border-radius: 2px;margin-top: 15px !important;padding: 1px !important;}
#profileRows a:hover {
    background-color: rgba(158,207,208,0.20) !important;
    color: #000000 !important;
       -moz-transition: 0.5s;
    -webkit-transition: 0.5s;
         -o-transition: 0.5s;
    text-decoration: none !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
          -moz-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
       -webkit-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
            -o-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
}


/* navi icons */

#profileRows a[href^="http://myanimelist.net/animelist/"]:hover::after,
#profileRows a[href^="http://myanimelist.net/mangalist/"]:hover::after
    {content: url("http://i.imgur.com/hIDvQ.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="http://myanimelist.net/editprofile.php"]:hover::after
    {content: url("http://i.imgur.com/iWr64.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="http://myanimelist.net/mymessages.php"]:hover::after
    {content: url("http://i.imgur.com/xh3Fe.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="http://myanimelist.net/myfriends.php?go=add"]:hover::after
    {content: url("http://i.imgur.com/7LUn2.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="http://myanimelist.net/myfriends.php?go=remove"]:hover::after
    {content: url("http://i.imgur.com/epeLD.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="#addtolistanchor"]:hover::after
    {content: url("http://i.imgur.com/7Sisd.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="javascript:void(0)"]:hover::after
    {content: url("http://i.imgur.com/yk2xH.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="javascript:doedit"]:hover::after
    {content: url("http://i.imgur.com/oYflF.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href^="http://myanimelist.net/panel.php?go=characters&do=edit"]:hover::after
    {content: url("http://i.imgur.com/oYflF.png"); float: right; padding: 0px 2px 4px 0px;}
#profileRows a[href$="&t=remove"]:hover::after
    {content: url("http://i.imgur.com/utIK9.png"); float: right; padding: 0px 2px 4px 0px;}


.profile_leftcell .normal_header {
    margin-top: 15px !important;
    padding: 2px 2px 2px 8px !important;
    background: rgba(158,207,208,0.20) !important;
    border: 0px solid !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
          -moz-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
       -webkit-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
            -o-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
}

.floatRightHeader a {padding: 0px 5px 0px 5px !important;}
.floatRightHeader a:hover {
    color: #000000 !important;
    background-color: #f7f7f7 !important;
    padding: 0px 5px 0px 5px !important;
       -moz-transition: 0.5s !important;
    -webkit-transition: 0.5s !important;
         -o-transition: 0.5s !important;
    text-decoration: none !important;
            border-radius: 2px;
       -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
               box-shadow: 0px 1px 2px rgba(0,0,0,0.3) inset !important;
          -moz-box-shadow: 0px 1px 2px rgba(0,0,0,0.3) inset !important;
       -webkit-box-shadow: 0px 1px 2px rgba(0,0,0,0.3) inset !important;
            -o-box-shadow: 0px 1px 2px rgba(0,0,0,0.3) inset !important;
}


/* buttons */

.button_add, .button_edit {
    float: right;
    border-left: 0px solid transparent !important;
    border-bottom: 0px solid transparent !important;
    padding: 0px 2px 0px 2px !important; 
          -moz-transition: 0.5s !important;
       -webkit-transition: 0.5s !important;
            -o-transition: 0.5s !important;
            border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
       -moz-border-radius: 2px;
               box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
          -moz-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
       -webkit-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
            -o-box-shadow: 0px 0px 5px rgba(0,0,0,0.3);
}
.button_add:hover, .button_edit:hover {
    color: #000000 !important;
       -moz-transition: 0.5s !important;
    -webkit-transition: 0.5s !important;
         -o-transition: 0.5s !important;
    text-decoration: none !important;
    padding: 0px 2px 0px 2px !important;
    background: #f7f7f7 !important;
    border: 0px solid !important;
            border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
       -moz-border-radius: 2px;

    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.3) inset !important;
}
.normal_header, h2 {
    padding: 2px 2px 3px 8px !important;
     border: 0px solid !important;
            border-radius: 2px;
    -webkit-border-radius: 2px;
         -o-border-radius: 2px;
       -moz-border-radius: 2px;
    margin-top: 15px !important;
    background: rgba(158,207,208,0.20) !important;
               box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
       -webkit-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
            -o-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
          -moz-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
}
.inputButton {
    color: #000000 !important;
    background-color:rgba(255,255,255,0.21) !important;
            border-radius: 4px;
    -webkit-border-radius: 4px;
         -o-border-radius: 4px;
       -moz-border-radius: 4px;
    padding: 2px 5px 2px 5px !important;
               box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.3);
       -webkit-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.3);
            -o-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.3);
          -moz-box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.3);
}
.inputButton:hover {
    background-color:rgba(0,0,0,0.1) !important;
               box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.6) inset !important;
       -webkit-box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.6) inset !important;
            -o-box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.6) inset !important;
          -moz-box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.6) inset !important;
}

div[style="width: 690px; overflow: hidden; margin: 0 auto;"] .normal_header {margin-left: 3px !important;margin-right: 3px !important;}
div[style="width: 630px; padding-right: 7px; overflow: hidden;"] .normal_header{margin-left: 3px !important;margin-right: 3px !important;margin-bottom: 3px !important;}
div[style="margin: 2px 0;"]::before {content: url("http://i.imgur.com/yxAkS.png");}
.forum_category {color: #000000 !important; background: rgba(158,207,208,0.90) !important;}
#forumTopics td.normal_header {
            border-radius: 0px !important;
       -moz-border-radius: 0px !important;
    -webkit-border-radius: 0px !important;
         -o-border-radius: 0px !important;
            box-shadow: 0px 0px 0px !important;
       -moz-box-shadow: 0px 0px 0px !important;
    -webkit-box-shadow: 0px 0px 0px !important;
         -o-box-shadow: 0px 0px 0px !important;
}

/* progress bar colors */

div[style*="background-color:#eaf0fa"]	{background-color:rgba(158,207,208,0.10) !important;}
div[style*="background-color:#dbe5f6"]	{background-color:rgba(158,207,208,0.20) !important;}
div[style*="background-color:#cddbf2"]	{background-color:rgba(158,207,208,0.25) !important;}
