css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.hcg-beilstein.hn.schule-bw.de/moodle/") {
    body, font {
        font-family: Verdana,sans-serif !important;
    }
    
    #content {
        font-size: 12px !important;
        background: white !important;
        padding: 0 !important;
    }
    
    #middle-column {
        padding: 0 10px 10px !important;
    }
    
    a {
        text-decoration: none !important;
        background: transparent !important;
    }
    
    a:hover {
        text-decoration: underline !important;
    }
    
    #page {
        padding: 0 !important;
        margin: 0 !important;
    }
    
    .headermain,
    .breadcrumb {
        margin-left: 10px !important;
    }
    
    font {
        background-color: transparent !important;
    }
    
    p:first-child,
    h2:first-child,
    h3:first-child,
    a:first-child + h3 {
        margin-top: 0 !important;
    }
    
    p:last-child,
    h2:last-child,
    h3:last-child {
        margin-bottom: 0 !important;
    }
    
    #layout-table {
        margin-top: 10px !important;
    }
    
    .headermenu {
        padding: 5px;
    }
    
    /* Boxes */
    .sideblock {
        overflow: hidden !important;
    }
    
    div.sideblock div.header,
    div.sideblock div.content {
        border: none !important;
    }
    
    div.sideblock div.header {
        margin-right: -10px !important;
        background: #88DD55 !important;
        padding: 3px !important;
        color: black !important;
    }
    
    div.sideblock div.header {
        line-height: 1em !important;
    }
    
    div.sideblock div.content {
        background-color: white !important;
    }
    
    #left-column .sideblock .header {
        padding-left: 10px !important;
    }
    
    #left-column .sideblock .content {
        padding-left: 10px !important;
    }
    
    #right-column .sideblock .header {
        padding-right: 10px !important;
    }
    
    #right-column .sideblock .content {
        padding-right: 10px !important;
    }
    
    #left-column {
        padding: 0 !important;
        border-right: 1px solid #88DD55;
    }
    
    #right-column {
        padding: 0 !important;
        border-left: 1px solid #88DD55;
    }
    
    .header .hide-show-image {
        display: none !important;
    }
    
    .header:hover .hide-show-image {
        display: block !important;
    }
    
    .sideblock ul {
        padding-left: 18px !important;
    }
    
    .sideblock ul.list {
        padding-left: 0 !important;
    }
    
    /* Main column */
    .headingblock, h2.headingblock {
        padding: 0 !important;
        margin-bottom: 5px !important;
    }
    
    #maincontent {
        height: 0 !important;
    }
    
    /* Topics View */
    
    .topics > tbody > .section:nth-child(4n-1),
    .topics > tbody > .section:nth-child(4n+1) > td {
        background: none !important;
    }
    
    .topics > tbody > .section:nth-child(4n-1),
    .topics > tbody > .section:nth-child(4n-1) > td {
        background: #EEFFDD !important;
    }
    
    .section .right.side {
        visibility: hidden !important;
    }
    
    .section:hover .right.side {
        visibility: visible !important;
    }
    
    /* Courses Overview */
    .coursebox {
        padding: 10px !important;
        -moz-box-sizing: border-box;
        
        background: #EEFFDD !important;
        border: none !important;
    }
}
