@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitly.com") {
body {
color: rgb(255,255,255)!important;
 background:#0D2E57 url('data:image/gif;base64,')
repeat fixed!important;
}
body.notouch,HTML.wf-adelle-n4-active.wf-adelle-i4-active.wf-adelle-n7-active.wf-adelle-i7-active.wf-active{background-color:transparent!important}
*{background-color:transparent!important;
}
#ext_container{background-color: rgba(241, 241, 241,0.1)!important;
}
.header_bottom{background-color: rgba(241, 241, 241,0.5)!important;
}
.notice_box network_items, .bitmarks{background-color: rgba(241, 241, 241,0.3) !important;
}
#header:before,#header{background-image:none!important;background-color: #000000!important;
}

.bp_addShortLink.public{top: -40px!important; left: -70px !important}
.bp_addShortLink.public fieldset{margin-left: -160px !important}
.magic_ph input.text{margin-left: 130px !important}
.nyanform{background-color:rgba(3,10,34,0.9)!important;}

.fastSearchField,ul.fs_privacyOptions{background-color: grey !important;}

DIV.account_dropdown{background-color: rgba(241, 241, 241,0.7)!important;
color:black!important
}
a.ohai,span.usernabe{color: #000000!important
}
ul.bundle_links{
display: block !important;
background-color: rgba(241, 241, 241,0.5) !important;
border-radius: 17px!important;
box-shadow: 5px 5px 5px #CBCAB3!important;
padding: 15px 0px 5px 15px !important;
}
ul.bundle_links li.link{
border:none !important;
box-shadow: 1px 1px 1px #CBCAB3!important;

}

div.img span{
border:none!important;
border-radius:2px!important;
padding:0px!important;
text-align: right!important;
float:none !important;
}
div.img {padding-left:5px!important
}
div.img span img{border-radius:3px!important;padding:3px!important;
background-color:#ffffff!important;width: 100px!important
}
.fs_resultsBox, .title_desc, .bitmark.nodesc{
background-color: rgba(241, 241, 241,0.2) !important;
}
.bitmark.nodesc{padding:3px!important;
}
.fs_longLinkUrl, .fs_notes, .fs_timeDisplay {color:white !important;
}
#external_container{background-image: none !important; }

h3 {color: #ffffff!important;
}

}
