css:
/*
 * YouTube - dark gray redesign
 *
 * Author      : Valacar, Osvifr
 * Updated     : 13.10.2011
 * Website     : http://www.valacar.com/userstyles/
 * Description : Modifies the colors, logos, and a few other images on YouTube (http://youtube.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url("http://www.youtube.com/"),
url-prefix("http://www.youtube.com/watch"),
url-prefix("http://www.youtube.com/results"),
url-prefix("http://www.youtube.com/show"),
url-prefix("http://www.youtube.com/channels"),
url-prefix("http://www.youtube.com/music"),
url-prefix("http://www.youtube.com/videos"),
url-prefix("http://www.youtube.com/trailers"),
url-prefix("http://www.youtube.com/gaming"),
url-prefix("http://www.youtube.com/contests_main"),
url-prefix("http://www.youtube.com/events_main")
{
	body {
	background:#000!important;
	color:#fff!important;
	}

	/* links */
	a,#chrome-promo #chrome-actionline,.browse-pager .page,.homepage-chrome-link,.watch-action-text,.iyt-edit-link {
	color:#ddd!important;
	}

	/* other links */
	[id^="map-link"] {
	color:#ffc!important;
	}

	/* Default for most borders */
	hr,div,[class],[id],[style*="border"],tr,td,th,h1,h2,h3 {
	border-color:#444!important;
	}

	/* various boxes / announcments
	-------------------------------*/
	.yt-alert {
	background:#222!important;
	}

	.side-announcement-box,#chrome-promo #chrome-link {
	background:#333!important;
	border-color:#444!important;
	}

	#iyt-login-suggest-side-box > div > div {
	background:#333!important;
	-moz-border-radius:3px!important;
	}

	#iyt-login-suggest-side-box [style*="border"] {
	border-color:#444!important;
	-moz-border-radius:7px!important;
	}

	/* green background bar */
	.confirmBox {
	background:#204507 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAMAAAAcCAIAAAArypKmAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAA8SURBVHjaYlRwZWcAAyYGGGBhZGTELYZVlomJCbcYCyPCFIQYpnlILCY4iwGLGCN+k/GLYTEZiyxAgAEA0QoBG4lF/qQAAAAASUVORK5CYII=) repeat-x bottom!important;
	}

	/* yellow background bar */
	#watch-video-response {
	background:#333!important;
	border:1px solid #444!important;
	}

	/* "more popular" box */
	#watch-more-popular * {
	background:#222!important;
	}

	#watch-more-popular img {
	display:none!important;
	}

	#search-tips-upload-restrict {
	background:#060!important;
	color:#fff!important;
	}

	#search-tips-upload-restrict a {
	color:#ff0!important;
	}

	/* search bar
	-------------*/
	#masthead-search {
	border-color:#444!important;
	}

	/*header profile bar extended*/
	#masthead-expanded {
	background:#111!important;
	border:0!important;
	}

	#playlist-bar-lists-content li a div {
	color:#FFF!important;
	}

	#playlist-bar-lists-content li a {
	background-color:#222!important;
	}

	#playlist-bar-bar {
	background-color:#333!important;
	}

	#playlist-bar-info span {
	background-color:#333!important;
	}

	#playlist-bar-info button {
	background-color:#333!important;
	}

	#playlist-bar-tray .yt-uix-button {
	background-color:#222!important;
	}

	#playlist-bar-tray-container .yt-uix-slider-body {
	background-color:#444!important;
	}

	/* front page feed modules
	--------------------------*/
	/* body */
	.feedmodule-anchor,.fyf-container,.module-item-wrapper-inner {
	background:#202020 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAABuCAIAAACVwMWbAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAAAzSURBVHjaYtTQ0GDABpgYcIBRiVGJUYmBlWD5////aJCMSowmhtHQHfX5qM9HagwCBBgA4wITTrkO5H0AAAAASUVORK5CYII=) repeat-x!important;
	}

	/* outer borders */
	.fm2-title-border-box-gray,.feedmodule-border-gray,.fm2-title-border-box-blue,.feedmodule-border-blue {
	border-color:#3c3c3c!important;
	}

	/* titlebar */
	.fm2-title-border-box-gray,.fm2-title-border-box-blue,.fyf-container-main-title,.statModule-title {
	background:#2a2a2a url(data:image/png;base64,) repeat-x!important;
	}

	.feedmodule-modheader {
	background:#000!important;
	}

	.fm2-titleText {
	color:#fff!important;
	}

	/* border below titlebar */
	.feedmodule-data {
	border-top:1px solid #222!important;
	}

	/* inbox module */
	.statModule-item-line {
	background:transparent!important;
	}

	/* option box */
	.opt-banner,.opt-main {
	background:#000!important;
	}

	.opt-close-text {
	color:#ddd!important;
	}

	/* masthead
	-----------*/
	#masthead-container {
	border-color:#333!important;
	}

	/* footer
	---------*/
	#footer,#footer .links {
	border-color:#444!important;
	}

	/* thumbnail pics
	-----------------*/
	.v220WrapperOuter,.v120WrapperOuter,.v90WrapperOuter,.v180WrapperOuter,.v50WrapperOuter,.v220WrapperInner,.v120WrapperInner,.v90WrapperInner,.v180WrapperInner,.v50WrapperInner,[class^="user-thumb-"] {
	border-color:transparent!important;
	background:transparent!important;
	-moz-box-shadow:3px 3px 10px rgba(0,0,0,0.4)!important;
	}

	img {
	border:none!important;
	}

	/* category browser
	-------------------*/
	.browse-categories-side {
	background:#292929!important;
	}

	/* menu bar
	-----------*/
	.browse-tab-modifiers {
	background:#333!important;
	}

	.browse-tab-modifiers .subcategory {
	background:none!important;
	}

	.browse-tab-modifiers .subcategory.selected {
	background:#555!important;
	}

	.subcategory.selected {
	color:#fff!important;
	}

	#ALL-titlebar {
	background:#333!important;
	}

	#user-navbar {
	background:#333!important;
	}

	#user-navbar-sections li {
	background:#333!important;
	}

	#user-navbar-sections li a {
	background:#555!important;
	}

	#user-navbar-sections .current a {
	background:#222!important;
	}

	/* drop down menus
	------------------ */
	.yt-menubutton-menu li a,.yt-menubutton-primary .yt-menubutton-menu li a,.yt-menulink-menu li a,.yt-menulink-primary .yt-menulink-menu li a {
	background:#000!important;
	color:#fff!important;
	}

	.yt-menubutton-menu li a:hover,.yt-menubutton-primary .yt-menubutton-menu li a:hover,.yt-menulink-menu li a:hover,.yt-menulink-primary .yt-menulink-menu li a:hover {
	background:#000!important;
	color:#fff!important;
	}

	.yt-menulink-menu {
	background:#000!important;
	-moz-box-shadow:3px 3px 10px rgba(0,0,0,0.4)!important;
	color:#fff!important;
	}

	.yt-menulink-menu span a {
	background:#000!important;
	color:#aaa!important;
	}

	/* buttons
	-----------*/
	.yt-button span {
	color:#000!important;
	}

	.watch-expander-head {
	background:#222!important;
	color:#fff!important;
	}

	.yt-uix-button {
	background:#333!important;
	color:#fff!important;
	}

	.yt-uix-button-menu-item {
	background:#222!important;
	color:#fff!important;
	border-color:#444!important;
	}

	.yt-uix-button-menu {
	border-color:#444!important;
	}

	/* watch-channel-discoverbox
	---------------------------------*/
	#watch-channel-discoverbox {
	background:#111!important;
	color:#fff!important;
	}

	/* watch-module-body
	---------------------------------*/
	.watch-module-body {
	background:#222!important;
	color:#ccc!important;
	border-color:#222!important;
	}

	#watch-related {
	background:#222!important;
	color:#ccc!important;
	}

	.video-list-item-link .title {
	color:#eee!important;
	}

	.video-list-item-link:hover {
	background:#aaa!important;
	color:#aaa!important;
	}

	#watch-description {
	background:#222!important;
	color:#fff!important;
	}

	#watch-discussion {
	background:#222!important;
	color:#fff!important;
	}

	#watch-comments-form textarea {
	background:#aaa!important;
	color:#000!important;
	}

	#watch-next-list-actions {
	background:#222!important;
	}

	.yt-uix-expander-head {
	background:#333!important;
	color:#000!important;
	}

	.comment-author {
	color:#999!important;
	}

	.current {
	background:#000!important;
	color:#fff!important;
	}

	.current .wrapper {
	background:#000!important;
	color:#fff!important;
	}

	/* spotlight box/ hot trend cloud
	---------------------------------*/
	.main-tabs-spotlight-inner,#movie-promo,.hot-trends-cloud-area {
	background:#333!important;
	}

	.main-tabs-spotlight .video-entry,#movie-promo-body,.hot-trends-cloud-inner-area {
	background:#000!important;
	}

	.hot-trends-cloud-area {
	margin-bottom:2em!important;
	}

	/* abos Startpage
	--------*/
	#feed_all {
	background:#333!important;
	}

	#feed_all li a:hover {
	background:#222!important;
	}

	#feed_all .current {
	background:#333!important;
	}

	/* pager
	--------*/
	.searchFooterBox div,.pagingDiv {
	background:none!important;
	border-color:#555!important;
	}

	.pagerNotCurrent {
	color:#888!important;
	}

	.pagerCurrent {
	background:none!important;
	color:#fff!important;
	font-size:125%!important;
	}

	/* comment pager
	----------------*/
	.watch-comment-pagination {
	background:none!important;
	}

	.watch-comment-pnum {
	font-size:130%!important;
	}

	.watch-comment-pnum > a {
	font-size:80%!important;
	}

	/* video
	---------*/
	.watch-wide-mode-grey-background #watch-this-vid {
	background:none!important;
	}

	#watch-headline-title {
	color:#fff!important;
	}

	#watch-video-container {
	background:#000!important;
	}

	#watch-headline-container {
	background:none!important;
	}

	/* Favorite / Share / Playlist / Flag box
	-----------------------------------------*/
	.watch-tab-contents .watch-tab-body,.watch-recent-shares-div {
	background:#222!important;
	}

	#watch-main-area {
	border:1px solid #323232!important;
	}

	.shv-box-titletxt {
	color:#fff!important;
	}

	/* List view
	------------- */
	.watch-ppv-vid {
	background:#222!important;
	}

	.watch-playlist-row-playing {
	background:#333!important;
	border-left:none!important;
	border-right:none!important;
	}

	.watch-playlist-row-playing span {
	color:#ccc!important;
	}

	/* List view tabs
	-----------------*/
	#more-from-header #more-from-tabs .more-from-tab {
	background:#222!important;
	color:#666!important;
	}

	#more-from-header #more-from-tabs .more-from-selected {
	background:transparent!important;
	color:#fff!important;
	}

	/* Video Statistics & Data
	--------------------------*/
	.watch-stats-title [style*="background"],.watch-stats-title-cell {
	background:#333!important;
	}

	/* "More share" options
	----------------------*/
	.autoshare-promo-inner {
	background:#222!important;
	}

	.watch-more-action div[style*="background"] {
	background:#000!important;
	}

	/* Text comment options
	-----------------------*/
	#watch-comments-options-inner {
	background:#222!important;
	}

	/* Tooltips
	-----------*/
	.watch-comments-tooltip {
	background:#ffc!important;
	color:#000!important;
	}

	.yt-uix-hovercard-popup-border {
	color:#000!important;
	}

	/* Expander*/
	.yt-uix-expander-head {
	color:#fff!important;
	}

	/* Comments*/
	.watch-comment-head,.watch-comment-head-hidden {
	background:transparent!important;
	}

	.watch-comment-auth-head {
	background:#333!important;
	}

	.comment-list .comment:hover {
	background-color:#333!important;
	}

	/* comment text */
	.watch-comment-body {
	color:#ccc!important;
	padding-left:1em!important;
	}

	/* Video description block
	--------------------------*/
	#watch-channel-vids-div {
	background:#222!important;
	}

	.watch-video-added.post-date {
	color:#aaa!important;
	}

	/* Turn down the lights
	-----------------------*/
	.watch-lights-off #baseDiv {
	background:#000!important;
	}

	/* Search results page
	----------------------*/
	#search-section-header * {
	color:#bbb!important;
	}

	#search-section-header .search-query {
	color:#fff!important;
	}

	.video-description,.search-ad-description {
	color:#bbb!important;
	}

	.video-long-title a,.playlist-long-title a {
	color:#fff!important;
	font-size:110%!important;
	font-weight:700!important;
	border-bottom:1px solid #666!important;
	}

	.video-description b,.show-description b,.episode-description b,.channel-description b,.movie-description b,.show-long-description b,.show-short-description b,.show-mini-description b,.video-long-title b,.playlist-long-title b {
	color:#ffc!important;
	}

	.search-type-selected {
	color:#fff!important;
	}

	.search-type-not-selected a {
	color:#bbb!important;
	border-bottom:1px solid #444!important;
	}

	.video-translation-links a {
	background:#ccc!important;
	color:#000!important;
	border:#999!important;
	}

	.searchView [style*="background"] {
	background:#333!important;
	}

	.grayTextAdWords {
	background:transparent!important;
	}

	/* Shows page
	-------------*/
	.shows-table {
	background:#000!important;
	}

	.shows-table tr.alt td {
	background:#1f1f1f!important;
	}

	#shows-episodes .episode-duration,#shows-episodes .episode-description {
	color:#bbb!important;
	}

	/* Advanced search options
	---------------------------*/
	#search-advanced-form .search-setting-inner {
	background:#222!important;
	}

	/* Warnings/Errors
	------------------*/
	#search-duplicates-message {
	background:#060!important;
	color:#fff!important;
	-moz-border-radius:7px!important;
	}

	#search-duplicates-message * {
	color:#ff0!important;
	text-decoration:underline!important;
	}

	.errorBox {
	background:#600!important;
	color:#fff!important;
	}

	.attentionBox * {
	color:#fff!important;
	}

	#subscribeLoginInvite {
	background:#000!important;
	}

	#subscribeLoginInvite * {
	background:#333!important;
	}

	#subscribeMessage {
	background:#060!important;
	}

	/* Google suggestions
	---------------------*/
	.google-ac-m {
	-moz-box-shadow:4px 4px 10px rgba(0,0,0,0.5)!important;
	}

	.google-ac-a,.google-ac-e {
	background:#222!important;
	border-color:#444!important;
	color:#ddd!important;
	}

	.google-ac-b .google-ac-c,.google-ac-b .google-ac-d {
	background:#666!important;
	color:#333!important;
	}

	.google-ac-e span {
	color:#ccc!important;
	}

	#completeTable {
	color:#000!important;
	}

	/* Miscellaneous
	----------------*/
	#recent-fav-video,#rec-playlist-video {
	background:transparent!important;
	}

	/* advertisements
	-----------------*/
	.ad-div,#pyv-placeholder,#ad300x250,.homepage-masthead-ad,#masthead_child_div,#chrome-promo,#google_companion_ad_div {
	display:none!important;
	}

	/* main image replacements
	--------------------------*/
	.master-sprite,.yt-button-primary,a.yt-button-primary,.yt-button-urgent,a.yt-button-urgent,.yt-button,a.yt-button,.yt-button-primary button,.button.ratingCapsVS,.dropdown-arrow,.yt-menulink-primary .yt-menulink-arr,.header .logo img,img[src*="pixel"] {
	background-color:transparent!important;
	}

	img[class^="yt-button-icon"] {
	background-image:url(http://s.ytimg.com/yt/img/button_icons-vfl72305.png)!important;
	}

	button.ratingVS-3\.0 {
	background-position:-24px -381px;
	}

	#logo {
	background:no-repeat url(http://img840.imageshack.us/img840/9990/youtubelogop.png)!important;
	width:98px;
	height:40px;
	}

	.no-bg {
	background:none repeat scroll 0 0 transparent!important;
	}

	img.yt-uix-button-arrow {
	background-image:none!important;
	}

	.yt-uix-button,.yt-uix-button-menu,.yt-uix-button-menu,.yt-menulink-menu {
	background:#555!important;
	color:#ddd!important;
	}

	.yt-uix-button-menu-item:hover,.yt-suggest-selected {
	background-color:#000!important;
	}

	.yt-uix-button:hover,.watch-expander-head:hover,.yt-uix-expander-head:hover {
	background-color:#000!important;
	color:#ddd!important;
	}

	input,textarea,#search-query-textbox,.yt-suggest-table {
	background-color:#333!important;
	color:#ddd!important;
	}

	input {
	color:#ddd!important;
	}

	textarea {
	color:#ddd!important;
	background-color:#333!important;
	}

	input {
	color:#ddd!important;
	}

	input[type="button"],input[type="submit"] {
	color:#000!important;
	background:#333!important;
	}

	input[disabled] {
	color:#888!important;
	}

	.watch-expander-head * {
	color:#ddd!important;
	}

	a.yt-button {
	color:#ddd!important;
	}

	.yt-menubutton-menu {
	background-color:#333!important;
	}

	.yt-menubutton-menu li:hover {
	background-color:#000!important;
	}

	.yt-menulink .yt-menulink-btn {
	background:none!important;
	}

	.yt-uix-button-arrow,.yt-uix-tooltip-tip-arrow {
	border-color:#ddd transparent transparent!important;
	}

	:hover > .yt-uix-button-arrow {
	border-color:#fff transparent transparent!important;
	}

	.yt-uix-button.reverse .yt-uix-button-arrow {
	border-bottom-color:#ddd!important;
	border-top-color:transparent!important;
	}

	img.yt-uix-button-icon-watch-share {
	background-image:url(http://s.ytimg.com/yt/img/sharing_icons-vfl159436.png)!important;
	}

	img.watch-check-grn-circle {
	background-image:url(data:image/png;base64,)!important;
	}

	.voted-up .yt-uix-button-icon-watch-comment-vote-up {
	background-image:url(http://s.ytimg.com/yt/img/comment-vote-up-vfl149388.png)!important;
	}

	.voted-up .yt-uix-button-icon-watch-comment-vote-down {
	background:url(http://s.ytimg.com/yt/img/comment-vote-down-disabled-vfl149388.png)!important;
	}

	.voted-down .yt-uix-button-icon-watch-comment-vote-up {
	background-image:url(http://s.ytimg.com/yt/img/comment-vote-up-disabled-vfl149388.png)!important;
}

.voted-down .yt-uix-button-icon-watch-comment-vote-down {
background:url(http://s.ytimg.com/yt/img/comment-vote-down-vfl149388.png)!important;
}

.flip {
background:none!important;
}
}
