css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slo-tech.com") {
    body { font-size: 0.822em !important; } 
	#head, #logo, #banner, #fresh_articles, #fresh_news, #fresh_news_title, #head #fixed_link, #content a.user, #content a.settings { display: none !important; }
        #content { margin-top: 10px !important; }
/*
        #logo { position: absolute !important; left: -167px !important; top: 1px !important; margin-top: 0 !important; padding-top: 0 !important; }
        #head h1, #head h1 img { top: 0 !important; margin-top: 0 !important; padding-top: 0 !important; padding-left: 2px !important; height: 24px !important; left: 0 !important; }
*/
	#content, .content, .content blockquote { font-family: Verdana,Ubuntu,Arial,sans-serif !important; font-size:1em !important; line-height: 1.43em !important;}
	#panels, #menu, #poll { color:#777 !important; font-family: Tahoma,Arial,"Liberation Sans",sans-serif !important; font-size:0.94em !important; }
	#panels a, #menu a, #poll a { color:#555 !important; font-size:0.94em !important; }
	#panels h2 a, #menu h2 a, #poll h2 a { color:#555 !important; font-size:0.94em !important; text-decoration: none !important; }
	.content blockquote, .content i { font-style: normal !important; color:#555 !important;}
	.post .signature, .post .history, .post .history a, .post .zgodovina, .post .zgodovina a, .messages, .views { font-family: Tahoma !important; color: #777 !important;}
	.post h4 { font-family: "ubuntu condensed" !important;}
	.post h4 a { font-size: .98em !important;}
	.post h4 a, .controls a, .history a, .zgodovina a { text-decoration: none !important;}
	body { background-color: #E3DECA !important;}
        .forums .sticky { background-color: transparent !important;} 
	.odd, .forums .odd, .forums tr:nth-child(odd) { background-color: #fff !important;}
	.even, .forums .even, .forums tr:nth-child(even) { background-color: #F1F0E9 !important;}
	#content h3 { font-family: ubuntu !important; }
	.forums h3 a  { font-family: ubuntu !important; font-size: 1.12em !important; line-height: 1.3em !important; color: #000 !important; }
	.forums h3 span { font-size: 0.93em !important;}
	.forums h3 span a { font-family: "ubuntu light" !important; font-size: 1.02em !important;}
	.forums a { text-decoration: none !important;}
	.forums thead, .forums tfoot, #content form { background-color: #F6F5F0 !important;}
	.news_item { background-color: #fff !important; padding:4px 8px !important;}
	#menu, #poll { background-color: #f5f5f5 !important;}
	#content a.user, #content a.settings, #head { display: none !important;}
	div.news_item , #content form { border: 1px solid #e4e4e4 !important;}
	.forums .icon, .forums .icon.new { color: #EAEADA !important;}
	#crta { color: #C6BB9B !important; background-color: #C6BB9B !important;}
	#content h2 { font-size: 1em !important;}
	.post .history p { padding-bottom: 0 !important;}
	.post .history li { line-height: 0.95em !important;}
	#content blockquote { margin-bottom: 0 !important;}
	.post .content { padding-top: 1.1em !important;}
	#content .pages a { color: #000 !important;}
	#content ul.set { border: none !important;}
	#content form { padding: 2px !important;}
	#menu, #poll { width: 12em !important;}
    .post, .news_item { border: 1px solid #e4e4e4 !important; 
-moz-box-shadow: 4px 4px 5px #BAAD87 !important; box-shadow: 4px 4px 5px #BAAD87 !important; }
    #panels h2 { background: #EAEADA !important; color: #000 !important; }
    .avatar, .avatar img { width: 32px !important; height: 32px !important; border-color: #e5e5e5 !important; }
    input.text:focus, input.text.focus, textarea:focus, input.datetime:focus, input.datetime.focus { background-color: #fff !important; border-color: #e4e4e4 !important; }
}
