css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.tumblr.com") {









#dashboard_index,



#dashboard_inbox,



#dashboard_edit_post,



#dashboard_submissions,



#dashboard_drafts,



#dashboard_post_queue,



#dashboard_likes,



#dashboard_followers,



#dashboard_following,



#dashboard_directory,



#dashboard_lookup_contacts,



#main_doc_page,



#recommended_tumblelogs,



#dashboard_settings,



#dashboard_create_tumblelog,



#dashboard_members,



#main_meetups,



#main_about,



#main_explore,



#dashboard_goodies,



#main_developers,



#main_themes,



#dashboard_manage_themes,



#main_edit_theme,



#main_jobs,



#main_policy,



#tagged_display,



#dashboard_tumblelog_settings {



background: url(http://img189.imageshack.us/img189/861/55802570.jpg) no-repeat #59b1c5 right bottom fixed !important;}



#dashboard_index div.l-content,



#dashboard_inbox div.l-content,



#dashboard_submissions div.l-content,



#dashboard_drafts div.l-content,



#dashboard_post_queue div.l-content,



#dashboard_likes div.l-content,



#dashboard_followers div.l-content,



#dashboard_members div.l-content,



#tagged_display div.l-content,



#main_tag div.l-content,



#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



background: transparent !important }



#left_column #posts .notification .block {

  color: transparent !important;

}

#left_column #posts .notification.alt {

  background-color: transparent !important;

}

#left_column #posts .notification.last_notification,

#left_column #posts .notification.single_notification {

  border-bottom-color: transparent !important;

}

#left_column #posts .notification a {

  color:#ffffff !important;

  border-bottom:0px !important;

}

#left_column #posts .notification blockquote {

  border-left-color:transparent !important;

}

#left_column #posts .notification .avatar {

  border-color: transparent !important;

  background-color:transparent !important;

  opacity:1 !important;

}

#left_column #posts .notification .avatar:hover {

  opacity:1 !important;

}







.selection_nipple { border-bottom-color: rgba(249,220,211,0.2) !important }















.permalink { background: none !important }







.avatar_frame {



background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important }







.notification {



background: rgba(249,220,211,0.2) !important;



border: rgba(255,255,255,0.1) !important;



background-image: none !important;



margin-bottom: 1px !important }







.notification a { border-bottom: 1px solid rgba(0,0,0,0.1) !important }



.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0.3) !important }







.notification_type_icon { display: none !important }







.nipple { border-right-color: rgba(0,0,0,0.07) !important }















.controls_section li { background: rgba(249,220,211,0.2) !important }



.with_subtitle small { color: rgba(0,0,0,0.5) !important }



.recessed a { color: rgba(255,255,255,0.7) !important }



.radar { display: none}















* { text-shadow: none !important }







h1, .title { color: rgba(0,0,0,0.6) !important }







.no_posts_found, h1.dashboard_header { color: rgba(255,255,255,0.9) !important }







.dashboard_options_form {



background: rgba(0,0,0,0.1) !important;



border: none !important;



color: rgba(255,255,255,0.7) !important }







.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(255,255,255,0.9) !important }







.blue_note, .blue_note a {



background: rgba(0,0,0,0.2) !important;



color: rgba(0,0,0,0.6) !important }















#footer, #footer a {



border: none !important;



color: rgba(0,0,0,0.5) !important }































ol#posts li.post.new_post {





}


















.new_post_label:hover { color: rgba(0,0,0,0.7) !important }



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
width:0;
height:70px;
padding-right:0px;
opacity: 0;
}

.new_post_label, .new_post_label_icon {
background: none !important;
}

li#new_post.post.is_mine.with_avatar.new_post {
background-image: url('http://assets.tumblr.com/images/new_post_icons_no_labels.png');
background-position: 46% 70%;
background-repeat: no-repeat;
}

a#new_post_label_text.new_post_label,
a#new_post_label_photo.new_post_label,
a#new_post_label_quote.new_post_label,
a#new_post_label_link.new_post_label,
a#new_post_label_chat.new_post_label,
a#new_post_label_audio.new_post_label,
a#new_post_label_video.new_post_label {
padding-top: 68px !important;
padding-bottom: 11px !important;
}












#pagination a {



background: rgba(0,0,0,0.4) !important;



color: rgba(255,255,255,0.7) !important }



#pagination a:hover {



color: rgba(255,255,255,0.9) !important }







#dashboard_controls a, #dashboard_controls .control {



color: rgba(255,255,255,0.7) !important }







.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {



display: none !important }





#logo {height: 0 !important;

	width: 0 !important;

	padding-left: 230px !important;

	padding-top: 64px !important;

	background: url(http://img338.imageshack.us/img338/9614/53036007.png) no-repeat !important;}


}
